  // Helper operations for public trust page
  async getAISystemsByVendor(vendorId: string): Promise<AISystem[]> {
    return db.select().from(aiSystems).where(eq(aiSystems.vendorId, vendorId));
  }

  // Compliance Report operations
  async createComplianceReport(insertReport: InsertComplianceReport): Promise<ComplianceReport> {
    const [report] = await db.insert(complianceReports).values(insertReport).returning();
    return report;
  }

  async getComplianceReports(healthSystemId: string): Promise<ComplianceReport[]> {
    return db.select().from(complianceReports)
      .where(eq(complianceReports.healthSystemId, healthSystemId))
      .orderBy(desc(complianceReports.generatedAt));
  }

  // Billing Account operations
  async getBillingAccountByTenant(tenantId: string, role: "health_system" | "vendor"): Promise<BillingAccount | undefined> {
    const condition = role === "health_system"
      ? eq(billingAccounts.healthSystemId, tenantId)
      : eq(billingAccounts.vendorId, tenantId);

    return db.query.billingAccounts.findFirst({
      where: condition,
    });
  }

  async getActiveSubscriptionByTenant(tenantId: string): Promise<Subscription | undefined> {
    // First get the billing account
    const billingAccount = await db.query.billingAccounts.findFirst({
      where: or(
        eq(billingAccounts.healthSystemId, tenantId),
        eq(billingAccounts.vendorId, tenantId)
      ),
    });

    if (!billingAccount) {
      return undefined;
    }

    // Then get the active subscription
    return db.query.subscriptions.findFirst({
      where: and(
        eq(subscriptions.billingAccountId, billingAccount.id),
        eq(subscriptions.status, 'active')
      ),
    });
  }

  // Stripe-specific operations for simplified billing
  async updateHealthSystemStripeCustomer(healthSystemId: string, stripeCustomerId: string): Promise<void> {
    await db.update(healthSystems)
      .set({ stripeCustomerId })
      .where(eq(healthSystems.id, healthSystemId));
  }

  async updateVendorStripeCustomer(vendorId: string, stripeCustomerId: string): Promise<void> {
    await db.update(vendors)
      .set({ stripeCustomerId })
      .where(eq(vendors.id, vendorId));
  }

  async updateHealthSystemSubscription(
    healthSystemId: string,
    updates: {
      stripeSubscriptionId?: string;
      subscriptionTier?: string;
      subscriptionStatus?: string;
      currentPeriodStart?: Date;
      currentPeriodEnd?: Date;
      trialEndsAt?: Date | null;
      aiSystemLimit?: number;
    }
  ): Promise<void> {
    await db.update(healthSystems)
      .set(updates)
      .where(eq(healthSystems.id, healthSystemId));
  }

  async updateVendorSubscription(
    vendorId: string,
    updates: {
      stripeSubscriptionId?: string;
      certificationTier?: string;
      subscriptionStatus?: string;
      currentPeriodStart?: Date;
      currentPeriodEnd?: Date;
      certificationExpiresAt?: Date;
    }
  ): Promise<void> {
    await db.update(vendors)
      .set(updates)
      .where(eq(vendors.id, vendorId));
  }

  async getAISystemsByHealthSystem(healthSystemId: string): Promise<AISystem[]> {
    return this.getAISystems(healthSystemId);
  }

  // ROI Metrics
  async createROIMetric(metric: InsertRoiMetric): Promise<RoiMetric> {
    const [created] = await db.insert(roiMetrics).values(metric).returning();
    return created;
  }

  async getROIMetricsByHealthSystem(healthSystemId: string): Promise<RoiMetric[]> {
    return db.select().from(roiMetrics)
      .where(eq(roiMetrics.healthSystemId, healthSystemId))
      .orderBy(desc(roiMetrics.recordedAt));
  }

  async getROIMetricsByVendor(vendorId: string): Promise<RoiMetric[]> {
    return db.select().from(roiMetrics)
      .where(eq(roiMetrics.vendorId, vendorId))
      .orderBy(desc(roiMetrics.recordedAt));
  }
}

export const storage = new DatabaseStorage();

