   *     tags: [Advanced Certification]
   *     security:
   *       - cookieAuth: []
   *     parameters:
   *       - in: path
   *         name: aiSystemId
   *         required: true
   *         schema:
   *           type: string
   *         description: AI system ID
   *     requestBody:
   *       required: true
