   *             type: object
   *             required: [predictions, groundTruth, sensitiveFeatures]
   *             properties:
   *               predictions:
   *                 type: array
   *                 items:
   *                   type: number
   *               groundTruth:
   *                 type: array
   *                 items:
   *                   type: number
