/**
 * üîí TRANSLATION ENGINE - Type Definitions
 * 
 * Core IP for mapping AI telemetry to healthcare compliance violations
 */

import type { AISystem, AITelemetryEvent, ComplianceControl } from "@shared/schema";

// Event types from AI monitoring platforms (20 types across 6 categories)
export type EventType = 
  // Privacy (2)
  | 'phi_exposure'
  | 'unauthorized_data_access'
  // Security (5)
  | 'prompt_injection_attempt'
  | 'authentication_failure'
  | 'rate_limit_exceeded'
  | 'input_validation_failure'
  | 'model_version_mismatch'
  // Performance (3)
  | 'model_drift'
  | 'performance_degradation'
  | 'high_latency'
  // Safety (4)
  | 'clinical_accuracy_failure'
  | 'false_negative_alert'
  | 'false_positive_alert'
  | 'harmful_output'
  // Fairness (3)
  | 'bias_detected'
  | 'disparate_impact'
  | 'fairness_threshold_violation'
  // Quality (3)
  | 'data_quality_degradation'
  | 'explainability_failure'
  // Legacy types (for backward compatibility)
  | 'drift'
  | 'phi_leakage'
  | 'bias'
  | 'latency'
  | 'error';

// Severity levels
export type Severity = 'info' | 'low' | 'medium' | 'high' | 'critical';

// Compliance frameworks
export type Framework = 'HIPAA' | 'NIST_AI_RMF' | 'FDA_SaMD' | 'ISO_42001' | 'CA_SB1047' | 'NYC_LL144';

// Parsed event from raw telemetry
export interface ParsedEvent {
  eventType: EventType;
  severity: Severity;
  metrics: {
    accuracyDrop?: number; // 0.0 to 1.0
    errorRate?: number; // 0.0 to 1.0
    latencyIncreasePct?: number; // percentage increase
    demographicVariance?: number; // 0.0 to 1.0
    phiExposureCount?: number;
    [key: string]: number | string | undefined;
  };
  metadata: {
    source: string; // 'langsmith', 'arize', 'manual'
    runId?: string;
    ruleId?: string;
    timestamp: Date;
    [key: string]: any;
  };
  aiSystemId: string;
  aiSystem?: AISystem; // Will be populated by engine
}

// Compliance violation detected
export interface ComplianceViolation {
  framework: Framework;
  controlId: string;
  controlName: string;
  violationType: 'breach' | 'deviation' | 'threshold_exceeded';
  severity: Severity;
  requiresReporting: boolean; // To regulators
  reportingDeadline?: Date;
  description: string;
  affectedSystem: {
    id: string;
    name: string;
    department: string;
  };
  detectedAt: Date;
}

// Required action based on violation
export interface RequiredAction {
  actionType: 'rollback' | 'notify' | 'document' | 'escalate' | 'restrict';
  priority: 'immediate' | 'urgent' | 'high' | 'medium' | 'low';
  description: string;
  assignee: 'ciso' | 'compliance_officer' | 'clinical_owner' | 'it_owner' | 'privacy_officer';
  deadline: Date;
  automated: boolean; // Can we execute this automatically?
  actionDetails?: Record<string, any>;
}

// Full translation output
export interface TranslatedEvent {
  originalEvent: ParsedEvent;
  violations: ComplianceViolation[];
  actions: RequiredAction[];
  actionsByViolation: Map<ComplianceViolation, RequiredAction[]>; // Grouped for correct persistence
  riskScore: {
    score: number; // 0-100
    level: 'Low' | 'Medium' | 'High' | 'Critical';
    factors: string[];
  };
  escalationRequired: boolean;
  escalationPath?: string[];
  processedAt: Date;
}

// Compliance rule for mapping
export interface ComplianceRule {
  framework: Framework;
  controlId: string;
  triggerConditions: {
    eventTypes: EventType[];
    severityMin?: Severity;
    metricThresholds?: Record<string, number>;
  };
  violationType: 'breach' | 'deviation' | 'threshold_exceeded';
  requiresReporting: boolean;
  reportingDeadlineDays?: number;
}

server/services/translation-engine/actio.content:
/**
 * üîí TRANSLATION ENGINE - Action Generator
 * 
 * Translates compliance violations ‚Üí Required remediation actions
 * Determines who needs to do what, by when, and whether it can be automated
 */

import type { ComplianceViolation, RequiredAction, Severity } from "./types";

export interface ActionWithViolation extends RequiredAction {
  violationId?: string; // Added to track which violation generated this action
}

export class ActionGenerator {
  /**
   * Generate required actions from compliance violations
   * Returns actions grouped by the violation that generated them
   */
  generate(violations: ComplianceViolation[]): Map<ComplianceViolation, RequiredAction[]> {
    const actionsByViolation = new Map<ComplianceViolation, RequiredAction[]>();

    violations.forEach(violation => {
      let actions: RequiredAction[] = [];

      switch (violation.framework) {
        case 'HIPAA':
          actions = this.generateHIPAAActions(violation);
          break;

        case 'NIST_AI_RMF':
          actions = this.generateNISTActions(violation);
          break;

        case 'FDA_SaMD':
          actions = this.generateFDAActions(violation);
          break;

        case 'CA_SB1047':
          actions = this.generateCaliforniaActions(violation);
          break;

        case 'NYC_LL144':
          actions = this.generateNYCActions(violation);
          break;
      }

      // Prioritize actions for this specific violation
      const prioritizedActions = this.prioritizeActions(actions);
      actionsByViolation.set(violation, prioritizedActions);
    });

    return actionsByViolation;
  }

  /**
   * HIPAA Actions
   */
  private generateHIPAAActions(violation: ComplianceViolation): RequiredAction[] {
    const actions: RequiredAction[] = [];

    // Breach Notification (164.402)
    if (violation.controlId === '164.402') {
      // Immediate privacy officer notification
      actions.push({
        actionType: 'notify',
        priority: 'immediate',
        description: 'Notify Privacy Officer of potential PHI breach immediately. Begin breach investigation protocol.',
        assignee: 'privacy_officer',
        deadline: new Date(Date.now() + 2 * 60 * 60 * 1000), // 2 hours
        automated: true, // Auto-send email/SMS
        actionDetails: {
          notificationChannels: ['email', 'sms', 'dashboard'],
          escalationPath: ['privacy_officer', 'ciso', 'compliance_officer'],
        },
      });

      // Document breach details
      actions.push({
        actionType: 'document',
        priority: 'urgent',
        description: 'Document breach details: scope, affected individuals, timeline, and initial investigation findings for HHS reporting.',
        assignee: 'compliance_officer',
        deadline: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        automated: false,
        actionDetails: {
          requiredFields: [
            'date_of_breach',
            'date_discovered',
            'affected_individuals_count',
            'phi_elements_exposed',
            'breach_cause',
            'mitigation_steps',
          ],
        },
      });

      // Critical: Rollback if severe
      if (violation.severity === 'critical') {
        actions.push({
          actionType: 'rollback',
          priority: 'immediate',
          description: `Rollback AI system "${violation.affectedSystem.name}" to last known secure version to prevent further PHI exposure.`,
          assignee: 'it_owner',
          deadline: new Date(Date.now() + 1 * 60 * 60 * 1000), // 1 hour
          automated: false, // Requires manual approval due to clinical impact
          actionDetails: {
            systemId: violation.affectedSystem.id,
            requiresApproval: true,
            approvers: ['ciso', 'clinical_owner'],
          },
        });
      }
    }

    // Audit Controls (164.312(b))
    if (violation.controlId === '164.312(b)') {
      actions.push({
        actionType: 'escalate',
        priority: violation.severity === 'critical' ? 'urgent' : 'high',
        description: `Escalate AI system performance issue to governance committee. System activity review required per HIPAA audit controls.`,
        assignee: 'ciso',
        deadline: new Date(Date.now() + 3 * 24 * 60 * 60 * 1000), // 3 days
        automated: true,
        actionDetails: {
          escalationPath: ['ciso', 'governance_committee'],
          meetingRequired: true,
        },
      });
    }

    return actions;
  }

  /**
   * NIST AI RMF Actions
   */
  private generateNISTActions(violation: ComplianceViolation): RequiredAction[] {
    const actions: RequiredAction[] = [];

    // Performance Monitoring (MANAGE-4.1)
    if (violation.controlId === 'MANAGE-4.1') {
      actions.push({
        actionType: 'escalate',
        priority: 'high',
        description: `Escalate to AI governance committee for performance degradation review. NIST AI RMF requires documented performance monitoring and response.`,
        assignee: 'ciso',
        deadline: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        automated: true,
        actionDetails: {
          frameworkReference: 'NIST AI RMF MANAGE-4.1',
          requiredDocumentation: [
            'Performance metrics analysis',
            'Root cause investigation',
            'Remediation plan',
          ],
        },
      });

      actions.push({
        actionType: 'document',
        priority: 'medium',
        description: 'Update AI risk assessment with performance degradation findings. Document in AI system risk register.',
        assignee: 'compliance_officer',
        deadline: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000), // 14 days
        automated: false,
        actionDetails: {
          artifact: 'ai_risk_register',
          systemId: violation.affectedSystem.id,
        },
      });
    }

    // Fairness Monitoring (MEASURE-2.1)
    if (violation.controlId === 'MEASURE-2.1') {
      actions.push({
        actionType: 'document',
        priority: 'high',
        description: 'Conduct bias audit and document findings. NIST AI RMF requires fairness metrics tracking and demographic disparity analysis.',
        assignee: 'compliance_officer',
        deadline: new Date(Date.now() + 10 * 24 * 60 * 60 * 1000), // 10 days
        automated: false,
        actionDetails: {
          requiredAnalysis: [
            'demographic_breakdown',
            'performance_by_group',
            'disparity_metrics',
            'mitigation_recommendations',
          ],
        },
      });
    }

    // Continuous Risk Management (MANAGE-1.1)
    if (violation.controlId === 'MANAGE-1.1') {
      actions.push({
        actionType: 'escalate',
        priority: violation.severity === 'high' ? 'urgent' : 'medium',
        description: 'Escalate AI risk incident to risk management committee. Continuous monitoring required per NIST AI RMF.',
        assignee: 'ciso',
        deadline: new Date(Date.now() + 5 * 24 * 60 * 60 * 1000), // 5 days
        automated: true,
      });
    }

    return actions;
  }

  /**
   * FDA Actions
   */
  private generateFDAActions(violation: ComplianceViolation): RequiredAction[] {
    const actions: RequiredAction[] = [];

    // Post-Market Surveillance (FDA-PCCP-2)
    if (violation.controlId === 'FDA-PCCP-2') {
      // FDA reporting required
      actions.push({
        actionType: 'notify',
        priority: 'immediate',
        description: `Prepare FDA notification of AI medical device performance issue. Performance degradation exceeds predetermined change control threshold.`,
        assignee: 'compliance_officer',
        deadline: violation.reportingDeadline || new Date(Date.now() + 30 * 24 * 60 * 60 * 1000),
        automated: false, // Requires human review for FDA submission
        actionDetails: {
          regulatoryBody: 'FDA',
          submissionType: 'Post-Market Surveillance Report',
          requiredDocuments: [
            'Performance analysis',
            'Root cause investigation',
            'Corrective action plan',
            'Timeline for resolution',
          ],
        },
      });

      // Restrict system use pending review
      if (violation.severity === 'high' || violation.severity === 'critical') {
        actions.push({
          actionType: 'restrict',
          priority: 'urgent',
          description: `Restrict AI medical device use until FDA review and corrective action complete. Clinical owner must implement temporary workflow alternative.`,
          assignee: 'clinical_owner',
          deadline: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours
          automated: false,
          actionDetails: {
            restrictionType: 'conditional_use',
            requiresApproval: true,
            alternativeWorkflow: 'manual_process',
          },
        });
      }
    }

    return actions;
  }

  /**
   * California Actions
   */
  private generateCaliforniaActions(violation: ComplianceViolation): RequiredAction[] {
    const actions: RequiredAction[] = [];

    if (violation.controlId === 'CA-BREACH') {
      // Notify affected individuals
      actions.push({
        actionType: 'notify',
        priority: 'immediate',
        description: 'Prepare notification for affected California residents and Attorney General. CA law requires notification within 30 days.',
        assignee: 'privacy_officer',
        deadline: violation.reportingDeadline || new Date(Date.now() + 30 * 24 * 60 * 60 * 1000),
        automated: false,
        actionDetails: {
          regulatoryBody: 'California Attorney General',
          affectedParties: 'california_residents',
        },
      });
    }

    return actions;
  }

  /**
   * NYC Actions
   */
  private generateNYCActions(violation: ComplianceViolation): RequiredAction[] {
    const actions: RequiredAction[] = [];

    if (violation.controlId === 'NYC-BIAS') {
      actions.push({
        actionType: 'document',
        priority: 'high',
        description: 'Conduct annual bias audit as required by NYC Local Law 144. Must be published publicly.',
        assignee: 'compliance_officer',
        deadline: new Date(Date.now() + 60 * 24 * 60 * 60 * 1000), // 60 days
        automated: false,
        actionDetails: {
          auditType: 'bias_audit',
          publicationRequired: true,
          framework: 'NYC Local Law 144',
        },
      });
    }

    return actions;
  }

  /**
   * Prioritize and deduplicate actions
   */
  private prioritizeActions(actions: RequiredAction[]): RequiredAction[] {
    // Remove duplicates (same actionType + assignee + system)
    const unique = this.deduplicateActions(actions);

    // Sort by priority and deadline
    return unique.sort((a, b) => {
      const priorityOrder = {
        immediate: 0,
        urgent: 1,
        high: 2,
        medium: 3,
        low: 4,
      };

      const priorityDiff = priorityOrder[a.priority] - priorityOrder[b.priority];
      if (priorityDiff !== 0) return priorityDiff;

      return a.deadline.getTime() - b.deadline.getTime();
    });
  }

  private deduplicateActions(actions: RequiredAction[]): RequiredAction[] {
    const map = new Map<string, RequiredAction>();

    actions.forEach(action => {
      const key = `${action.actionType}-${action.assignee}-${action.description.substring(0, 50)}`;
      const existing = map.get(key);

      if (!existing || this.isPriorityHigher(action.priority, existing.priority)) {
        map.set(key, action);
      }
    });

    return Array.from(map.values());
  }

  private isPriorityHigher(a: string, b: string): boolean {
    const priorityOrder = { immediate: 0, urgent: 1, high: 2, medium: 3, low: 4 };
    return priorityOrder[a as keyof typeof priorityOrder] < priorityOrder[b as keyof typeof priorityOrder];
  }
}

server/services/translation-engine/compl.content:
/**
 * üîí TRANSLATION ENGINE - Compliance Mapping Service
 * 
 * CORE IP - THE DEFENSIBLE MOAT
 * 
 * Maps AI monitoring telemetry to specific compliance control violations
 * across HIPAA, NIST AI RMF, FDA SaMD, and state regulations.
 * 
 * This is where 3+ years of healthcare + AI compliance expertise is encoded.
 */

import { logger } from "../../logger";
import { storage } from "../../storage";
import { getThresholds } from "./threshold-config";
import { policyLoader } from "./policy-loader";
import type {
  ParsedEvent,
  ComplianceViolation,
  Framework,
  Severity,
  ComplianceRule,
} from "./types";
import type { AISystem } from "@shared/schema";

export class ComplianceMapping {
  private rules: Map<string, ComplianceRule[]> = new Map();

  constructor() {
    this.loadMappingRules();
  }

  /**
   * üîí IP MOAT - Load policy from encrypted database
   * 
   * Checks database-backed policies first, falls back to static rules if not found.
   * This hybrid approach activates the IP moat while maintaining reliability.
   */
  private async loadPolicyForEvent(eventType: string, framework: string): Promise<ComplianceViolation[] | null> {
    try {
      const policy = await policyLoader.getPolicy(eventType, framework);

      if (!policy) {
        return null; // No database policy found, fallback to static rules
      }

      // Found encrypted policy - convert to violations format
      return policy.frameworks.map(fw => ({
        framework: fw.framework as Framework,
        controlId: fw.controlId,
        controlName: fw.controlName,
        violationType: fw.violationType as any,
        severity: fw.severity as Severity,
        requiresReporting: fw.requiresReporting,
        reportingDeadline: fw.reportingDeadlineDays 
          ? this.calculateDeadline(fw.reportingDeadlineDays)
          : undefined,
        description: `${fw.controlName} - Event type: ${eventType}`,
        affectedSystem: {} as any, // Will be filled by caller
        detectedAt: new Date(),
      }));
    } catch (error) {
      logger.error({ error, eventType, framework }, 'Failed to load policy from database - using static fallback');
      return null; // Fallback to static rules on error
    }
  }

  /**
   * Main mapping function: Translate event ‚Üí compliance violations
   */
  async mapToViolations(event: ParsedEvent): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];

    // Get AI system details for context
    const aiSystem = event.aiSystem || await storage.getAISystem(event.aiSystemId);
    if (!aiSystem) {
      throw new Error(`AI system ${event.aiSystemId} not found`);
    }

    // Map events to framework-specific violations (20 event types)
    switch (event.eventType) {
      // PRIVACY EVENTS (2)
      case 'phi_exposure':
      case 'phi_leakage': // Legacy
        violations.push(...await this.handlePHILeakage(event, aiSystem));
        break;

      case 'unauthorized_data_access':
        violations.push(...await this.handleUnauthorizedAccess(event, aiSystem));
        break;

      // SECURITY EVENTS (5)
      case 'prompt_injection_attempt':
        violations.push(...await this.handlePromptInjection(event, aiSystem));
        break;

      case 'authentication_failure':
        violations.push(...await this.handleAuthFailure(event, aiSystem));
        break;

      case 'rate_limit_exceeded':
        violations.push(...await this.handleRateLimitExceeded(event, aiSystem));
        break;

      case 'input_validation_failure':
        violations.push(...await this.handleInputValidationFailure(event, aiSystem));
        break;

      case 'model_version_mismatch':
        violations.push(...await this.handleVersionMismatch(event, aiSystem));
        break;

      // PERFORMANCE EVENTS (3)
      case 'model_drift':
      case 'drift': // Legacy
      case 'performance_degradation':
        violations.push(...await this.handleDrift(event, aiSystem));
        break;

      case 'high_latency':
      case 'latency': // Legacy
        violations.push(...await this.handleLatency(event, aiSystem));
        break;

      // SAFETY EVENTS (4)
      case 'clinical_accuracy_failure':
        violations.push(...await this.handleClinicalAccuracy(event, aiSystem));
        break;

      case 'false_negative_alert':
      case 'false_positive_alert':
        violations.push(...await this.handleFalseAlerts(event, aiSystem));
        break;

      case 'harmful_output':
        violations.push(...await this.handleHarmfulOutput(event, aiSystem));
        break;

      // FAIRNESS EVENTS (3)
      case 'bias_detected':
      case 'bias': // Legacy
      case 'disparate_impact':
      case 'fairness_threshold_violation':
        violations.push(...await this.handleBias(event, aiSystem));
        break;

      // QUALITY EVENTS (3)
      case 'data_quality_degradation':
        violations.push(...await this.handleDataQuality(event, aiSystem));
        break;

      case 'explainability_failure':
        violations.push(...await this.handleExplainabilityFailure(event, aiSystem));
        break;

      // LEGACY
      case 'error':
        violations.push(...await this.handleError(event, aiSystem));
        break;
    }

    return violations;
  }

  /**
   * DRIFT / PERFORMANCE DEGRADATION
   * 
   * Model drift affects multiple frameworks:
   * - NIST AI RMF: MANAGE-4.1 (performance monitoring)
   * - HIPAA: 164.312(b) (audit controls)
   * - FDA: PCCP-ML (predetermined change control)
   * 
   * üîí IP MOAT: Uses encrypted database policies when available
   */
  private async handleDrift(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];
    const accuracyDrop = event.metrics.accuracyDrop || 0;

    // Get configurable thresholds for this health system
    const thresholds = await getThresholds(aiSystem.healthSystemId);

    // üîí STEP 1: Try loading from encrypted database policy (IP MOAT)
    const dbPolicy = await this.loadPolicyForEvent('model_drift', 'NIST_AI_RMF');

    if (dbPolicy && dbPolicy.length > 0 && accuracyDrop > thresholds.drift.accuracyDropMedium) {
      // Found encrypted policy - use it and enrich with event details
      for (const violation of dbPolicy) {
        violations.push({
          ...violation,
          severity: accuracyDrop > thresholds.drift.accuracyDropHigh ? 'high' : 'medium',
          description: `AI system accuracy dropped by ${(accuracyDrop * 100).toFixed(1)}%, exceeding acceptable performance threshold. Requires investigation and potential model retraining.`,
          affectedSystem: {
            id: aiSystem.id,
            name: aiSystem.name,
            department: aiSystem.department,
          },
          detectedAt: event.metadata.timestamp,
        });
      }
      logger.debug({ eventType: 'model_drift' }, 'üîí Using encrypted database policy (IP MOAT activated)');
    } else if (accuracyDrop > thresholds.drift.accuracyDropMedium) {
      // STEP 2: Fallback to static rules if no database policy
      logger.debug({ eventType: 'model_drift' }, 'Using static fallback rules');

      // NIST AI RMF - Performance Monitoring
      violations.push({
        framework: 'NIST_AI_RMF',
        controlId: 'MANAGE-4.1',
        controlName: 'AI system performance is monitored',
        violationType: accuracyDrop > thresholds.drift.accuracyDropHigh ? 'deviation' : 'threshold_exceeded',
        severity: accuracyDrop > thresholds.drift.accuracyDropHigh ? 'high' : 'medium',
        requiresReporting: false,
        description: `AI system accuracy dropped by ${(accuracyDrop * 100).toFixed(1)}%, exceeding acceptable performance threshold. Requires investigation and potential model retraining.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    // HIPAA - Audit Controls (Service Availability)
    if (event.severity === 'high' || event.severity === 'critical') {
      violations.push({
        framework: 'HIPAA',
        controlId: '164.312(b)',
        controlName: 'Audit Controls - System Activity Review',
        violationType: 'threshold_exceeded',
        severity: event.severity,
        requiresReporting: false,
        description: `AI system ${aiSystem.name} performance degradation detected. Audit controls require review of system activity and performance metrics.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    // FDA SaMD - Predetermined Change Control Plan
    // Only applies if system is FDA-regulated medical device
    const isFDARegulated = aiSystem.department === 'Imaging' || 
                          aiSystem.department === 'Pathology' || 
                          aiSystem.name.toLowerCase().includes('diagnostic');

    if (isFDARegulated && accuracyDrop > thresholds.drift.accuracyDropFDA) {
      violations.push({
        framework: 'FDA_SaMD',
        controlId: 'FDA-PCCP-2',
        controlName: 'Post-Market Surveillance - Model Performance',
        violationType: 'deviation',
        severity: 'high',
        requiresReporting: true,
        reportingDeadline: this.calculateDeadline(30), // FDA requires 30-day notification
        description: `FDA-regulated AI medical device showing significant performance degradation (${(accuracyDrop * 100).toFixed(1)}% accuracy drop). Requires FDA notification and corrective action plan.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    return violations;
  }

  /**
   * PHI LEAKAGE
   * 
   * CRITICAL - Always triggers HIPAA breach notification
   * - HIPAA: 164.402 (breach notification)
   * - HIPAA: 164.308(a)(1)(ii)(D) (security management)
   * - State laws: California, etc.
   * 
   * üîí IP MOAT: Uses encrypted database policies when available
   */
  private async handlePHILeakage(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];

    // üîí STEP 1: Try loading from encrypted database policy (IP MOAT)
    const dbPolicy = await this.loadPolicyForEvent('phi_exposure', 'HIPAA');

    if (dbPolicy && dbPolicy.length > 0) {
      // Found encrypted policy - use it and enrich with event details
      for (const violation of dbPolicy) {
        violations.push({
          ...violation,
          description: `CRITICAL: Potential PHI breach detected in AI system ${aiSystem.name}. ${event.metrics.phiExposureCount || 'Unknown number of'} patient records may have been exposed. Immediate investigation and breach notification process required.`,
          affectedSystem: {
            id: aiSystem.id,
            name: aiSystem.name,
            department: aiSystem.department,
          },
          detectedAt: event.metadata.timestamp,
        });
      }
      logger.debug({ eventType: 'phi_exposure' }, 'üîí Using encrypted database policy (IP MOAT activated)');
    } else {
      // STEP 2: Fallback to static rules if no database policy
      logger.debug({ eventType: 'phi_exposure' }, 'Using static fallback rules');

      // HIPAA Breach Notification Rule
      violations.push({
        framework: 'HIPAA',
        controlId: '164.402',
        controlName: 'Breach Notification - Unauthorized Disclosure',
        violationType: 'breach',
        severity: 'critical',
        requiresReporting: true,
        reportingDeadline: this.calculateDeadline(60), // 60 days to notify HHS
        description: `CRITICAL: Potential PHI breach detected in AI system ${aiSystem.name}. ${event.metrics.phiExposureCount || 'Unknown number of'} patient records may have been exposed. Immediate investigation and breach notification process required.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });

      // HIPAA Security Rule - Information System Activity Review
      violations.push({
        framework: 'HIPAA',
        controlId: '164.308(a)(1)(ii)(D)',
        controlName: 'Security Management - Information System Activity Review',
        violationType: 'breach',
        severity: 'critical',
        requiresReporting: true,
        description: `Security management process requires immediate review of AI system activity logs to determine scope of PHI exposure and implement corrective measures.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    // State-specific laws (California example)
    // Check actual health system state for state-specific compliance requirements
    const healthSystem = await storage.getHealthSystem(aiSystem.healthSystemId);
    const isCaliforniaSubject = healthSystem?.state === 'CA';

    if (isCaliforniaSubject) {
      violations.push({
        framework: 'CA_SB1047',
        controlId: 'CA-BREACH',
        controlName: 'California Breach Notification',
        violationType: 'breach',
        severity: 'critical',
        requiresReporting: true,
        reportingDeadline: this.calculateDeadline(30), // California requires faster notification
        description: `California law requires notification of affected individuals and Attorney General within 30 days of PHI breach discovery.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    return violations;
  }

  /**
   * BIAS / FAIRNESS VIOLATIONS
   * 
   * - NIST AI RMF: MEASURE-2.1 (fairness monitoring)
   * - NYC Local Law 144 (employment AI)
   */
  private async handleBias(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];
    const variance = event.metrics.demographicVariance || 0;

    // Get configurable thresholds for this health system
    const thresholds = await getThresholds(aiSystem.healthSystemId);

    // NIST AI RMF - Fairness Monitoring
    if (variance > thresholds.bias.varianceMedium) {
      violations.push({
        framework: 'NIST_AI_RMF',
        controlId: 'MEASURE-2.1',
        controlName: 'AI system performance is monitored for fairness',
        violationType: 'threshold_exceeded',
        severity: variance > thresholds.bias.varianceHigh ? 'high' : 'medium',
        requiresReporting: false,
        description: `AI system showing ${(variance * 100).toFixed(1)}% demographic variance in predictions, indicating potential bias. Fairness review and model retraining recommended.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    // NYC Local Law 144 (if applicable to employment/HR AI)
    const isEmploymentAI = aiSystem.department.toLowerCase().includes('hr') || 
                          aiSystem.department.toLowerCase().includes('employment') ||
                          aiSystem.name.toLowerCase().includes('hiring');

    if (isEmploymentAI && variance > thresholds.bias.varianceNYC) {
      violations.push({
        framework: 'NYC_LL144',
        controlId: 'NYC-BIAS',
        controlName: 'Bias Audit Required',
        violationType: 'threshold_exceeded',
        severity: 'high',
        requiresReporting: true,
        description: `Employment AI system requires annual bias audit under NYC Local Law 144. Current demographic variance (${(variance * 100).toFixed(1)}%) exceeds acceptable threshold.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    return violations;
  }

  /**
   * LATENCY / SERVICE AVAILABILITY
   * 
   * - HIPAA: 164.312(b) (service availability)
   */
  private async handleLatency(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];
    const latencyIncrease = event.metrics.latencyIncreasePct || 0;

    // Get configurable thresholds for this health system
    const thresholds = await getThresholds(aiSystem.healthSystemId);

    // HIPAA - Service Availability
    if (latencyIncrease > thresholds.latency.increaseMedium) {
      violations.push({
        framework: 'HIPAA',
        controlId: '164.312(b)',
        controlName: 'Audit Controls - Service Availability',
        violationType: 'deviation',
        severity: latencyIncrease > thresholds.latency.increaseHigh ? 'high' : 'medium',
        requiresReporting: false,
        description: `AI system latency increased by ${(latencyIncrease * 100).toFixed(1)}%, potentially affecting clinical workflow and patient care delivery. Performance optimization required.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    return violations;
  }

  /**
   * ERROR RATE VIOLATIONS
   * 
   * - NIST AI RMF: MANAGE-1.1 (risk management)
   * - FDA: Performance monitoring (if medical device)
   */
  private async handleError(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const violations: ComplianceViolation[] = [];
    const errorRate = event.metrics.errorRate || 0;

    // Get configurable thresholds for this health system
    const thresholds = await getThresholds(aiSystem.healthSystemId);

    // NIST AI RMF - Continuous Risk Management
    if (errorRate > thresholds.error.rateMedium) {
      violations.push({
        framework: 'NIST_AI_RMF',
        controlId: 'MANAGE-1.1',
        controlName: 'AI risks are managed continuously',
        violationType: 'threshold_exceeded',
        severity: errorRate > thresholds.error.rateHigh ? 'high' : 'medium',
        requiresReporting: false,
        description: `AI system error rate (${(errorRate * 100).toFixed(2)}%) exceeds acceptable threshold. Risk review and error mitigation required.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    // FDA monitoring (if medical device)
    const isFDARegulated = aiSystem.department === 'Imaging' || 
                          aiSystem.department === 'Pathology' ||
                          aiSystem.name.toLowerCase().includes('diagnostic');

    if (isFDARegulated && errorRate > thresholds.error.rateFDA) {
      violations.push({
        framework: 'FDA_SaMD',
        controlId: 'FDA-PCCP-2',
        controlName: 'Post-Market Surveillance',
        violationType: 'deviation',
        severity: 'high',
        requiresReporting: errorRate > thresholds.error.rateHigh,
        reportingDeadline: errorRate > thresholds.error.rateHigh ? this.calculateDeadline(30) : undefined,
        description: `FDA-regulated AI medical device showing elevated error rate (${(errorRate * 100).toFixed(2)}%). Post-market surveillance and corrective action required.`,
        affectedSystem: {
          id: aiSystem.id,
          name: aiSystem.name,
          department: aiSystem.department,
        },
        detectedAt: event.metadata.timestamp,
      });
    }

    return violations;
  }

  /**
   * UNAUTHORIZED DATA ACCESS
   * - HIPAA: 164.308(a)(4) Access Control
   * - ISO 27001: Access Control violations
   */
  private async handleUnauthorizedAccess(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'HIPAA',
      controlId: '164.308(a)(4)',
      controlName: 'Access Control - Unauthorized Access Prevention',
      violationType: 'breach',
      severity: 'critical',
      requiresReporting: true,
      reportingDeadline: this.calculateDeadline(60),
      description: `Unauthorized access attempt detected on ${aiSystem.name}. HIPAA requires immediate investigation and potential breach notification.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * PROMPT INJECTION ATTEMPT
   * - NIST AI RMF: GOVERN-5.1 (AI system security)
   * - ISO 27001: Information Security Incident Management
   */
  private async handlePromptInjection(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'NIST_AI_RMF',
      controlId: 'GOVERN-5.1',
      controlName: 'AI system security and resilience practices',
      violationType: 'breach',
      severity: 'high',
      requiresReporting: false,
      description: `Prompt injection attack detected on ${aiSystem.name}. Security incident response required.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * AUTHENTICATION FAILURE
   * - HIPAA: 164.312(d) Person/Entity Authentication
   */
  private async handleAuthFailure(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'HIPAA',
      controlId: '164.312(d)',
      controlName: 'Person or Entity Authentication',
      violationType: 'deviation',
      severity: 'medium',
      requiresReporting: false,
      description: `Authentication failure on ${aiSystem.name}. Review access controls and authentication mechanisms.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * RATE LIMIT EXCEEDED
   * - HIPAA: 164.312(b) Service Availability
   */
  private async handleRateLimitExceeded(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'HIPAA',
      controlId: '164.312(b)',
      controlName: 'Audit Controls - Service Availability',
      violationType: 'threshold_exceeded',
      severity: 'medium',
      requiresReporting: false,
      description: `Rate limit exceeded on ${aiSystem.name}. Potential denial of service or capacity planning issue.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * INPUT VALIDATION FAILURE
   * - NIST AI RMF: MANAGE-4.2 (Data quality monitoring)
   */
  private async handleInputValidationFailure(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'NIST_AI_RMF',
      controlId: 'MANAGE-4.2',
      controlName: 'Mechanisms for tracking AI system inputs',
      violationType: 'deviation',
      severity: 'low',
      requiresReporting: false,
      description: `Input validation failure detected on ${aiSystem.name}. Review input quality controls.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * MODEL VERSION MISMATCH
   * - FDA: Predetermined Change Control Plan (PCCP)
   */
  private async handleVersionMismatch(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'FDA_SaMD',
      controlId: 'FDA-PCCP-1',
      controlName: 'Predetermined Change Control Plan',
      violationType: 'deviation',
      severity: 'medium',
      requiresReporting: false,
      description: `Model version mismatch detected on ${aiSystem.name}. Verify change control procedures.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * CLINICAL ACCURACY FAILURE
   * - FDA: Clinical Validation Requirements
   * - NIST AI RMF: MANAGE-4.1 (Performance monitoring)
   */
  private async handleClinicalAccuracy(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'FDA_SaMD',
      controlId: 'FDA-CV-1',
      controlName: 'Clinical Validation',
      violationType: 'breach',
      severity: 'critical',
      requiresReporting: true,
      reportingDeadline: this.calculateDeadline(30),
      description: `Clinical accuracy failure detected on ${aiSystem.name}. FDA requires immediate investigation and potential device recall.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * FALSE ALERTS (Positive/Negative)
   * - FDA: Analytical/Clinical Validation
   */
  private async handleFalseAlerts(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const isFalseNegative = event.eventType === 'false_negative_alert';

    return [{
      framework: 'FDA_SaMD',
      controlId: 'FDA-AV-1',
      controlName: 'Analytical Validation',
      violationType: 'deviation',
      severity: isFalseNegative ? 'high' : 'medium',
      requiresReporting: isFalseNegative,
      reportingDeadline: isFalseNegative ? this.calculateDeadline(30) : undefined,
      description: `${isFalseNegative ? 'False negative' : 'False positive'} alert rate exceeds threshold on ${aiSystem.name}. Analytical validation required.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * HARMFUL OUTPUT
   * - NIST AI RMF: MEASURE-2.7 (Safety testing)
   * - CA SB 1047: Safety requirements
   */
  private async handleHarmfulOutput(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'CA_SB1047',
      controlId: 'CA-SB1047-2',
      controlName: 'Covered Model Safety Testing',
      violationType: 'breach',
      severity: 'critical',
      requiresReporting: true,
      reportingDeadline: this.calculateDeadline(10),
      description: `Harmful or unsafe output detected on ${aiSystem.name}. CA SB 1047 requires immediate safety incident reporting.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * DATA QUALITY DEGRADATION
   * - NIST AI RMF: MANAGE-4.2 (Data quality)
   */
  private async handleDataQuality(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    return [{
      framework: 'NIST_AI_RMF',
      controlId: 'MANAGE-4.2',
      controlName: 'Mechanisms for tracking AI system inputs',
      violationType: 'threshold_exceeded',
      severity: 'medium',
      requiresReporting: false,
      description: `Data quality degradation detected on ${aiSystem.name}. Input data monitoring required.`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * EXPLAINABILITY FAILURE
   * - NIST AI RMF: MEASURE-2.3 (Transparency)
   * - NYC Local Law 144: Explainability requirements
   */
  private async handleExplainabilityFailure(event: ParsedEvent, aiSystem: AISystem): Promise<ComplianceViolation[]> {
    const isEmploymentAI = aiSystem.department === 'HR' || aiSystem.name.toLowerCase().includes('hiring');

    return [{
      framework: isEmploymentAI ? 'NYC_LL144' : 'NIST_AI_RMF',
      controlId: isEmploymentAI ? 'NYC-LL144-1' : 'MEASURE-2.3',
      controlName: isEmploymentAI ? 'Bias Audit Requirement' : 'AI system transparency',
      violationType: 'deviation',
      severity: isEmploymentAI ? 'high' : 'low',
      requiresReporting: isEmploymentAI,
      reportingDeadline: isEmploymentAI ? this.calculateDeadline(90) : undefined,
      description: `Explainability failure on ${aiSystem.name}. ${isEmploymentAI ? 'NYC Local Law 144 requires bias audit documentation.' : 'Transparency controls required.'}`,
      affectedSystem: {
        id: aiSystem.id,
        name: aiSystem.name,
        department: aiSystem.department,
      },
      detectedAt: event.metadata.timestamp,
    }];
  }

  /**
   * Helper: Calculate regulatory reporting deadline
   */
  private calculateDeadline(days: number): Date {
    const deadline = new Date();
    deadline.setDate(deadline.getDate() + days);
    return deadline;
  }

  /**
   * Load compliance mapping rules
   * 
   * üîí IP MOAT: Warms policy cache with encrypted database policies
   * Rules are updated quarterly as regulations evolve
   */
  private async loadMappingRules() {
    // Mapping rules are encoded in the handler methods above
    // This structure allows for future expansion to rule-based engine

    // Warm the policy cache with common event types
    const criticalEventTypes = [
      'phi_exposure',
      'bias_detected',
      'model_drift',
      'unauthorized_data_access',
      'clinical_accuracy_failure'
    ];

    try {
      await policyLoader.warmCache(criticalEventTypes);
      logger.info("‚úÖ Compliance mapping rules loaded - policy cache warmed (IP MOAT activated)");
    } catch (error) {
      // Non-blocking - static rules will work as fallback
      logger.warn({ error }, "‚ö†Ô∏è Failed to warm policy cache - using static fallback rules");
      logger.info("‚úÖ Compliance mapping rules loaded (static mode)");
    }
  }
}

server/services/background-jobs.ts.content:
/**
 * Background Job Processor
 * 
 * Lightweight async job system for:
 * - Certification workflows
 * - Scheduled compliance checks
 * - Report generation
 * - Email notifications
 * 
 * PRODUCTION DEPLOYMENT WARNING:
 * - Set ENABLE_BACKGROUND_WORKERS=true on ONLY ONE server instance
 * - Or configure Redis for distributed locking across multiple workers
 * - Without this, multiple servers will execute duplicate jobs
 * 
 * This is MVP-appropriate and can be replaced with Inngest later if needed.
 */

import { storage } from "../storage";
import type { BackgroundJob } from "@shared/schema";
import { CacheService } from "../cache";
import { logger } from "../logger";

export type JobType = "certification_workflow" | "compliance_check" | "report_generation" | "email_notification" | "predictive_alerts" | "action_executor";

export interface JobPayload {
  [key: string]: any;
}

export interface JobResult {
  success: boolean;
  data?: any;
  error?: string;
}

/**
 * Job registry - maps job types to their handler functions
 */
type JobHandler = (payload: JobPayload) => Promise<JobResult>;
const jobHandlers = new Map<JobType, JobHandler>();

/**
 * Register a job handler
 */
export function registerJobHandler(jobType: JobType, handler: JobHandler) {
  jobHandlers.set(jobType, handler);
  logger.info({ jobType }, `Registered job handler: ${jobType}`);
}

/**
 * Create and enqueue a background job
 */
export async function enqueueJob(jobType: JobType, payload: JobPayload): Promise<BackgroundJob> {
  const job = await storage.createBackgroundJob({
    jobType,
    status: "pending",
    payload: JSON.stringify(payload),
  });

  logger.info({ jobId: job.id, jobType }, `Enqueued job ${job.id} (${jobType})`);

  // Process async (don't await)
  processJob(job.id).catch(err => {
    logger.error({ jobId: job.id, err }, `Error processing job ${job.id}`);
  });

  return job;
}

/**
 * Acquire distributed lock for job processing
 * Uses Redis SETNX for atomic lock acquisition across multiple server instances
 */
async function acquireLock(lockKey: string, ttlSeconds: number = 300): Promise<boolean> {
  // Try to set lock with TTL (returns true if lock acquired, false if already exists)
  const lockValue = `lock-${Date.now()}`;
  const acquired = await CacheService.setNX(lockKey, lockValue, ttlSeconds);

  return acquired;
}

/**
 * Release distributed lock
 */
async function releaseLock(lockKey: string): Promise<void> {
  await CacheService.del(lockKey);
}

/**
 * Process a single job
 */
async function processJob(jobId: string): Promise<void> {
  const job = await storage.getBackgroundJob(jobId);
  if (!job) {
    logger.error({ jobId }, `Job ${jobId} not found`);
    return;
  }

  // Skip if already running or completed
  if (job.status !== "pending") {
    logger.debug({ jobId, status: job.status }, `Job ${jobId} already ${job.status}, skipping`);
    return;
  }

  // Try to acquire distributed lock (prevents duplicate processing across instances)
  const lockKey = `job-lock:${jobId}`;
  const lockAcquired = await acquireLock(lockKey, 300); // 5 min TTL

  if (!lockAcquired) {
    logger.debug({ jobId }, `Job ${jobId} already locked by another worker, skipping`);
    return;
  }

  try {
    // Mark as running
    await storage.updateBackgroundJobStatus(jobId, "running", new Date());

    logger.info({ jobId, jobType: job.jobType }, `Processing job ${jobId} (${job.jobType})`);

    // Get handler
    const handler = jobHandlers.get(job.jobType as JobType);
    if (!handler) {
      throw new Error(`No handler registered for job type: ${job.jobType}`);
    }

    // Parse payload
    const payload: JobPayload = JSON.parse(job.payload);

    // Execute handler
    const result = await handler(payload);

    if (result.success) {
      await storage.completeBackgroundJob(jobId, JSON.stringify(result.data), new Date());
      logger.info({ jobId }, `Completed job ${jobId}`);
    } else {
      throw new Error(result.error || "Job failed");
    }
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Unknown error";
    await storage.failBackgroundJob(jobId, errorMessage, new Date());
    logger.error({ jobId, err: error }, `Failed job ${jobId}: ${errorMessage}`);
  } finally {
    // Always release the lock
    await releaseLock(lockKey);
  }
}

/**
 * Get job status
 */
export async function getJobStatus(jobId: string): Promise<BackgroundJob | null> {
  const job = await storage.getBackgroundJob(jobId);
  return job ?? null;
}

/**
 * Get all jobs of a specific type
 */
export async function getJobsByType(jobType: JobType): Promise<BackgroundJob[]> {
  return storage.getBackgroundJobsByType(jobType);
}

/**
 * Retry a failed job
 */
export async function retryJob(jobId: string): Promise<void> {
  await storage.updateBackgroundJobStatus(jobId, "pending");

  // Process async
  processJob(jobId).catch(err => {
    logger.error({ jobId, err }, `Error retrying job ${jobId}`);
  });
}

/**
 * Worker loop - processes pending jobs from database
 * This ensures jobs are picked up even after server restarts
 * 
 * PRODUCTION: Set ENABLE_BACKGROUND_WORKERS=true on only ONE server instance
 */
let workerLoopRunning = false;

export async function startWorkerLoop(intervalMs: number = 10000): Promise<void> {
  // Check if background workers are enabled via environment variable
  const workersEnabled = process.env.ENABLE_BACKGROUND_WORKERS === 'true' || process.env.NODE_ENV === 'development';

  if (!workersEnabled) {
    logger.info('Background workers disabled (set ENABLE_BACKGROUND_WORKERS=true to enable)');
    return;
  }

  if (workerLoopRunning) {
    logger.warn("Worker loop already running");
    return;
  }

  workerLoopRunning = true;
  logger.info({ intervalMs }, `Starting background job worker loop (${intervalMs}ms interval)`);

  const processNextBatch = async () => {
    try {
      // Get all pending jobs
      const pendingJobs = await storage.getPendingBackgroundJobs();

      if (pendingJobs.length > 0) {
        logger.info({ count: pendingJobs.length }, `Processing ${pendingJobs.length} pending job(s)`);

        // Process each job (sequentially to avoid overload)
        for (const job of pendingJobs) {
          await processJob(job.id);
        }
      }
    } catch (error) {
      logger.error({ err: error }, "Worker loop error");
    }

    // Schedule next batch
    if (workerLoopRunning) {
      setTimeout(processNextBatch, intervalMs);
    }
  };

  // Start the loop
  processNextBatch();
}

export function stopWorkerLoop(): void {
  workerLoopRunning = false;
  logger.info("Stopped background job worker loop");
}

/**
 * Schedule a recurring job (simple cron-like scheduler)
 */
export interface JobSchedule {
  jobType: JobType;
  payload: JobPayload;
  intervalMs: number; // How often to run (in milliseconds)
}

const scheduledJobs = new Map<string, NodeJS.Timeout>();

export function scheduleRecurringJob(schedule: JobSchedule, scheduleId: string): void {
  // Check if background workers are enabled via environment variable
  const workersEnabled = process.env.ENABLE_BACKGROUND_WORKERS === 'true' || process.env.NODE_ENV === 'development';

  if (!workersEnabled) {
    logger.info({ scheduleId }, `Skipping scheduled job ${scheduleId} (background workers disabled)`);
    return;
  }

  // Clear existing schedule if any
  if (scheduledJobs.has(scheduleId)) {
    clearInterval(scheduledJobs.get(scheduleId)!);
  }

  logger.info({ scheduleId, jobType: schedule.jobType, intervalMs: schedule.intervalMs }, `Scheduled recurring job: ${scheduleId}`);

  // Create recurring job
  const intervalId = setInterval(async () => {
    logger.info({ scheduleId }, `Triggering scheduled job: ${scheduleId}`);
    await enqueueJob(schedule.jobType, schedule.payload);
  }, schedule.intervalMs);

  scheduledJobs.set(scheduleId, intervalId);
}

export function cancelScheduledJob(scheduleId: string): void {
  if (scheduledJobs.has(scheduleId)) {
    clearInterval(scheduledJobs.get(scheduleId)!);
    scheduledJobs.delete(scheduleId);
    logger.info({ scheduleId }, `Cancelled scheduled job: ${scheduleId}`);
  }
}

/**
 * Initialize default job handlers
 */
export async function initializeJobHandlers(): Promise<void> {
  // Register certification processor
  const { processCertificationApplication } = await import("./certification-processor");

  registerJobHandler("certification_workflow", async (payload: JobPayload) => {
    const result = await processCertificationApplication(payload.applicationId);
    // Always return success=true to complete job cleanly
    // Application status and result payload encode actual pass/fail
    return { success: true, data: result };
  });

  // Register predictive alerts generator
  const { predictiveAlertService } = await import("./predictive-alert-service");

  registerJobHandler("predictive_alerts", async (payload: JobPayload) => {
    const healthSystemId = payload.healthSystemId;
    const alertsGenerated = await predictiveAlertService.generatePredictiveAlertsForHealthSystem(healthSystemId);
    return { 
      success: true, 
      data: { 
        healthSystemId, 
        alertsGenerated,
        timestamp: new Date().toISOString()
      } 
    };
  });

  // Register automated action executor
  const { actionExecutor } = await import("./action-executor");

  registerJobHandler("action_executor", async (payload: JobPayload) => {
    const result = await actionExecutor.processPendingActions();
    return {
      success: true,
      data: {
        executed: result.executed,
        failed: result.failed,
        timestamp: new Date().toISOString()
      }
    };
  });

  logger.info("Job handlers initialized");
}

server/services/certification-processor..content:
/**
 * Certification Application Processor
 * 
 * Performs automated testing and validation of vendor certification applications.
 * 
 * Workflow:
 * 1. Validate documentation completeness
 * 2. Check compliance statements
 * 3. Verify deployment history (if applicable)
 * 4. Run vendor testing suite (PHI, clinical accuracy, bias, security)
 * 5. Generate automated checks result
 * 6. Update application status
 */

import { storage } from "../storage";
import { logger } from "../logger";
import { VendorTestingSuite } from "./vendor-testing/testing-suite";

export interface CertificationCheckResult {
  passed: boolean;
  checks: {
    documentationComplete: boolean;
    complianceStatementsValid: boolean;
    deploymentHistoryValid: boolean;
    phiExposureTest: boolean;
    clinicalAccuracyTest: boolean;
    biasDetectionTest: boolean;
    securityScanTest: boolean;
  };
  recommendations: string[];
  score: number;
  testResults?: {
    phiExposure?: any;
    clinicalAccuracy?: any;
    biasDetection?: any;
    securityScan?: any;
  };
}

/**
 * Process a certification application (automated testing phase)
 */
export async function processCertificationApplication(applicationId: string): Promise<CertificationCheckResult> {
  logger.info({ applicationId }, `Processing certification application: ${applicationId}`);

  const application = await storage.getCertificationApplication(applicationId);

  if (!application) {
    throw new Error(`Application not found: ${applicationId}`);
  }

  // Automated checks
  const checks = {
    documentationComplete: checkDocumentationComplete(application),
    complianceStatementsValid: checkComplianceStatements(application),
    deploymentHistoryValid: await checkDeploymentHistory(application.vendorId, application.tierRequested),
    phiExposureTest: false,
    clinicalAccuracyTest: false,
    biasDetectionTest: false,
    securityScanTest: false,
  };

  const recommendations: string[] = [];
  const testResults: any = {};

  // Run vendor testing suite
  logger.info({ applicationId }, "Running vendor testing suite...");
  const { vendorTestingSuite } = await import("./vendor-testing/testing-suite");

  try {
    const testConfig = {
      applicationId,
      vendorId: application.vendorId,
      aiSystemEndpoint: application.apiEndpoint || undefined,
    };

    const allTestResults = await vendorTestingSuite.runAllTests(testConfig);

    // Process test results
    for (const testResult of allTestResults) {
      switch (testResult.testType) {
        case 'phi_exposure':
          testResults.phiExposure = testResult;
          checks.phiExposureTest = testResult.passed;
          if (!testResult.passed) {
            const violations = testResult.details?.violations || 0;
            recommendations.push(`PHI Exposure Test: ${violations} violations detected${testResult.errorMessage ? ' - ' + testResult.errorMessage : ''}`);
          }
          break;

        case 'clinical_accuracy':
          testResults.clinicalAccuracy = testResult;
          checks.clinicalAccuracyTest = testResult.passed;
          if (!testResult.passed) {
            recommendations.push(`Clinical Accuracy Test: Score ${testResult.score}% (minimum 90% required)`);
          }
          break;

        case 'bias_detection':
          testResults.biasDetection = testResult;
          checks.biasDetectionTest = testResult.passed;
          if (!testResult.passed) {
            const biasedGroups = testResult.details?.biasedGroups || [];
            recommendations.push(`Bias Detection Test: Bias detected in ${biasedGroups.join(", ")}`);
          }
          break;

        case 'security_scan':
          testResults.securityScan = testResult;
          checks.securityScanTest = testResult.passed;
          if (!testResult.passed) {
            const vulnerabilities = testResult.details?.vulnerabilities || 0;
            recommendations.push(`Security Scan: ${vulnerabilities} vulnerabilities found${testResult.errorMessage ? ' - ' + testResult.errorMessage : ''}`);
          }
          break;
      }
    }

    logger.info({ applicationId, testResults }, "Vendor testing suite completed");
  } catch (error) {
    logger.error({ applicationId, error }, "Vendor testing suite failed");
    recommendations.push("Automated testing suite encountered errors - manual review required");
  }

  // Generate recommendations based on basic checks
  if (!checks.documentationComplete) {
    recommendations.push("Please upload complete documentation for all requested compliance frameworks");
  }

  if (!checks.complianceStatementsValid) {
    recommendations.push("Compliance statements must align with requested certification tier");
  }

  if (!checks.deploymentHistoryValid) {
    if (application.tierRequested === "Platinum") {
      recommendations.push("Platinum tier requires at least 3 active deployments with health systems");
    } else if (application.tierRequested === "Gold") {
      recommendations.push("Gold tier requires at least 1 active deployment with a health system");
    }
  }

  // Calculate score (0-100)
  // Documentation: 20 points
  // Compliance statements: 20 points
  // Deployment history: 10 points
  // PHI test: 15 points
  // Clinical accuracy test: 15 points
  // Bias detection test: 10 points
  // Security scan: 10 points
  let score = 0;
  if (checks.documentationComplete) score += 20;
  if (checks.complianceStatementsValid) score += 20;
  if (checks.deploymentHistoryValid) score += 10;
  if (checks.phiExposureTest) score += 15;
  if (checks.clinicalAccuracyTest) score += 15;
  if (checks.biasDetectionTest) score += 10;
  if (checks.securityScanTest) score += 10;

  const passed = Object.values(checks).every(check => check === true);

  const result: CertificationCheckResult = {
    passed,
    checks,
    recommendations,
    score,
    testResults,
  };

  // Update application with automated check results
  await storage.updateCertificationApplicationStatus(
    applicationId,
    passed ? "in_review" : "pending",
    passed,
    JSON.stringify(result)
  );

  logger.info({ applicationId, passed, score }, `Certification application ${applicationId}: ${passed ? "PASSED" : "FAILED"} automated checks (score: ${score})`);

  return result;
}

/**
 * Check if documentation is complete for the requested tier
 */
function checkDocumentationComplete(application: any): boolean {
  const docUrls = application.documentationUrls || [];

  // Tier requirements:
  // Silver: At least 1 document (HIPAA compliance statement)
  // Gold: At least 2 documents (HIPAA + NIST)
  // Platinum: At least 3 documents (HIPAA + NIST + FDA or ISO)

  const requiredDocs: Record<string, number> = {
    "Silver": 1,
    "Gold": 2,
    "Platinum": 3,
  };

  const required = requiredDocs[application.tierRequested] || 1;
  return docUrls.length >= required;
}

/**
 * Check if compliance statements are valid
 */
function checkComplianceStatements(application: any): boolean {
  if (!application.complianceStatements) {
    return false;
  }

  try {
    const statements = JSON.parse(application.complianceStatements);

    // Tier requirements:
    // Silver: HIPAA compliance
    // Gold: HIPAA + NIST AI RMF
    // Platinum: HIPAA + NIST AI RMF + (FDA or ISO)

    switch (application.tierRequested) {
      case "Silver":
        return statements.hipaa === true;
      case "Gold":
        return statements.hipaa === true && statements.nist === true;
      case "Platinum":
        return statements.hipaa === true && 
               statements.nist === true && 
               (statements.fda === true || statements.iso === true);
      default:
        return false;
    }
  } catch (error) {
    return false;
  }
}

/**
 * Check deployment history meets tier requirements
 * 
 * PRODUCTION VERSION - Validates tier-specific deployment requirements:
 * - Silver: 0 required (welcoming new vendors)
 * - Gold: 1+ active deployments required
 * - Platinum: 3+ active deployments required
 */
async function checkDeploymentHistory(vendorId: string, tierRequested: string): Promise<boolean> {
  try {
    const deployments = await storage.getDeploymentsByVendor(vendorId);
    const activeDeployments = deployments.filter(d => d.status === "active");
    const activeCount = activeDeployments.length;

    logger.info({ 
      vendorId, 
      tierRequested, 
      activeCount 
    }, "Checking deployment history for certification tier");

    // Tier-specific deployment requirements
    switch (tierRequested) {
      case "Silver":
        // Silver tier: No deployment history required (welcome new vendors)
        return true;

      case "Gold":
        // Gold tier: At least 1 active deployment required
        if (activeCount < 1) {
          logger.warn({ vendorId, activeCount }, "Gold tier requires 1+ active deployments");
          return false;
        }
        return true;

      case "Platinum":
        // Platinum tier: At least 3 active deployments required (proven track record)
        if (activeCount < 3) {
          logger.warn({ vendorId, activeCount }, "Platinum tier requires 3+ active deployments");
          return false;
        }
        return true;

      default:
        logger.error({ tierRequested }, "Unknown certification tier requested");
        return false;
    }
  } catch (error) {
    logger.error({ err: error, vendorId }, "Failed to check deployment history");
    return false; // Fail closed for safety
  }
}

