import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/lib/auth";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { AreaChart, Area, BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from "recharts";
import { TrendingUp, TrendingDown, Minus, AlertTriangle, Shield, Activity, Building2, Download } from "lucide-react";
import { exportDepartmentMetrics, exportPortfolioHealth, exportAlertTrends, exportComplianceTrend, exportRiskTrend } from "@/lib/csv-export";

interface PortfolioHealth {
  overall: number;
  breakdown: {
    riskManagement: number;
    compliancePosture: number;
    alertResponse: number;
    systemVerification: number;
  };
  grade: "A" | "B" | "C" | "D" | "F";
  trend: "improving" | "stable" | "declining";
}

interface DepartmentMetrics {
  department: string;
  systemCount: number;
  averageRiskScore: number;
  averageComplianceRate: number;
  activeAlertCount: number;
  highRiskSystemCount: number;
}

interface AlertTrendAnalysis {
  totalAlerts: number;
  resolvedAlerts: number;
  unresolvedAlerts: number;
  bySeverity: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };
  timeSeries: Array<{ date: string; value: number }>;
  averageResolutionTimeHours: number | null;
}

export default function AdvancedAnalytics() {
  const { user } = useAuth();
  const healthSystemId = user?.healthSystemId;

  const { data: portfolioHealth, isLoading: healthLoading } = useQuery<PortfolioHealth>({
    queryKey: [`/api/health-systems/${healthSystemId}/analytics/portfolio-health`],
    enabled: !!healthSystemId,
  });

  const { data: departments, isLoading: deptLoading } = useQuery<DepartmentMetrics[]>({
    queryKey: [`/api/health-systems/${healthSystemId}/analytics/departments`],
    enabled: !!healthSystemId,
  });

  const { data: alertTrends, isLoading: alertLoading } = useQuery<AlertTrendAnalysis>({
    queryKey: [`/api/health-systems/${healthSystemId}/analytics/alert-trends`],
    enabled: !!healthSystemId,
  });

  const { data: complianceTrend, isLoading: complianceLoading } = useQuery<Array<{ date: string; value: number }>>({
    queryKey: [`/api/health-systems/${healthSystemId}/analytics/compliance-trend`],
    enabled: !!healthSystemId,
  });

  const { data: riskTrend, isLoading: riskLoading } = useQuery<Array<{ date: string; value: number }>>({
    queryKey: [`/api/health-systems/${healthSystemId}/analytics/risk-trend`],
    enabled: !!healthSystemId,
  });

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case "A": return "text-green-600 dark:text-green-400";
      case "B": return "text-blue-600 dark:text-blue-400";
      case "C": return "text-yellow-600 dark:text-yellow-400";
      case "D": return "text-orange-600 dark:text-orange-400";
      case "F": return "text-red-600 dark:text-red-400";
      default: return "text-gray-600 dark:text-gray-400";
    }
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case "improving": return <TrendingUp className="w-4 h-4 text-green-600" data-testid="icon-trending-up" />;
      case "declining": return <TrendingDown className="w-4 h-4 text-red-600" data-testid="icon-trending-down" />;
      default: return <Minus className="w-4 h-4 text-gray-600" data-testid="icon-stable" />;
    }
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="text-page-title">Advanced Analytics</h1>
          <p className="text-muted-foreground mt-1">
            Comprehensive insights into your AI portfolio performance
          </p>
        </div>
      </div>

      {/* Portfolio Health Score */}
      <Card data-testid="card-portfolio-health">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Shield className="w-5 h-5" />
                Portfolio Health Score
              </CardTitle>
              <CardDescription>
                Composite metric based on risk, compliance, alerts, and verification
              </CardDescription>
            </div>
            {portfolioHealth && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => exportPortfolioHealth(portfolioHealth)}
                data-testid="button-export-portfolio"
              >
                <Download className="w-4 h-4 mr-2" />
                Export
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          {healthLoading ? (
            <div className="space-y-4">
              <Skeleton className="h-24 w-full" />
              <Skeleton className="h-48 w-full" />
            </div>
          ) : portfolioHealth ? (
            <div className="space-y-6">
              <div className="flex items-center gap-6">
                <div className="text-center">
                  <div className={`text-6xl font-bold ${getGradeColor(portfolioHealth.grade)}`} data-testid="text-health-score">
                    {portfolioHealth.overall}
                  </div>
                  <div className="text-sm text-muted-foreground">Overall Score</div>
                </div>
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-2">
                    <Badge variant="outline" className={getGradeColor(portfolioHealth.grade)} data-testid="badge-health-grade">
                      Grade {portfolioHealth.grade}
                    </Badge>
                    <div className="flex items-center gap-1">
                      {getTrendIcon(portfolioHealth.trend)}
                      <span className="text-sm text-muted-foreground capitalize">
                        {portfolioHealth.trend}
                      </span>
                    </div>
                  </div>
                  <div className="grid grid-cols-2 gap-4 mt-4">
                    <div>
                      <div className="text-sm text-muted-foreground">Risk Management</div>
                      <div className="text-2xl font-semibold" data-testid="text-risk-score">
                        {portfolioHealth.breakdown.riskManagement}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">Compliance Posture</div>
                      <div className="text-2xl font-semibold" data-testid="text-compliance-score">
                        {portfolioHealth.breakdown.compliancePosture}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">Alert Response</div>
                      <div className="text-2xl font-semibold" data-testid="text-alert-score">
                        {portfolioHealth.breakdown.alertResponse}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">System Verification</div>
                      <div className="text-2xl font-semibold" data-testid="text-verification-score">
                        {portfolioHealth.breakdown.systemVerification}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="text-center text-muted-foreground py-8">
              No portfolio data available
            </div>
          )}
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Compliance Trend */}
        <Card data-testid="card-compliance-trend">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <Shield className="w-5 h-5" />
                  Compliance Trend
                </CardTitle>
                <CardDescription>6-month historical compliance rate</CardDescription>
              </div>
              {complianceTrend && complianceTrend.length > 0 && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => exportComplianceTrend(complianceTrend)}
                  data-testid="button-export-compliance"
                >
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent>
            {complianceLoading ? (
              <Skeleton className="h-64 w-full" />
            ) : complianceTrend && complianceTrend.length > 0 ? (
              <ResponsiveContainer width="100%" height={250}>
                <AreaChart data={complianceTrend}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Area type="monotone" dataKey="value" stroke="#10b981" fill="#10b981" fillOpacity={0.3} name="Compliance Rate (%)" />
                </AreaChart>
              </ResponsiveContainer>
            ) : (
              <div className="text-center text-muted-foreground py-12">
                No compliance trend data available
              </div>
            )}
          </CardContent>
        </Card>

        {/* Risk Score Trend */}
        <Card data-testid="card-risk-trend">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <AlertTriangle className="w-5 h-5" />
                  Risk Score Trend
                </CardTitle>
                <CardDescription>6-month historical risk levels</CardDescription>
              </div>
              {riskTrend && riskTrend.length > 0 && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => exportRiskTrend(riskTrend)}
                  data-testid="button-export-risk"
                >
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent>
            {riskLoading ? (
              <Skeleton className="h-64 w-full" />
            ) : riskTrend && riskTrend.length > 0 ? (
              <ResponsiveContainer width="100%" height={250}>
                <LineChart data={riskTrend}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Line type="monotone" dataKey="value" stroke="#ef4444" strokeWidth={2} name="Avg Risk Score" />
                </LineChart>
              </ResponsiveContainer>
            ) : (
              <div className="text-center text-muted-foreground py-12">
                No risk trend data available
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Department Metrics */}
      <Card data-testid="card-department-metrics">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Building2 className="w-5 h-5" />
                Department Performance
              </CardTitle>
              <CardDescription>
                Comparative metrics across organizational departments
              </CardDescription>
            </div>
            {departments && departments.length > 0 && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => exportDepartmentMetrics(departments)}
                data-testid="button-export-departments"
              >
                <Download className="w-4 h-4 mr-2" />
                Export
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          {deptLoading ? (
            <Skeleton className="h-80 w-full" />
          ) : departments && departments.length > 0 ? (
            <div className="space-y-4">
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={departments}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="department" />
                  <YAxis yAxisId="left" />
                  <YAxis yAxisId="right" orientation="right" />
                  <Tooltip />
                  <Legend />
                  <Bar yAxisId="left" dataKey="averageComplianceRate" fill="#10b981" name="Compliance %" />
                  <Bar yAxisId="right" dataKey="activeAlertCount" fill="#ef4444" name="Active Alerts" />
                </BarChart>
              </ResponsiveContainer>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-6">
                {departments.map((dept) => (
                  <div key={dept.department} className="border rounded-lg p-4" data-testid={`card-department-${dept.department.toLowerCase().replace(/\s+/g, '-')}`}>
                    <h4 className="font-semibold mb-2">{dept.department}</h4>
                    <div className="space-y-1 text-sm">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Systems:</span>
                        <span className="font-medium">{dept.systemCount}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Avg Risk:</span>
                        <span className="font-medium">{dept.averageRiskScore.toFixed(1)}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Compliance:</span>
                        <span className="font-medium">{dept.averageComplianceRate}%</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">High Risk:</span>
                        <span className="font-medium">{dept.highRiskSystemCount}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="text-center text-muted-foreground py-12">
              No department data available
            </div>
          )}
        </CardContent>
      </Card>

      {/* Alert Trends */}
      <Card data-testid="card-alert-trends">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Activity className="w-5 h-5" />
                Alert Activity
              </CardTitle>
              <CardDescription>30-day alert patterns and distribution</CardDescription>
            </div>
            {alertTrends && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => exportAlertTrends(alertTrends)}
                data-testid="button-export-alerts"
              >
                <Download className="w-4 h-4 mr-2" />
                Export
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          {alertLoading ? (
            <Skeleton className="h-64 w-full" />
          ) : alertTrends ? (
            <div className="space-y-6">
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div>
                  <div className="text-sm text-muted-foreground">Total Alerts</div>
                  <div className="text-2xl font-semibold" data-testid="text-total-alerts">{alertTrends.totalAlerts}</div>
                </div>
                <div>
                  <div className="text-sm text-muted-foreground">Resolved</div>
                  <div className="text-2xl font-semibold text-green-600" data-testid="text-resolved-alerts">{alertTrends.resolvedAlerts}</div>
                </div>
                <div>
                  <div className="text-sm text-muted-foreground">Unresolved</div>
                  <div className="text-2xl font-semibold text-red-600" data-testid="text-unresolved-alerts">{alertTrends.unresolvedAlerts}</div>
                </div>
                <div>
                  <div className="text-sm text-muted-foreground">Critical</div>
                  <div className="text-2xl font-semibold text-red-600" data-testid="text-critical-alerts">{alertTrends.bySeverity.critical}</div>
                </div>
              </div>
              {alertTrends.timeSeries && alertTrends.timeSeries.length > 0 && (
                <ResponsiveContainer width="100%" height={200}>
                  <LineChart data={alertTrends.timeSeries}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="value" stroke="#3b82f6" strokeWidth={2} name="Alerts per Day" />
                  </LineChart>
                </ResponsiveContainer>
              )}
            </div>
          ) : (
            <div className="text-center text-muted-foreground py-12">
              No alert data available
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/UserManagement.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { UserPlus, Mail, Shield, Clock, XCircle } from "lucide-react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";

const inviteUserSchema = z.object({
  email: z.string().email("Invalid email address"),
  permissions: z.enum(["admin", "user", "viewer"]),
});

type InviteUserForm = z.infer<typeof inviteUserSchema>;

interface User {
  id: string;
  username: string;
  email: string;
  firstName?: string | null;
  lastName?: string | null;
  permissions: string;
  status: string;
  lastLogin?: string | null;
  createdAt: string;
}

interface Invitation {
  id: string;
  email: string;
  permissions: string;
  status: string;
  expiresAt: string;
  createdAt: string;
  tokenPrefix: string;
}

export default function UserManagement() {
  const { toast } = useToast();
  const [inviteDialogOpen, setInviteDialogOpen] = useState(false);

  // Fetch users
  const { data: users = [], isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Fetch invitations
  const { data: invitations = [], isLoading: invitationsLoading } = useQuery<Invitation[]>({
    queryKey: ["/api/users/invitations"],
  });

  // Invite user mutation
  const inviteUserMutation = useMutation({
    mutationFn: async (data: InviteUserForm) => {
      return await apiRequest("POST", "/api/users/invite", data);
    },
    onSuccess: () => {
      toast({ title: "Invitation sent successfully" });
      queryClient.invalidateQueries({ queryKey: ["/api/users/invitations"] });
      setInviteDialogOpen(false);
      form.reset();
    },
    onError: (error: Error) => {
      toast({ 
        title: "Failed to send invitation", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  // Update user mutation
  const updateUserMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: { permissions?: string; status?: string } }) => {
      return await apiRequest("PATCH", `/api/users/${id}`, updates);
    },
    onSuccess: () => {
      toast({ title: "User updated successfully" });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
    onError: (error: Error) => {
      toast({ 
        title: "Failed to update user", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  // Cancel invitation mutation
  const cancelInvitationMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/users/invitations/${id}`);
    },
    onSuccess: () => {
      toast({ title: "Invitation cancelled" });
      queryClient.invalidateQueries({ queryKey: ["/api/users/invitations"] });
    },
    onError: (error: Error) => {
      toast({ 
        title: "Failed to cancel invitation", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const form = useForm<InviteUserForm>({
    resolver: zodResolver(inviteUserSchema),
    defaultValues: {
      email: "",
      permissions: "user",
    },
  });

  const onInviteSubmit = (data: InviteUserForm) => {
    inviteUserMutation.mutate(data);
  };

  const getPermissionBadgeVariant = (permissions: string) => {
    switch (permissions) {
      case "admin":
        return "default";
      case "user":
        return "secondary";
      case "viewer":
        return "outline";
      default:
        return "secondary";
    }
  };

  const getStatusBadgeVariant = (status: string) => {
    return status === "active" ? "default" : "secondary";
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">User Management</h1>
          <p className="text-muted-foreground mt-1">
            Invite and manage users in your organization
          </p>
        </div>
        <Dialog open={inviteDialogOpen} onOpenChange={setInviteDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-invite-user">
              <UserPlus className="w-4 h-4 mr-2" />
              Invite User
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Invite New User</DialogTitle>
              <DialogDescription>
                Send an invitation email to add a new user to your organization
              </DialogDescription>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onInviteSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="user@example.com" 
                          {...field} 
                          data-testid="input-invite-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="permissions"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Permissions</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger data-testid="select-permissions">
                            <SelectValue placeholder="Select permissions" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="admin">Admin - Full access</SelectItem>
                          <SelectItem value="user">User - Standard access</SelectItem>
                          <SelectItem value="viewer">Viewer - Read-only access</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end gap-2 pt-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setInviteDialogOpen(false)}
                    data-testid="button-cancel-invite"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={inviteUserMutation.isPending}
                    data-testid="button-send-invite"
                  >
                    {inviteUserMutation.isPending ? "Sending..." : "Send Invitation"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Active Users */}
      <Card>
        <CardHeader>
          <CardTitle>Active Users</CardTitle>
          <CardDescription>
            {users.length} {users.length === 1 ? "user" : "users"} in your organization
          </CardDescription>
        </CardHeader>
        <CardContent>
          {usersLoading ? (
            <p className="text-muted-foreground">Loading users...</p>
          ) : users.length === 0 ? (
            <p className="text-muted-foreground">No users found</p>
          ) : (
            <div className="space-y-3">
              {users.map((user) => (
                <div
                  key={user.id}
                  className="flex items-center justify-between p-4 border rounded-lg hover-elevate"
                  data-testid={`user-card-${user.id}`}
                >
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center">
                      <span className="text-sm font-semibold text-primary">
                        {user.email.charAt(0).toUpperCase()}
                      </span>
                    </div>
                    <div>
                      <p className="font-medium" data-testid={`text-username-${user.id}`}>
                        {user.username}
                      </p>
                      <p className="text-sm text-muted-foreground flex items-center gap-1">
                        <Mail className="w-3 h-3" />
                        {user.email}
                      </p>
                      {user.lastLogin && (
                        <p className="text-xs text-muted-foreground">
                          Last login: {formatDistanceToNow(new Date(user.lastLogin), { addSuffix: true })}
                        </p>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <Badge variant={getPermissionBadgeVariant(user.permissions)}>
                      <Shield className="w-3 h-3 mr-1" />
                      {user.permissions}
                    </Badge>
                    <Badge variant={getStatusBadgeVariant(user.status)}>
                      {user.status}
                    </Badge>
                    <Select
                      value={user.permissions}
                      onValueChange={(value) => updateUserMutation.mutate({ id: user.id, updates: { permissions: value } })}
                    >
                      <SelectTrigger className="w-32" data-testid={`select-user-permissions-${user.id}`}>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="admin">Admin</SelectItem>
                        <SelectItem value="user">User</SelectItem>
                        <SelectItem value="viewer">Viewer</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Pending Invitations */}
      <Card>
        <CardHeader>
          <CardTitle>Pending Invitations</CardTitle>
          <CardDescription>
            {invitations.filter(inv => inv.status === 'pending').length} pending {invitations.filter(inv => inv.status === 'pending').length === 1 ? "invitation" : "invitations"}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {invitationsLoading ? (
            <p className="text-muted-foreground">Loading invitations...</p>
          ) : invitations.filter(inv => inv.status === 'pending').length === 0 ? (
            <p className="text-muted-foreground">No pending invitations</p>
          ) : (
            <div className="space-y-3">
              {invitations.filter(inv => inv.status === 'pending').map((invitation) => (
                <div
                  key={invitation.id}
                  className="flex items-center justify-between p-4 border rounded-lg hover-elevate"
                  data-testid={`invitation-card-${invitation.id}`}
                >
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 rounded-full bg-orange-500/10 flex items-center justify-center">
                      <Clock className="w-5 h-5 text-orange-500" />
                    </div>
                    <div>
                      <p className="font-medium" data-testid={`text-invitation-email-${invitation.id}`}>
                        {invitation.email}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        Sent {formatDistanceToNow(new Date(invitation.createdAt), { addSuffix: true })}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        Expires {formatDistanceToNow(new Date(invitation.expiresAt), { addSuffix: true })}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <Badge variant={getPermissionBadgeVariant(invitation.permissions)}>
                      <Shield className="w-3 h-3 mr-1" />
                      {invitation.permissions}
                    </Badge>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => cancelInvitationMutation.mutate(invitation.id)}
                      disabled={cancelInvitationMutation.isPending}
                      data-testid={`button-cancel-invitation-${invitation.id}`}
                    >
                      <XCircle className="w-4 h-4 mr-1" />
                      Cancel
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/AuditLogs.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState } from "react";
import { FileText, User, Shield, Clock } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { AuditLog } from "@shared/schema";

export default function AuditLogs() {
  const [actionFilter, setActionFilter] = useState<string>("all");
  const [resourceTypeFilter, setResourceTypeFilter] = useState<string>("all");

  const { data: logs = [], isLoading } = useQuery<AuditLog[]>({
    queryKey: ["/api/audit-logs", { action: actionFilter !== "all" ? actionFilter : undefined, resourceType: resourceTypeFilter !== "all" ? resourceTypeFilter : undefined }],
  });

  const actionTypes = ["all", "create", "update", "delete", "login", "logout", "invite_user", "cancel_invitation", "accept_invitation", "resolve_alert", "approve_certification", "reject_certification"];
  const resourceTypes = ["all", "user", "ai_system", "alert", "certification", "deployment", "compliance_report"];

  const getActionColor = (action: string) => {
    if (action.includes("delete") || action.includes("reject")) return "destructive";
    if (action.includes("create") || action.includes("approve")) return "default";
    if (action.includes("update")) return "secondary";
    return "outline";
  };

  return (
    <div className="container mx-auto p-6 max-w-7xl">
      <div className="mb-6">
        <h1 className="text-3xl font-semibold mb-2" data-testid="text-audit-logs-title">Audit Logs</h1>
        <p className="text-muted-foreground">
          Comprehensive activity tracking for compliance and security monitoring
        </p>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <Shield className="h-5 w-5" />
            Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Action Type</label>
              <Select value={actionFilter} onValueChange={setActionFilter}>
                <SelectTrigger data-testid="select-action-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {actionTypes.map((action) => (
                    <SelectItem key={action} value={action}>
                      {action === "all" ? "All Actions" : action.replace(/_/g, " ").toUpperCase()}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Resource Type</label>
              <Select value={resourceTypeFilter} onValueChange={setResourceTypeFilter}>
                <SelectTrigger data-testid="select-resource-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {resourceTypes.map((type) => (
                    <SelectItem key={type} value={type}>
                      {type === "all" ? "All Resources" : type.replace(/_/g, " ").toUpperCase()}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Audit Log Entries */}
      {isLoading ? (
        <Card>
          <CardContent className="p-6">
            <p className="text-center text-muted-foreground">Loading audit logs...</p>
          </CardContent>
        </Card>
      ) : logs.length === 0 ? (
        <Card>
          <CardContent className="p-6">
            <p className="text-center text-muted-foreground">No audit logs found</p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-3">
          {logs.map((log) => (
            <Card key={log.id} className="hover-elevate" data-testid={`card-audit-log-${log.id}`}>
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between gap-4 flex-wrap">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-2 flex-wrap">
                      <Badge variant={getActionColor(log.action)} data-testid={`badge-action-${log.id}`}>
                        {log.action.replace(/_/g, " ").toUpperCase()}
                      </Badge>
                      <Badge variant="outline" data-testid={`badge-resource-${log.id}`}>
                        <FileText className="h-3 w-3 mr-1" />
                        {log.resourceType}
                      </Badge>
                      {log.resourceName && (
                        <span className="text-sm font-medium truncate" data-testid={`text-resource-name-${log.id}`}>
                          {log.resourceName}
                        </span>
                      )}
                    </div>
                    <CardDescription className="flex items-center gap-2 text-xs">
                      <Clock className="h-3 w-3" />
                      {formatDistanceToNow(new Date(log.createdAt), { addSuffix: true })}
                    </CardDescription>
                  </div>
                </div>
              </CardHeader>

              {(log.metadata !== null || log.changes !== null) ? (
                <CardContent className="pt-0">
                  <div className="space-y-2 text-sm">
                    {log.metadata !== null && log.metadata !== undefined && (
                      <div>
                        <span className="font-medium text-muted-foreground">Details: </span>
                        <span className="text-foreground">
                          {typeof log.metadata === 'object' 
                            ? JSON.stringify(log.metadata as Record<string, unknown>, null, 2)
                            : String(log.metadata)
                          }
                        </span>
                      </div>
                    )}

                    {log.changes !== null && log.changes !== undefined && (
                      <details className="mt-2">
                        <summary className="cursor-pointer font-medium text-muted-foreground hover:text-foreground">
                          View Changes
                        </summary>
                        <pre className="mt-2 p-3 bg-muted rounded-md text-xs overflow-x-auto">
                          {JSON.stringify(log.changes as Record<string, unknown>, null, 2)}
                        </pre>
                      </details>
                    )}

                    {(log.ipAddress || log.userAgent) ? (
                      <div className="flex items-center gap-4 text-xs text-muted-foreground mt-2 flex-wrap">
                        {log.ipAddress && (
                          <span data-testid={`text-ip-${log.id}`}>IP: {log.ipAddress}</span>
                        )}
                        {log.userAgent && (
                          <span className="truncate max-w-md" data-testid={`text-useragent-${log.id}`}>
                            {log.userAgent}
                          </span>
                        )}
                      </div>
                    ) : null}
                  </div>
                </CardContent>
              ) : null}
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/SystemHealth.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Activity, Users, AlertTriangle, FileText, Briefcase, CheckCircle } from "lucide-react";

interface SystemHealthMetrics {
  users: {
    total: number;
  };
  alerts: {
    total: number;
    unresolved: number;
  };
  auditLogs: {
    recent: number;
  };
  backgroundJobs: {
    pending: number;
    running: number;
  };
  aiSystems?: {
    total: number;
  };
}

export default function SystemHealth() {
  const { data: metrics, isLoading, isError, error, refetch } = useQuery<SystemHealthMetrics>({
    queryKey: ["/api/system-health"],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  if (isLoading) {
    return (
      <div className="container mx-auto p-6 max-w-7xl">
        <p className="text-center text-muted-foreground">Loading system health metrics...</p>
      </div>
    );
  }

  if (isError || !metrics) {
    return (
      <div className="container mx-auto p-6 max-w-7xl">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-destructive">
              <AlertTriangle className="h-5 w-5" />
              Error Loading System Health
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-muted-foreground mb-4">
              {error instanceof Error ? error.message : "Failed to fetch system health metrics. Please try again."}
            </p>
            <button 
              onClick={() => refetch()} 
              className="px-4 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90"
              data-testid="button-retry"
            >
              Retry
            </button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const healthScore = calculateHealthScore(metrics);

  return (
    <div className="container mx-auto p-6 max-w-7xl">
      <div className="mb-6">
        <h1 className="text-3xl font-semibold mb-2" data-testid="text-system-health-title">System Health</h1>
        <p className="text-muted-foreground">
          Monitor platform performance and system status
        </p>
      </div>

      {/* Overall Health Score */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Activity className="h-5 w-5" />
            Overall Health Score
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4">
            <div className="text-4xl font-bold" data-testid="text-health-score">
              {healthScore}%
            </div>
            <Badge variant={healthScore >= 80 ? "default" : healthScore >= 60 ? "secondary" : "destructive"}>
              {healthScore >= 80 ? "Healthy" : healthScore >= 60 ? "Fair" : "Needs Attention"}
            </Badge>
          </div>
        </CardContent>
      </Card>

      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Users */}
        <Card data-testid="card-users-metric">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Users className="h-4 w-4" />
              Active Users
            </CardTitle>
            <CardDescription>Total users in organization</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold" data-testid="text-users-count">
              {metrics?.users.total || 0}
            </div>
          </CardContent>
        </Card>

        {/* AI Systems (Health Systems only) */}
        {metrics?.aiSystems && (
          <Card data-testid="card-ai-systems-metric">
            <CardHeader className="pb-3">
              <CardTitle className="text-base flex items-center gap-2">
                <Activity className="h-4 w-4" />
                AI Systems
              </CardTitle>
              <CardDescription>Systems under monitoring</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold" data-testid="text-ai-systems-count">
                {metrics.aiSystems.total}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Alerts */}
        <Card data-testid="card-alerts-metric">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <AlertTriangle className="h-4 w-4" />
              Unresolved Alerts
            </CardTitle>
            <CardDescription>Alerts requiring attention</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex items-baseline gap-2">
              <div className="text-3xl font-bold" data-testid="text-alerts-unresolved">
                {metrics?.alerts.unresolved || 0}
              </div>
              {(metrics?.alerts.unresolved || 0) === 0 && (
                <CheckCircle className="h-5 w-5 text-green-600" />
              )}
            </div>
          </CardContent>
        </Card>

        {/* Audit Logs */}
        <Card data-testid="card-audit-metric">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Recent Activity
            </CardTitle>
            <CardDescription>Audit log entries (last 10)</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold" data-testid="text-audit-count">
              {metrics?.auditLogs.recent || 0}
            </div>
          </CardContent>
        </Card>

        {/* Background Jobs */}
        <Card data-testid="card-jobs-metric">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Briefcase className="h-4 w-4" />
              Background Jobs
            </CardTitle>
            <CardDescription>Pending job queue</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex items-baseline gap-2">
              <div className="text-3xl font-bold" data-testid="text-jobs-pending">
                {metrics?.backgroundJobs.pending || 0}
              </div>
              {(metrics?.backgroundJobs.pending || 0) === 0 && (
                <Badge variant="outline">All Clear</Badge>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

function calculateHealthScore(metrics: SystemHealthMetrics): number {
  let score = 100;

  // Deduct points for unresolved alerts
  if (metrics.alerts.unresolved > 0) {
    score -= Math.min(30, metrics.alerts.unresolved * 5);
  }

  // Deduct points for pending background jobs
  if (metrics.backgroundJobs.pending > 5) {
    score -= Math.min(20, (metrics.backgroundJobs.pending - 5) * 2);
  }

  // Bonus points for active audit logging
  if (metrics.auditLogs.recent >= 5) {
    score = Math.min(100, score + 5);
  }

  return Math.max(0, Math.round(score));
}

client/src/pages/OrganizationSettings.ts.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { Building2, Save } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

const organizationSchema = z.object({
  name: z.string().min(1, "Organization name is required"),
  description: z.string().optional(),
  website: z.string().url("Must be a valid URL").optional().or(z.literal("")),
  logoUrl: z.string().url("Must be a valid URL").optional().or(z.literal("")),
});

type OrganizationFormData = z.infer<typeof organizationSchema>;

interface Organization {
  id: string;
  name: string;
  description?: string;
  website?: string;
  logoUrl?: string;
  settings?: string;
  type: 'health-system' | 'vendor';
}

export default function OrganizationSettings() {
  const { toast } = useToast();

  const { data: organization, isLoading } = useQuery<Organization>({
    queryKey: ["/api/organization"],
  });

  const form = useForm<OrganizationFormData>({
    resolver: zodResolver(organizationSchema),
    values: {
      name: organization?.name || "",
      description: organization?.description || "",
      website: organization?.website || "",
      logoUrl: organization?.logoUrl || "",
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: OrganizationFormData) => {
      return apiRequest("PATCH", "/api/organization", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/organization"] });
      toast({
        title: "Settings saved",
        description: "Your organization settings have been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update organization settings. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: OrganizationFormData) => {
    updateMutation.mutate(data);
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6 max-w-4xl">
        <p className="text-center text-muted-foreground">Loading organization settings...</p>
      </div>
    );
  }

  const isVendor = organization?.type === 'vendor';

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <div className="mb-6">
        <h1 className="text-3xl font-semibold mb-2" data-testid="text-org-settings-title">Organization Settings</h1>
        <p className="text-muted-foreground">
          Manage your organization profile and preferences
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Building2 className="h-5 w-5" />
            Organization Profile
          </CardTitle>
          <CardDescription>
            Update your organization details and branding
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Organization Name</FormLabel>
                    <FormControl>
                      <Input 
                        {...field} 
                        placeholder="Enter organization name"
                        data-testid="input-org-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description</FormLabel>
                    <FormControl>
                      <Textarea 
                        {...field} 
                        placeholder="Brief description of your organization"
                        rows={3}
                        data-testid="input-org-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {isVendor && (
                <>
                  <FormField
                    control={form.control}
                    name="website"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Website</FormLabel>
                        <FormControl>
                          <Input 
                            {...field} 
                            type="url"
                            placeholder="https://example.com"
                            data-testid="input-org-website"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="logoUrl"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Logo URL</FormLabel>
                        <FormControl>
                          <Input 
                            {...field} 
                            type="url"
                            placeholder="https://example.com/logo.png"
                            data-testid="input-org-logo"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </>
              )}

              <div className="flex justify-end gap-3">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => form.reset()}
                  data-testid="button-reset"
                >
                  Reset
                </Button>
                <Button 
                  type="submit" 
                  disabled={updateMutation.isPending}
                  data-testid="button-save"
                >
                  <Save className="h-4 w-4 mr-2" />
                  {updateMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

server/cache.ts.content:
import { Redis } from '@upstash/redis';
import { logger } from './logger';

// Upstash Redis client for caching compliance data
// Set UPSTASH_REDIS_REST_URL and UPSTASH_REDIS_REST_TOKEN in environment
let redis: Redis | null = null;

if (process.env.UPSTASH_REDIS_REST_URL && process.env.UPSTASH_REDIS_REST_TOKEN) {
  redis = new Redis({
    url: process.env.UPSTASH_REDIS_REST_URL,
    token: process.env.UPSTASH_REDIS_REST_TOKEN,
  });
  logger.info('Redis cache initialized');
} else {
  logger.warn('Redis cache disabled (no credentials)');
}

// Cache TTLs (in seconds)
export const CACHE_TTL = {
  COMPLIANCE_CONTROLS: 60 * 60 * 24, // 24 hours (rarely changes)
  COMPLIANCE_MAPPINGS: 60 * 60, // 1 hour
  HEALTH_SYSTEM: 60 * 15, // 15 minutes
  VENDOR: 60 * 15, // 15 minutes
  AI_SYSTEMS: 60 * 5, // 5 minutes (changes frequently)
  ANALYTICS: 60 * 30, // 30 minutes
} as const;

// Cache key prefixes
const KEYS = {
  COMPLIANCE_CONTROL: (id: string) => `compliance:control:${id}`,
  COMPLIANCE_CONTROLS_ALL: () => 'compliance:controls:all',
  COMPLIANCE_MAPPING: (aiSystemId: string) => `compliance:mapping:${aiSystemId}`,
  HEALTH_SYSTEM: (id: string) => `health_system:${id}`,
  VENDOR: (id: string) => `vendor:${id}`,
  AI_SYSTEM: (id: string) => `ai_system:${id}`,
  ANALYTICS: (healthSystemId: string) => `analytics:${healthSystemId}`,
} as const;

export class CacheService {
  /**
   * Get value from cache
   */
  static async get<T>(key: string): Promise<T | null> {
    if (!redis) return null;

    try {
      const value = await redis.get<T>(key);
      return value;
    } catch (error) {
      logger.error({ err: error, key }, 'Redis get error');
      return null;
    }
  }

  /**
   * Set value in cache with TTL
   */
  static async set(key: string, value: any, ttl: number): Promise<void> {
    if (!redis) return;

    try {
      await redis.set(key, value, { ex: ttl });
    } catch (error) {
      logger.error({ err: error, key, ttl }, 'Redis set error');
    }
  }

  /**
   * Set value in cache with TTL only if key doesn't exist (for distributed locking)
   * Returns true if lock acquired, false if already exists
   */
  static async setNX(key: string, value: any, ttl: number): Promise<boolean> {
    if (!redis) return true; // No Redis = no distributed locking needed (single instance)

    try {
      const result = await redis.set(key, value, { ex: ttl, nx: true });
      return result === 'OK';
    } catch (error) {
      logger.error({ err: error, key, ttl }, 'Redis setNX error');
      return false;
    }
  }

  /**
   * Delete value from cache
   */
  static async del(key: string): Promise<void> {
    if (!redis) return;

    try {
      await redis.del(key);
    } catch (error) {
      logger.error({ err: error, key }, 'Redis del error');
    }
  }

  /**
   * Delete multiple keys matching a pattern
   */
  static async delPattern(pattern: string): Promise<void> {
    if (!redis) return;

    try {
      const keys = await redis.keys(pattern);
      if (keys.length > 0) {
        await redis.del(...keys);
      }
    } catch (error) {
      logger.error({ err: error, pattern }, 'Redis delPattern error');
    }
  }

  // Compliance Controls
  static async getComplianceControl(id: string) {
    return this.get(KEYS.COMPLIANCE_CONTROL(id));
  }

  static async setComplianceControl(id: string, control: any) {
    return this.set(KEYS.COMPLIANCE_CONTROL(id), control, CACHE_TTL.COMPLIANCE_CONTROLS);
  }

  static async getAllComplianceControls() {
    return this.get(KEYS.COMPLIANCE_CONTROLS_ALL());
  }

  static async setAllComplianceControls(controls: any[]) {
    return this.set(KEYS.COMPLIANCE_CONTROLS_ALL(), controls, CACHE_TTL.COMPLIANCE_CONTROLS);
  }

  static async invalidateComplianceControls() {
    await this.delPattern('compliance:control:*');
    await this.del(KEYS.COMPLIANCE_CONTROLS_ALL());
  }

  // Compliance Mappings
  static async getComplianceMapping(aiSystemId: string) {
    return this.get(KEYS.COMPLIANCE_MAPPING(aiSystemId));
  }

  static async setComplianceMapping(aiSystemId: string, mappings: any) {
    return this.set(KEYS.COMPLIANCE_MAPPING(aiSystemId), mappings, CACHE_TTL.COMPLIANCE_MAPPINGS);
  }

  static async invalidateComplianceMapping(aiSystemId: string) {
    return this.del(KEYS.COMPLIANCE_MAPPING(aiSystemId));
  }

  // Health Systems
  static async getHealthSystem(id: string) {
    return this.get(KEYS.HEALTH_SYSTEM(id));
  }

  static async setHealthSystem(id: string, healthSystem: any) {
    return this.set(KEYS.HEALTH_SYSTEM(id), healthSystem, CACHE_TTL.HEALTH_SYSTEM);
  }

  static async invalidateHealthSystem(id: string) {
    return this.del(KEYS.HEALTH_SYSTEM(id));
  }

  // Vendors
  static async getVendor(id: string) {
    return this.get(KEYS.VENDOR(id));
  }

  static async setVendor(id: string, vendor: any) {
    return this.set(KEYS.VENDOR(id), vendor, CACHE_TTL.VENDOR);
  }

  static async invalidateVendor(id: string) {
    return this.del(KEYS.VENDOR(id));
  }

  // AI Systems
  static async getAISystem(id: string) {
    return this.get(KEYS.AI_SYSTEM(id));
  }

  static async setAISystem(id: string, aiSystem: any) {
    return this.set(KEYS.AI_SYSTEM(id), aiSystem, CACHE_TTL.AI_SYSTEMS);
  }

  static async invalidateAISystem(id: string) {
    return this.del(KEYS.AI_SYSTEM(id));
  }

  // Analytics
  static async getAnalytics(healthSystemId: string) {
    return this.get(KEYS.ANALYTICS(healthSystemId));
  }

  static async setAnalytics(healthSystemId: string, analytics: any) {
    return this.set(KEYS.ANALYTICS(healthSystemId), analytics, CACHE_TTL.ANALYTICS);
  }

  static async invalidateAnalytics(healthSystemId: string) {
    return this.del(KEYS.ANALYTICS(healthSystemId));
  }
}

