/**
 * CSRF Protection Middleware
 * 
 * Implements Double-Submit Cookie pattern with session binding
 * - Generates CSRF token stored in session
 * - Validates token on state-changing requests (POST, PUT, DELETE, PATCH)
 * - Provides /api/csrf-token endpoint for frontend to fetch token
 */

import type { Request, Response, NextFunction } from 'express';
import crypto from 'crypto';
import { logger } from '../logger';

/**
 * Generate a cryptographically secure CSRF token
 */
export function generateCsrfToken(): string {
  return crypto.randomBytes(32).toString('hex');
}

/**
 * Middleware to ensure CSRF token exists in session
 */
export function ensureCsrfToken(req: Request, res: Response, next: NextFunction): void {
  if (!req.session.csrfToken) {
    req.session.csrfToken = generateCsrfToken();
  }
  next();
}

/**
 * Middleware to validate CSRF token on state-changing requests
 */
export function validateCsrfToken(req: Request, res: Response, next: NextFunction): Response | void {
  const method = req.method.toUpperCase();

  // Skip CSRF validation for safe methods
  if (['GET', 'HEAD', 'OPTIONS'].includes(method)) {
    return next();
  }

  // Skip CSRF validation for unauthenticated requests (no session = no CSRF risk)
  if (!req.session.userId) {
    return next();
  }

  // Skip CSRF validation for API webhooks (authenticated via other means)
  if (req.path.startsWith('/api/webhooks/')) {
    return next();
  }

  // Get token from header (preferred) or body
  const tokenFromRequest = req.get('X-CSRF-Token') || req.body?.csrfToken;
  const tokenFromSession = req.session.csrfToken;

  if (!tokenFromRequest || !tokenFromSession) {
    logger.warn({
      path: req.path,
      method: req.method,
      hasRequestToken: !!tokenFromRequest,
      hasSessionToken: !!tokenFromSession,
    }, 'CSRF validation failed: Missing token');
    return res.status(403).json({ error: 'CSRF token missing' });
  }

  // Use constant-time comparison to prevent timing attacks
  if (!crypto.timingSafeEqual(Buffer.from(tokenFromRequest), Buffer.from(tokenFromSession))) {
    logger.warn({
      path: req.path,
      method: req.method,
    }, 'CSRF validation failed: Token mismatch');
    return res.status(403).json({ error: 'Invalid CSRF token' });
  }

  next();
}

/**
 * Route handler to get CSRF token
 */
export function getCsrfToken(req: Request, res: Response): Response {
  if (!req.session.csrfToken) {
    req.session.csrfToken = generateCsrfToken();
  }

  return res.json({ csrfToken: req.session.csrfToken });
}

server/logger.ts.content:
/**
 * Structured Logging with Pino
 * 
 * Provides production-ready structured logging with:
 * - JSON output for log aggregation (production)
 * - Pretty-printed output for development
 * - Request correlation IDs
 * - Automatic error serialization
 * - User context tracking
 * - Performance metrics
 */

import pino from 'pino';
import type { Request, Response } from 'express';

// Create base logger instance
export const logger = pino({
  level: process.env.LOG_LEVEL || (process.env.NODE_ENV === 'production' ? 'info' : 'debug'),

  // Use pretty printing in development, JSON in production
  transport: process.env.NODE_ENV === 'development' ? {
    target: 'pino-pretty',
    options: {
      colorize: true,
      translateTime: 'HH:MM:ss',
      ignore: 'pid,hostname',
    }
  } : undefined,

  // Base configuration
  base: {
    env: process.env.NODE_ENV || 'development',
    service: 'spectral-healthcare-ai',
  },

  // Serialize errors with stack traces
  serializers: {
    err: pino.stdSerializers.err,
    req: pino.stdSerializers.req,
    res: pino.stdSerializers.res,
  },

  // Redact sensitive fields from logs
  redact: {
    paths: ['req.headers.authorization', 'req.headers.cookie', 'password', 'token', 'apiKey'],
    censor: '[REDACTED]',
  },
});

/**
 * Create child logger with request context
 */
export function createRequestLogger(req: Request) {
  return logger.child({
    requestId: generateRequestId(),
    method: req.method,
    path: req.path,
    userId: (req.session as any)?.userId,
    ip: req.ip,
  });
}

/**
 * Generate unique request ID
 */
function generateRequestId(): string {
  return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Log HTTP request/response
 */
export function logHttpRequest(req: Request, res: Response, duration: number) {
  const level = res.statusCode >= 500 ? 'error' : res.statusCode >= 400 ? 'warn' : 'info';

  logger[level]({
    type: 'http_request',
    method: req.method,
    path: req.path,
    statusCode: res.statusCode,
    duration,
    userId: (req.session as any)?.userId,
    ip: req.ip,
    userAgent: req.get('user-agent'),
  }, `${req.method} ${req.path} ${res.statusCode} ${duration}ms`);
}

/**
 * Log business events
 */
export function logEvent(event: {
  type: string;
  action: string;
  userId?: string;
  resourceType?: string;
  resourceId?: string;
  metadata?: Record<string, any>;
}) {
  logger.info({
    logType: 'business_event',
    eventType: event.type,
    action: event.action,
    userId: event.userId,
    resourceType: event.resourceType,
    resourceId: event.resourceId,
    metadata: event.metadata,
  }, `${event.type}: ${event.action}`);
}

/**
 * Log security events
 */
export function logSecurityEvent(event: {
  type: 'auth_success' | 'auth_failure' | 'csrf_violation' | 'unauthorized_access' | 'permission_denied';
  userId?: string;
  ip?: string;
  path?: string;
  metadata?: Record<string, any>;
}) {
  logger.warn({
    logType: 'security_event',
    eventType: event.type,
    userId: event.userId,
    ip: event.ip,
    path: event.path,
    metadata: event.metadata,
  }, `Security: ${event.type}`);
}

/**
 * Log database operations
 */
export function logDbOperation(operation: {
  type: 'query' | 'insert' | 'update' | 'delete';
  table: string;
  duration: number;
  error?: Error;
}) {
  if (operation.error) {
    logger.error({
      logType: 'db_operation',
      operationType: operation.type,
      table: operation.table,
      duration: operation.duration,
      err: operation.error,
    }, `DB ${operation.type} failed on ${operation.table}`);
  } else {
    logger.debug({
      logType: 'db_operation',
      operationType: operation.type,
      table: operation.table,
      duration: operation.duration,
    }, `DB ${operation.type} on ${operation.table} (${operation.duration}ms)`);
  }
}

/**
 * Log performance metrics
 */
export function logMetric(metric: {
  name: string;
  value: number;
  unit: 'ms' | 'count' | 'bytes';
  tags?: Record<string, string>;
}) {
  logger.info({
    type: 'metric',
    ...metric,
  }, `Metric: ${metric.name} = ${metric.value}${metric.unit}`);
}

server/init-db-indexes.ts.content:
/**
 * Database Index Initialization
 * 
 * Creates critical performance indexes for production deployment
 * These indexes dramatically improve query performance for:
 * - AI systems filtering by health system
 * - Monitoring alerts filtering by system and status
 * - Compliance mappings reverse lookups
 * - Audit log time-based queries
 * - Telemetry time-series queries
 */

import { db } from "./db";
import { sql } from "drizzle-orm";
import { logger } from "./logger";

export async function initializeDatabaseIndexes(): Promise<void> {
  try {
    logger.info("Creating database indexes...");

    // AI Systems: Index for filtering by health system
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_ai_systems_health_system ON ai_systems (health_system_id)`);

    // Monitoring Alerts: Composite index for finding unresolved alerts by AI system
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_monitoring_alerts_system_resolved ON monitoring_alerts (ai_system_id, resolved)`);

    // Audit Logs: Index for time-based compliance queries
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_audit_logs_created_at ON audit_logs (created_at DESC)`);

    // Compliance Mappings: Index for reverse lookups from controls to systems
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_compliance_mappings_control ON compliance_mappings (control_id)`);

    // AI Telemetry: Index for time-based telemetry queries
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_ai_telemetry_created_at ON ai_telemetry_events (created_at DESC)`);

    // AI Telemetry: Composite index for system-specific time-series queries
    await db.execute(sql`CREATE INDEX IF NOT EXISTS idx_ai_telemetry_system_time ON ai_telemetry_events (ai_system_id, created_at DESC)`);

    logger.info("Database indexes created successfully");
  } catch (error) {
    logger.error({ err: error }, "Failed to create database indexes");
    throw error;
  }
}

server/services/ENCRYPTION_README.md.content:
# API Key Encryption Service

## Overview
This service provides HIPAA-compliant encryption for sensitive data (API keys, secrets) stored in the database using AES-256-GCM encryption.

## Setup

### 1. Generate Encryption Key
Run this command to generate a secure encryption key:
```bash
node -e "console.log(require('crypto').randomBytes(32).toString('base64'))"
```

### 2. Set Environment Variable
Add the generated key to your secrets:
```bash
ENCRYPTION_KEY=<your-generated-key-from-step-1>
```

**IMPORTANT**: 
- Never commit this key to version control
- Store it securely in Replit Secrets or your environment
- If you lose this key, you cannot decrypt existing data

## Usage

### Encrypting Data
```typescript
import { encrypt, encryptFields } from './services/encryption';

// Encrypt a single value
const encryptedApiKey = encrypt('sk-1234567890abcdef');

// Encrypt specific fields in an object
const config = {
  apiKey: 'sk-1234567890abcdef',
  projectId: 'proj-123', // won't be encrypted
  secretKey: 'secret-xyz'
};

const encryptedConfig = encryptFields(config, ['apiKey', 'secretKey']);
// Result: { apiKey: 'encrypted:...', projectId: 'proj-123', secretKey: 'encrypted:...' }
```

### Decrypting Data
```typescript
import { decrypt, decryptFields } from './services/encryption';

// Decrypt a single value
const apiKey = decrypt('encrypted:iv:authTag:ciphertext');

// Decrypt specific fields in an object
const decryptedConfig = decryptFields(encryptedConfig, ['apiKey', 'secretKey']);
// Result: { apiKey: 'sk-1234567890abcdef', projectId: 'proj-123', secretKey: 'secret-xyz' }
```

### Example: Storing AI System Integration Config
```typescript
import { encryptFields, decryptFields } from './services/encryption';

// When creating/updating AI system with integration credentials
const integrationConfig = {
  provider: 'langsmith', // Not sensitive
  apiKey: 'ls-1234567890', // Sensitive - should be encrypted
  projectId: 'my-project', // Not sensitive
  webhookSecret: 'secret-abc' // Sensitive - should be encrypted
};

// Encrypt before storing
const encryptedConfig = encryptFields(integrationConfig, ['apiKey', 'webhookSecret']);
await storage.updateAISystem(aiSystemId, {
  integrationConfig: encryptedConfig
});

// Decrypt when reading
const aiSystem = await storage.getAISystem(aiSystemId);
if (aiSystem.integrationConfig) {
  const decryptedConfig = decryptFields(
    aiSystem.integrationConfig, 
    ['apiKey', 'webhookSecret']
  );
  // Use decryptedConfig.apiKey to make API calls
}
```

## Security Features

1. **AES-256-GCM**: Industry-standard authenticated encryption
2. **Unique IV per encryption**: Each encryption uses a fresh initialization vector
3. **Authentication tags**: Protects against tampering
4. **Backward compatible**: Detects unencrypted legacy data and handles gracefully
5. **HIPAA compliant**: Meets encryption requirements for protected health information

## Error Handling

The service will:
- Throw an error if `ENCRYPTION_KEY` is not set
- Throw an error if the key is the wrong length
- Log errors and throw on encryption/decryption failures
- Gracefully handle unencrypted legacy data (won't fail if field doesn't have 'encrypted:' prefix)

## Key Rotation (Advanced)

If you need to rotate your encryption key:
1. Generate a new key
2. Create a migration script that:
   - Reads all encrypted data with old key
   - Decrypts with old key
   - Re-encrypts with new key
   - Updates database
3. Update `ENCRYPTION_KEY` environment variable
4. Deploy the migration

**Note**: This is a complex operation. Contact your security team before attempting key rotation.

