import { db } from "../../db";
import { aiSystems, stateRegulations } from "../../../shared/schema";
import { eq, and, lte, or, isNull } from "drizzle-orm";
import type { ParsedEvent } from "./types";
import { logger } from "../../utils/logger";

export interface StateLawViolation {
  state: string;
  regulationName: string;
  controlId: string;
  controlName: string;
  description: string;
  requiresReporting: boolean;
  reportingDeadlineDays?: number;
  detectedAt: Date;
  affectedSystem: {
    id: string;
    name: string;
    department: string;
  };
  severity: 'critical' | 'high' | 'medium' | 'low';
}

export interface AISystemContext {
  id: string;
  name: string;
  department: string;
  location?: string;
  aiSystemType?: string;
  isHighRisk?: boolean;
  isEmploymentAI?: boolean;
}

export class StateLawEngine {
  constructor() {
    logger.info("üèõÔ∏è State Law Engine initialized");
  }

  async checkCompliance(
    event: ParsedEvent,
    systemContext: AISystemContext
  ): Promise<StateLawViolation[]> {
    const violations: StateLawViolation[] = [];

    const activeRegulations = await db
      .select()
      .from(stateRegulations)
      .where(
        and(
          lte(stateRegulations.effectiveDate, new Date()),
          or(
            isNull(stateRegulations.sunsetDate),
            lte(new Date(), stateRegulations.sunsetDate)
          )
        )
      );

    for (const regulation of activeRegulations) {
      const isApplicable = this.isRegulationApplicable(
        regulation,
        event,
        systemContext
      );

      if (isApplicable) {
        const violation = this.createViolation(regulation, event, systemContext);
        if (violation) {
          violations.push(violation);
        }
      }
    }

    if (violations.length > 0) {
      logger.info("State law violations detected", {
        eventType: event.eventType,
        aiSystemId: systemContext.id,
        violationCount: violations.length,
        states: violations.map(v => v.state),
      });
    }

    return violations;
  }

  private isRegulationApplicable(
    regulation: any,
    event: ParsedEvent,
    systemContext: AISystemContext
  ): boolean {
    const mappedEventTypes = regulation.mappedEventTypes || [];
    const eventMatches = mappedEventTypes.includes(event.eventType);

    if (!eventMatches) {
      return false;
    }

    switch (regulation.state) {
      case 'CA':
        return this.isCaliforniaApplicable(regulation, systemContext);
      case 'CO':
        return this.isColoradoApplicable(regulation, systemContext);
      case 'NY':
        return this.isNewYorkApplicable(regulation, systemContext);
      default:
        return false;
    }
  }

  private isCaliforniaApplicable(regulation: any, system: AISystemContext): boolean {
    if (regulation.regulationName === 'CA SB 1047') {
      return (
        system.location === 'California' ||
        system.isHighRisk === true ||
        this.isHighRiskAIType(system.department)
      );
    }
    return false;
  }

  private isColoradoApplicable(regulation: any, system: AISystemContext): boolean {
    if (regulation.regulationName === 'Colorado AI Act') {
      return (
        system.location === 'Colorado' ||
        system.isHighRisk === true ||
        this.isHighImpactAIType(system.department)
      );
    }
    return false;
  }

  private isNewYorkApplicable(regulation: any, system: AISystemContext): boolean {
    if (regulation.regulationName === 'NYC Local Law 144') {
      return (
        system.location === 'New York' ||
        system.isEmploymentAI === true ||
        system.department === 'HR' ||
        system.name.toLowerCase().includes('hiring') ||
        system.name.toLowerCase().includes('recruitment')
      );
    }
    return false;
  }

  private isHighRiskAIType(department: string): boolean {
    const highRiskDepartments = [
      'Imaging',
      'Pathology',
      'Emergency',
      'Surgery',
      'Intensive Care',
      'Cardiology',
    ];
    return highRiskDepartments.includes(department);
  }

  private isHighImpactAIType(department: string): boolean {
    const highImpactDepartments = [
      'Imaging',
      'Pathology',
      'Emergency',
      'Surgery',
      'HR',
      'Finance',
    ];
    return highImpactDepartments.includes(department);
  }

  private createViolation(
    regulation: any,
    event: ParsedEvent,
    systemContext: AISystemContext
  ): StateLawViolation | null {
    const detectionLogic = regulation.detectionLogic || {};
    const severity = this.calculateSeverity(event, detectionLogic);

    return {
      state: regulation.state,
      regulationName: regulation.regulationName,
      controlId: regulation.controlId,
      controlName: regulation.controlName,
      description: this.buildDescription(regulation, event, systemContext),
      requiresReporting: regulation.requiresReporting || false,
      reportingDeadlineDays: regulation.reportingDeadlineDays,
      detectedAt: event.metadata.timestamp,
      affectedSystem: {
        id: systemContext.id,
        name: systemContext.name,
        department: systemContext.department,
      },
      severity,
    };
  }

  private calculateSeverity(
    event: ParsedEvent,
    detectionLogic: any
  ): 'critical' | 'high' | 'medium' | 'low' {
    if (event.severity === 'critical') return 'critical';

    const severityMapping = detectionLogic.severityMapping || {};
    return severityMapping[event.eventType] || event.severity || 'medium';
  }

  private buildDescription(
    regulation: any,
    event: ParsedEvent,
    systemContext: AISystemContext
  ): string {
    const baseDescription = regulation.description;

    return `${baseDescription} Violation detected on ${systemContext.name} (${systemContext.department}). Event: ${event.eventType}. Immediate review required.`;
  }

  async seedStateRegulations(): Promise<void> {
    logger.info("üå± Seeding state regulations...");

    const existingCount = await db.select().from(stateRegulations);
    if (existingCount.length > 0) {
      logger.info("State regulations already seeded, skipping...");
      return;
    }

    const regulations = [
      {
        state: 'CA',
        regulationName: 'CA SB 1047',
        controlId: 'CA-SB1047-1',
        controlName: 'Safety Testing Requirements',
        description: 'Covered AI models must undergo safety testing before deployment.',
        requiresReporting: true,
        reportingDeadlineDays: 30,
        effectiveDate: new Date('2024-01-01'),
        sunsetDate: null,
        mappedEventTypes: ['clinical_accuracy_failure', 'harmful_output', 'model_drift'],
        detectionLogic: {
          severityMapping: {
            clinical_accuracy_failure: 'critical',
            harmful_output: 'critical',
            model_drift: 'high',
          },
        },
      },
      {
        state: 'CA',
        regulationName: 'CA SB 1047',
        controlId: 'CA-SB1047-2',
        controlName: 'Incident Reporting',
        description: 'Developers must report safety incidents within specified timeframes.',
        requiresReporting: true,
        reportingDeadlineDays: 10,
        effectiveDate: new Date('2024-01-01'),
        sunsetDate: null,
        mappedEventTypes: ['harmful_output', 'phi_exposure', 'unauthorized_data_access'],
        detectionLogic: {
          severityMapping: {
            harmful_output: 'critical',
            phi_exposure: 'critical',
            unauthorized_data_access: 'critical',
          },
        },
      },
      {
        state: 'CO',
        regulationName: 'Colorado AI Act',
        controlId: 'CO-AI-1',
        controlName: 'Algorithmic Impact Assessment',
        description: 'High-risk AI systems require impact assessments covering discrimination risks.',
        requiresReporting: true,
        reportingDeadlineDays: 60,
        effectiveDate: new Date('2024-02-01'),
        sunsetDate: null,
        mappedEventTypes: ['bias_detected', 'disparate_impact', 'fairness_threshold_violation'],
        detectionLogic: {
          severityMapping: {
            bias_detected: 'high',
            disparate_impact: 'high',
            fairness_threshold_violation: 'high',
          },
        },
      },
      {
        state: 'CO',
        regulationName: 'Colorado AI Act',
        controlId: 'CO-AI-2',
        controlName: 'Consumer Notice Requirements',
        description: 'Consumers must be notified when consequential decisions are made by AI.',
        requiresReporting: false,
        reportingDeadlineDays: null,
        effectiveDate: new Date('2024-02-01'),
        sunsetDate: null,
        mappedEventTypes: ['explainability_failure'],
        detectionLogic: {
          severityMapping: {
            explainability_failure: 'medium',
          },
        },
      },
      {
        state: 'NY',
        regulationName: 'NYC Local Law 144',
        controlId: 'NYC-LL144-1',
        controlName: 'Bias Audit Requirement',
        description: 'Employment automated decision tools must undergo annual bias audits.',
        requiresReporting: true,
        reportingDeadlineDays: 90,
        effectiveDate: new Date('2023-07-05'),
        sunsetDate: null,
        mappedEventTypes: ['bias_detected', 'disparate_impact', 'fairness_threshold_violation', 'explainability_failure'],
        detectionLogic: {
          severityMapping: {
            bias_detected: 'high',
            disparate_impact: 'high',
            fairness_threshold_violation: 'high',
            explainability_failure: 'high',
          },
        },
      },
      {
        state: 'NY',
        regulationName: 'NYC Local Law 144',
        controlId: 'NYC-LL144-2',
        controlName: 'Candidate Notice Requirement',
        description: 'Employers must notify candidates when using automated employment decision tools.',
        requiresReporting: false,
        reportingDeadlineDays: null,
        effectiveDate: new Date('2023-07-05'),
        sunsetDate: null,
        mappedEventTypes: ['explainability_failure'],
        detectionLogic: {
          severityMapping: {
            explainability_failure: 'medium',
          },
        },
      },
    ];

    for (const reg of regulations) {
      await db.insert(stateRegulations).values(reg);
    }

    logger.info("‚úÖ State regulations seeded successfully", {
      count: regulations.length,
      states: ['CA', 'CO', 'NY'],
    });
  }
}

export const stateLawEngine = new StateLawEngine();

server/services/network-metrics-calculat.content:
/**
 * üìà NETWORK METRICS CALCULATOR - Phase 2 Network Effects
 * 
 * Calculates and tracks network effects metrics daily
 * Measures the strength of the Spectral network over time
 */

import { db } from "../db";
import { 
  networkMetricsDailySnapshots,
  healthSystems,
  vendors,
  vendorAcceptances,
  spectralStandardAdoptions,
  complianceCertifications
} from "../../shared/schema";
import { eq, gte, lte, and, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface NetworkSnapshot {
  snapshotDate: Date;
  totalHealthSystems: number;
  activeHealthSystems: number;
  totalVendors: number;
  certifiedVendors: number;
  totalAcceptances: number;
  spectralStandardAdopters: number;
  networkDensity: number;
  averageAcceptanceRate: number;
  newHealthSystemsThisWeek: number;
  newVendorsThisWeek: number;
  newCertificationsThisWeek: number;
}

export class NetworkMetricsCalculator {
  /**
   * Calculate and store daily network metrics
   * Should be run once per day via background job
   */
  async calculateDailyMetrics(): Promise<NetworkSnapshot> {
    logger.info("Calculating daily network metrics");

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    // 1. Total health systems
    const allHealthSystems = await db.select().from(healthSystems);
    const totalHealthSystems = allHealthSystems.length;

    // 2. Active health systems (those with at least one vendor acceptance)
    const acceptances = await db.select().from(vendorAcceptances);
    const activeHealthSystemIds = new Set(acceptances.map(a => a.healthSystemId));
    const activeHealthSystems = activeHealthSystemIds.size;

    // 3. Total vendors
    const allVendors = await db.select().from(vendors);
    const totalVendors = allVendors.length;

    // 4. Certified vendors (those with approved certifications)
    const certifications = await db
      .select()
      .from(complianceCertifications)
      .where(eq(complianceCertifications.status, 'approved'));
    const certifiedVendorIds = new Set(certifications.map(c => c.vendorId));
    const certifiedVendors = certifiedVendorIds.size;

    // 5. Total acceptances (accepted status only)
    const activeAcceptances = acceptances.filter(a => a.status === 'accepted');
    const totalAcceptances = activeAcceptances.length;

    // 6. Spectral Standard adopters
    const adoptions = await db.select().from(spectralStandardAdoptions);
    const spectralStandardAdopters = new Set(adoptions.map(a => a.healthSystemId)).size;

    // 7. Network density: (actual connections / possible connections)
    // Possible connections = totalHealthSystems * totalVendors
    const possibleConnections = totalHealthSystems * totalVendors;
    const networkDensity = possibleConnections > 0 
      ? totalAcceptances / possibleConnections 
      : 0;

    // 8. Average acceptance rate per health system
    const acceptancesByHealthSystem = new Map<string, number>();
    activeAcceptances.forEach(a => {
      const count = acceptancesByHealthSystem.get(a.healthSystemId) || 0;
      acceptancesByHealthSystem.set(a.healthSystemId, count + 1);
    });
    const averageAcceptanceRate = activeHealthSystems > 0
      ? totalAcceptances / activeHealthSystems
      : 0;

    // 9. New health systems this week
    const oneWeekAgo = new Date(today);
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    const newHealthSystemsThisWeek = allHealthSystems.filter(
      hs => new Date(hs.createdAt) >= oneWeekAgo
    ).length;

    // 10. New vendors this week
    const newVendorsThisWeek = allVendors.filter(
      v => new Date(v.createdAt) >= oneWeekAgo
    ).length;

    // 11. New certifications this week
    const newCertificationsThisWeek = certifications.filter(
      c => new Date(c.createdAt) >= oneWeekAgo
    ).length;

    // Store snapshot
    await db.insert(networkMetricsDailySnapshots).values({
      snapshotDate: today,
      totalHealthSystems,
      activeHealthSystems,
      totalVendors,
      certifiedVendors,
      totalAcceptances,
      spectralStandardAdopters,
      networkDensity: networkDensity.toFixed(4),
      averageAcceptanceRate: averageAcceptanceRate.toFixed(2),
      newHealthSystemsThisWeek,
      newVendorsThisWeek,
      newCertificationsThisWeek,
    });

    logger.info({
      totalHealthSystems,
      activeHealthSystems,
      totalVendors,
      certifiedVendors,
      networkDensity: networkDensity.toFixed(4),
    }, "Daily network metrics calculated and stored");

    return {
      snapshotDate: today,
      totalHealthSystems,
      activeHealthSystems,
      totalVendors,
      certifiedVendors,
      totalAcceptances,
      spectralStandardAdopters,
      networkDensity,
      averageAcceptanceRate,
      newHealthSystemsThisWeek,
      newVendorsThisWeek,
      newCertificationsThisWeek,
    };
  }

  /**
   * Get network metrics for a specific date range
   */
  async getMetricsRange(startDate: Date, endDate: Date): Promise<NetworkSnapshot[]> {
    const snapshots = await db
      .select()
      .from(networkMetricsDailySnapshots)
      .where(
        and(
          gte(networkMetricsDailySnapshots.snapshotDate, startDate),
          lte(networkMetricsDailySnapshots.snapshotDate, endDate)
        )
      );

    return snapshots.map(s => ({
      snapshotDate: s.snapshotDate,
      totalHealthSystems: s.totalHealthSystems,
      activeHealthSystems: s.activeHealthSystems,
      totalVendors: s.totalVendors,
      certifiedVendors: s.certifiedVendors,
      totalAcceptances: s.totalAcceptances,
      spectralStandardAdopters: s.spectralStandardAdopters,
      networkDensity: parseFloat(s.networkDensity || '0'),
      averageAcceptanceRate: parseFloat(s.averageAcceptanceRate || '0'),
      newHealthSystemsThisWeek: s.newHealthSystemsThisWeek || 0,
      newVendorsThisWeek: s.newVendorsThisWeek || 0,
      newCertificationsThisWeek: s.newCertificationsThisWeek || 0,
    }));
  }

  /**
   * Get latest network snapshot
   */
  async getLatestSnapshot(): Promise<NetworkSnapshot | null> {
    const result = await db
      .select()
      .from(networkMetricsDailySnapshots)
      .orderBy(desc(networkMetricsDailySnapshots.snapshotDate))
      .limit(1);

    if (result.length === 0) {
      return null;
    }

    const s = result[0];
    return {
      snapshotDate: s.snapshotDate,
      totalHealthSystems: s.totalHealthSystems,
      activeHealthSystems: s.activeHealthSystems,
      totalVendors: s.totalVendors,
      certifiedVendors: s.certifiedVendors,
      totalAcceptances: s.totalAcceptances,
      spectralStandardAdopters: s.spectralStandardAdopters,
      networkDensity: parseFloat(s.networkDensity || '0'),
      averageAcceptanceRate: parseFloat(s.averageAcceptanceRate || '0'),
      newHealthSystemsThisWeek: s.newHealthSystemsThisWeek || 0,
      newVendorsThisWeek: s.newVendorsThisWeek || 0,
      newCertificationsThisWeek: s.newCertificationsThisWeek || 0,
    };
  }

  /**
   * Calculate network effects strength score (0-100)
   * Based on key metrics that indicate strong network effects
   */
  async calculateNetworkEffectsScore(): Promise<{
    score: number;
    breakdown: {
      densityScore: number;
      adoptionScore: number;
      growthScore: number;
      standardizationScore: number;
    };
  }> {
    const latest = await this.getLatestSnapshot();

    if (!latest) {
      return {
        score: 0,
        breakdown: {
          densityScore: 0,
          adoptionScore: 0,
          growthScore: 0,
          standardizationScore: 0,
        },
      };
    }

    // 1. Network Density Score (0-25 points)
    // Higher density = stronger network effects
    const densityScore = Math.min(latest.networkDensity * 100, 25);

    // 2. Adoption Score (0-25 points)
    // % of health systems actively using the platform
    const adoptionRate = latest.totalHealthSystems > 0
      ? latest.activeHealthSystems / latest.totalHealthSystems
      : 0;
    const adoptionScore = adoptionRate * 25;

    // 3. Growth Score (0-25 points)
    // Recent growth indicates momentum
    const growthRate = (
      latest.newHealthSystemsThisWeek +
      latest.newVendorsThisWeek +
      latest.newCertificationsThisWeek
    ) / 10; // Normalize
    const growthScore = Math.min(growthRate, 25);

    // 4. Standardization Score (0-25 points)
    // % of health systems that adopted the Spectral Standard
    const standardizationRate = latest.totalHealthSystems > 0
      ? latest.spectralStandardAdopters / latest.totalHealthSystems
      : 0;
    const standardizationScore = standardizationRate * 25;

    const totalScore = densityScore + adoptionScore + growthScore + standardizationScore;

    logger.info({
      score: totalScore.toFixed(1),
      densityScore: densityScore.toFixed(1),
      adoptionScore: adoptionScore.toFixed(1),
      growthScore: growthScore.toFixed(1),
      standardizationScore: standardizationScore.toFixed(1),
    }, "Network effects score calculated");

    return {
      score: Math.min(totalScore, 100),
      breakdown: {
        densityScore,
        adoptionScore,
        growthScore,
        standardizationScore,
      },
    };
  }
}

export const networkMetricsCalculator = new NetworkMetricsCalculator();

server/services/executive-summary-genera.content:
/**
 * üìä EXECUTIVE SUMMARY GENERATOR - Phase 3 Executive Reporting
 * 
 * AI-powered narrative generation for board-ready compliance reports
 * Transforms technical compliance data into executive-friendly summaries
 */

import { db } from "../db";
import {
  executiveReports,
  alerts,
  complianceCertifications,
  aiSystems,
  healthSystems,
  vendors,
} from "../../shared/schema";
import { eq, and, gte, lte, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface ExecutiveReportRequest {
  healthSystemId: string;
  reportType: 'board_summary' | 'quarterly_compliance' | 'risk_overview' | 'monthly_snapshot';
  reportPeriod: string; // e.g., 'Q1 2025', 'January 2025'
  startDate: Date;
  endDate: Date;
  generatedBy: string;
}

export interface ExecutiveReportData {
  id: string;
  reportType: string;
  reportTitle: string;
  reportPeriod: string;
  startDate: Date;
  endDate: Date;
  narrative: string;
  keyMetrics: {
    totalAISystems: number;
    activeAlerts: number;
    criticalAlerts: number;
    complianceRate: number;
    averageRiskScore: number;
    certifiedVendors: number;
  };
  riskSummary: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };
  complianceStatus: {
    HIPAA: { compliant: number; total: number; percentage: number };
    'NIST AI RMF': { compliant: number; total: number; percentage: number };
    'FDA SaMD': { compliant: number; total: number; percentage: number };
  };
  actionItems: Array<{
    priority: 'critical' | 'high' | 'medium';
    item: string;
    deadline?: string;
    owner?: string;
  }>;
  trendAnalysis: {
    alertTrend: 'increasing' | 'decreasing' | 'stable';
    riskTrend: 'improving' | 'worsening' | 'stable';
    complianceTrend: 'improving' | 'declining' | 'stable';
  };
  status: string;
  createdAt: Date;
}

export class ExecutiveSummaryGenerator {
  /**
   * Generate comprehensive executive report
   */
  async generateReport(request: ExecutiveReportRequest): Promise<ExecutiveReportData> {
    logger.info({
      healthSystemId: request.healthSystemId,
      reportType: request.reportType,
      reportPeriod: request.reportPeriod,
    }, "Generating executive report");

    // 1. Collect raw data
    const healthSystem = await db
      .select()
      .from(healthSystems)
      .where(eq(healthSystems.id, request.healthSystemId))
      .limit(1);

    if (healthSystem.length === 0) {
      throw new Error("Health system not found");
    }

    // 2. Get all AI systems for this health system
    const allSystems = await db
      .select()
      .from(aiSystems)
      .where(eq(aiSystems.healthSystemId, request.healthSystemId));

    // 3. Get alerts in period
    const periodAlerts = await db
      .select()
      .from(alerts)
      .where(
        and(
          eq(alerts.healthSystemId, request.healthSystemId),
          gte(alerts.createdAt, request.startDate),
          lte(alerts.createdAt, request.endDate)
        )
      );

    // 4. Get active alerts (not resolved)
    const activeAlerts = periodAlerts.filter(a => a.status !== 'resolved');
    const criticalAlerts = activeAlerts.filter(a => a.severity === 'critical');

    // 5. Calculate risk summary
    const riskSummary = {
      critical: periodAlerts.filter(a => a.severity === 'critical').length,
      high: periodAlerts.filter(a => a.severity === 'high').length,
      medium: periodAlerts.filter(a => a.severity === 'medium').length,
      low: periodAlerts.filter(a => a.severity === 'low').length,
    };

    // 6. Calculate compliance status
    const certifications = await db
      .select()
      .from(complianceCertifications);

    const complianceStatus = {
      HIPAA: this.calculateFrameworkCompliance('HIPAA', allSystems, certifications),
      'NIST AI RMF': this.calculateFrameworkCompliance('NIST AI RMF', allSystems, certifications),
      'FDA SaMD': this.calculateFrameworkCompliance('FDA SaMD', allSystems, certifications),
    };

    // 7. Calculate average risk score
    const systemsWithRisk = allSystems.filter(s => s.riskScore !== null);
    const averageRiskScore = systemsWithRisk.length > 0
      ? systemsWithRisk.reduce((sum, s) => sum + (parseFloat(s.riskScore!) || 0), 0) / systemsWithRisk.length
      : 0;

    // 8. Get certified vendors count
    const certifiedVendors = await db
      .select()
      .from(vendors)
      .where(eq(vendors.certificationStatus, 'certified'));

    // 9. Generate key metrics
    const keyMetrics = {
      totalAISystems: allSystems.length,
      activeAlerts: activeAlerts.length,
      criticalAlerts: criticalAlerts.length,
      complianceRate: this.calculateOverallComplianceRate(complianceStatus),
      averageRiskScore: Math.round(averageRiskScore),
      certifiedVendors: certifiedVendors.length,
    };

    // 10. Generate action items
    const actionItems = this.generateActionItems(criticalAlerts, allSystems, complianceStatus);

    // 11. Calculate trend analysis
    const trendAnalysis = await this.calculateTrends(
      request.healthSystemId,
      request.startDate,
      request.endDate
    );

    // 12. Generate AI-powered narrative
    const narrative = this.generateNarrative(
      healthSystem[0].name,
      request.reportPeriod,
      keyMetrics,
      riskSummary,
      complianceStatus,
      trendAnalysis
    );

    // 13. Create report title
    const reportTitle = this.generateReportTitle(request.reportType, request.reportPeriod);

    // 14. Store in database
    const created = await db
      .insert(executiveReports)
      .values({
        healthSystemId: request.healthSystemId,
        reportType: request.reportType,
        reportTitle,
        reportPeriod: request.reportPeriod,
        startDate: request.startDate,
        endDate: request.endDate,
        narrative,
        keyMetrics: JSON.stringify(keyMetrics),
        riskSummary: JSON.stringify(riskSummary),
        complianceStatus: JSON.stringify(complianceStatus),
        actionItems: JSON.stringify(actionItems),
        trendAnalysis: JSON.stringify(trendAnalysis),
        generatedBy: request.generatedBy,
        status: 'draft',
      })
      .returning();

    logger.info({
      reportId: created[0].id,
      reportType: request.reportType,
      keyMetrics,
    }, "Executive report generated");

    return {
      id: created[0].id,
      reportType: created[0].reportType,
      reportTitle: created[0].reportTitle!,
      reportPeriod: created[0].reportPeriod!,
      startDate: created[0].startDate,
      endDate: created[0].endDate,
      narrative: created[0].narrative!,
      keyMetrics,
      riskSummary,
      complianceStatus,
      actionItems,
      trendAnalysis,
      status: created[0].status,
      createdAt: created[0].createdAt,
    };
  }

  /**
   * Calculate compliance for a specific framework
   */
  private calculateFrameworkCompliance(
    framework: string,
    systems: any[],
    certifications: any[]
  ): { compliant: number; total: number; percentage: number } {
    const total = systems.length;

    // Count systems with approved certifications for this framework
    const systemsWithCerts = new Set(
      certifications
        .filter(c => c.framework === framework && c.status === 'approved')
        .map(c => c.aiSystemId)
    );

    const compliant = systems.filter(s => systemsWithCerts.has(s.id)).length;
    const percentage = total > 0 ? Math.round((compliant / total) * 100) : 0;

    return { compliant, total, percentage };
  }

  /**
   * Calculate overall compliance rate across all frameworks
   */
  private calculateOverallComplianceRate(complianceStatus: any): number {
    const frameworks = ['HIPAA', 'NIST AI RMF', 'FDA SaMD'];
    const total = frameworks.reduce((sum, f) => sum + complianceStatus[f].total, 0);
    const compliant = frameworks.reduce((sum, f) => sum + complianceStatus[f].compliant, 0);

    return total > 0 ? Math.round((compliant / total) * 100) : 0;
  }

  /**
   * Generate action items based on current state
   */
  private generateActionItems(
    criticalAlerts: any[],
    systems: any[],
    complianceStatus: any
  ): Array<{ priority: string; item: string; deadline?: string; owner?: string }> {
    const items: Array<{ priority: string; item: string; deadline?: string }> = [];

    // Critical alerts require immediate action
    if (criticalAlerts.length > 0) {
      items.push({
        priority: 'critical',
        item: `Address ${criticalAlerts.length} critical compliance alert${criticalAlerts.length > 1 ? 's' : ''}`,
        deadline: '7 days',
      });
    }

    // Low compliance frameworks need attention
    Object.entries(complianceStatus).forEach(([framework, status]: [string, any]) => {
      if (status.percentage < 70 && status.total > 0) {
        items.push({
          priority: 'high',
          item: `Improve ${framework} compliance from ${status.percentage}% to target 90%`,
          deadline: '30 days',
        });
      }
    });

    // High-risk systems
    const highRiskSystems = systems.filter(s => 
      s.riskScore && parseFloat(s.riskScore) >= 80
    );

    if (highRiskSystems.length > 0) {
      items.push({
        priority: 'high',
        item: `Review and mitigate ${highRiskSystems.length} high-risk AI system${highRiskSystems.length > 1 ? 's' : ''}`,
        deadline: '14 days',
      });
    }

    return items.slice(0, 5); // Top 5 action items
  }

  /**
   * Calculate trend analysis
   */
  private async calculateTrends(
    healthSystemId: string,
    startDate: Date,
    endDate: Date
  ): Promise<{
    alertTrend: 'increasing' | 'decreasing' | 'stable';
    riskTrend: 'improving' | 'worsening' | 'stable';
    complianceTrend: 'improving' | 'declining' | 'stable';
  }> {
    // Get previous period for comparison
    const periodLength = endDate.getTime() - startDate.getTime();
    const prevStartDate = new Date(startDate.getTime() - periodLength);
    const prevEndDate = new Date(startDate.getTime());

    // Current period alerts
    const currentAlerts = await db
      .select()
      .from(alerts)
      .where(
        and(
          eq(alerts.healthSystemId, healthSystemId),
          gte(alerts.createdAt, startDate),
          lte(alerts.createdAt, endDate)
        )
      );

    // Previous period alerts
    const previousAlerts = await db
      .select()
      .from(alerts)
      .where(
        and(
          eq(alerts.healthSystemId, healthSystemId),
          gte(alerts.createdAt, prevStartDate),
          lte(alerts.createdAt, prevEndDate)
        )
      );

    // Calculate trends
    const alertTrend = this.determineTrend(
      currentAlerts.length,
      previousAlerts.length
    ) as 'increasing' | 'decreasing' | 'stable';

    // Risk trend (fewer critical alerts = improving)
    const currentCritical = currentAlerts.filter(a => a.severity === 'critical').length;
    const previousCritical = previousAlerts.filter(a => a.severity === 'critical').length;

    const riskTrend = this.determineTrend(
      previousCritical,
      currentCritical
    ) as 'improving' | 'worsening' | 'stable';

    // Compliance trend (based on resolution rate)
    const currentResolved = currentAlerts.filter(a => a.status === 'resolved').length;
    const currentResolveRate = currentAlerts.length > 0
      ? currentResolved / currentAlerts.length
      : 0;

    const previousResolved = previousAlerts.filter(a => a.status === 'resolved').length;
    const previousResolveRate = previousAlerts.length > 0
      ? previousResolved / previousAlerts.length
      : 0;

    const complianceTrend = this.determineTrend(
      currentResolveRate,
      previousResolveRate
    ) as 'improving' | 'declining' | 'stable';

    return { alertTrend, riskTrend, complianceTrend };
  }

  /**
   * Determine trend direction
   */
  private determineTrend(current: number, previous: number): string {
    if (previous === 0) return 'stable';

    const change = ((current - previous) / previous) * 100;

    if (change > 10) return 'increasing';
    if (change < -10) return 'decreasing';
    return 'stable';
  }

  /**
   * Generate executive narrative using template-based approach
   * (In production, this would use an LLM with PHI-safe configuration)
   */
  private generateNarrative(
    healthSystemName: string,
    reportPeriod: string,
    keyMetrics: any,
    riskSummary: any,
    complianceStatus: any,
    trendAnalysis: any
  ): string {
    const sections: string[] = [];

    // Opening summary
    sections.push(
      `**AI Governance Summary for ${healthSystemName} - ${reportPeriod}**\n\n` +
      `This report provides a comprehensive overview of AI system compliance, risk management, and governance activities for ${reportPeriod}.`
    );

    // Key findings
    sections.push(
      `\n**Key Findings:**\n` +
      `‚Ä¢ Currently managing ${keyMetrics.totalAISystems} AI systems across the organization\n` +
      `‚Ä¢ ${keyMetrics.activeAlerts} active compliance alerts requiring attention\n` +
      `‚Ä¢ Overall compliance rate: ${keyMetrics.complianceRate}%\n` +
      `‚Ä¢ Average risk score: ${keyMetrics.averageRiskScore}/100\n` +
      `‚Ä¢ Working with ${keyMetrics.certifiedVendors} certified AI vendors`
    );

    // Risk overview
    const totalRisk = riskSummary.critical + riskSummary.high + riskSummary.medium + riskSummary.low;
    sections.push(
      `\n**Risk Profile:**\n` +
      `During this period, the system detected ${totalRisk} total risk events:\n` +
      `‚Ä¢ Critical: ${riskSummary.critical}\n` +
      `‚Ä¢ High: ${riskSummary.high}\n` +
      `‚Ä¢ Medium: ${riskSummary.medium}\n` +
      `‚Ä¢ Low: ${riskSummary.low}`
    );

    if (riskSummary.critical > 0) {
      sections.push(
        `\n‚ö†Ô∏è **Immediate Action Required:** ${riskSummary.critical} critical alerts demand urgent executive attention.`
      );
    }

    // Compliance status
    sections.push(
      `\n**Regulatory Compliance Status:**\n` +
      `‚Ä¢ HIPAA: ${complianceStatus.HIPAA.percentage}% (${complianceStatus.HIPAA.compliant}/${complianceStatus.HIPAA.total} systems)\n` +
      `‚Ä¢ NIST AI RMF: ${complianceStatus['NIST AI RMF'].percentage}% (${complianceStatus['NIST AI RMF'].compliant}/${complianceStatus['NIST AI RMF'].total} systems)\n` +
      `‚Ä¢ FDA SaMD: ${complianceStatus['FDA SaMD'].percentage}% (${complianceStatus['FDA SaMD'].compliant}/${complianceStatus['FDA SaMD'].total} systems)`
    );

    // Trend analysis
    const trendEmoji = {
      improving: 'üìà',
      worsening: 'üìâ',
      declining: 'üìâ',
      stable: '‚Üí',
      increasing: 'üìà',
      decreasing: 'üìâ',
    };

    sections.push(
      `\n**Trend Analysis:**\n` +
      `‚Ä¢ Alert Volume: ${trendEmoji[trendAnalysis.alertTrend]} ${trendAnalysis.alertTrend}\n` +
      `‚Ä¢ Risk Posture: ${trendEmoji[trendAnalysis.riskTrend]} ${trendAnalysis.riskTrend}\n` +
      `‚Ä¢ Compliance: ${trendEmoji[trendAnalysis.complianceTrend]} ${trendAnalysis.complianceTrend}`
    );

    // Closing recommendation
    sections.push(
      `\n**Executive Recommendation:**\n` +
      this.generateRecommendation(keyMetrics, riskSummary, complianceStatus)
    );

    return sections.join('\n');
  }

  /**
   * Generate recommendation based on current state
   */
  private generateRecommendation(keyMetrics: any, riskSummary: any, complianceStatus: any): string {
    if (riskSummary.critical > 0) {
      return `Immediate action is required to address ${riskSummary.critical} critical compliance issues. Recommend emergency review with legal and compliance teams within 48 hours.`;
    }

    if (keyMetrics.complianceRate < 70) {
      return `Compliance rate of ${keyMetrics.complianceRate}% is below industry standards. Recommend investing in vendor certification program and compliance automation.`;
    }

    if (keyMetrics.averageRiskScore >= 70) {
      return `Elevated average risk score (${keyMetrics.averageRiskScore}/100) suggests need for enhanced monitoring and risk mitigation strategies.`;
    }

    return `AI governance posture is strong. Continue current monitoring practices and maintain focus on proactive compliance management.`;
  }

  /**
   * Generate report title
   */
  private generateReportTitle(reportType: string, reportPeriod: string): string {
    const titles = {
      board_summary: `Board AI Governance Summary - ${reportPeriod}`,
      quarterly_compliance: `Quarterly Compliance Report - ${reportPeriod}`,
      risk_overview: `AI Risk Overview - ${reportPeriod}`,
      monthly_snapshot: `Monthly AI Governance Snapshot - ${reportPeriod}`,
    };

    return titles[reportType as keyof typeof titles] || `Executive Report - ${reportPeriod}`;
  }

  /**
   * Get latest executive report
   */
  async getLatestReport(healthSystemId: string): Promise<ExecutiveReportData | null> {
    const result = await db
      .select()
      .from(executiveReports)
      .where(eq(executiveReports.healthSystemId, healthSystemId))
      .orderBy(desc(executiveReports.createdAt))
      .limit(1);

    if (result.length === 0) {
      return null;
    }

    const report = result[0];
    return {
      id: report.id,
      reportType: report.reportType,
      reportTitle: report.reportTitle!,
      reportPeriod: report.reportPeriod!,
      startDate: report.startDate,
      endDate: report.endDate,
      narrative: report.narrative!,
      keyMetrics: JSON.parse(JSON.stringify(report.keyMetrics)),
      riskSummary: JSON.parse(JSON.stringify(report.riskSummary)),
      complianceStatus: JSON.parse(JSON.stringify(report.complianceStatus)),
      actionItems: JSON.parse(JSON.stringify(report.actionItems)),
      trendAnalysis: JSON.parse(JSON.stringify(report.trendAnalysis)),
      status: report.status,
      createdAt: report.createdAt,
    };
  }

  /**
   * Get all reports for a health system
   */
  async getAllReports(healthSystemId: string): Promise<ExecutiveReportData[]> {
    const results = await db
      .select()
      .from(executiveReports)
      .where(eq(executiveReports.healthSystemId, healthSystemId))
      .orderBy(desc(executiveReports.createdAt));

    return results.map(report => ({
      id: report.id,
      reportType: report.reportType,
      reportTitle: report.reportTitle!,
      reportPeriod: report.reportPeriod!,
      startDate: report.startDate,
      endDate: report.endDate,
      narrative: report.narrative!,
      keyMetrics: JSON.parse(JSON.stringify(report.keyMetrics)),
      riskSummary: JSON.parse(JSON.stringify(report.riskSummary)),
      complianceStatus: JSON.parse(JSON.stringify(report.complianceStatus)),
      actionItems: JSON.parse(JSON.stringify(report.actionItems)),
      trendAnalysis: JSON.parse(JSON.stringify(report.trendAnalysis)),
      status: report.status,
      createdAt: report.createdAt,
    }));
  }

  /**
   * Publish report (mark as reviewed and published)
   */
  async publishReport(reportId: string, reviewedBy: string): Promise<void> {
    await db
      .update(executiveReports)
      .set({
        status: 'published',
        reviewedBy,
        reviewedAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(executiveReports.id, reportId));

    logger.info({ reportId, reviewedBy }, "Executive report published");
  }
}

export const executiveSummaryGenerator = new ExecutiveSummaryGenerator();

server/services/vendor-acceptance-workfl.content:
/**
 * üåê VENDOR ACCEPTANCE WORKFLOW - Phase 2 Network Effects
 * 
 * Handles health systems accepting/rejecting vendor certifications
 * Creates network effects through vendor adoption tracking
 */

import { db } from "../db";
import { 
  vendorAcceptances, 
  healthSystemVendorRelationships,
  complianceCertifications,
  vendors,
  healthSystems,
  users
} from "../../shared/schema";
import { eq, and, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface AcceptVendorRequest {
  healthSystemId: string;
  vendorId: string;
  certificationId?: string;
  acceptedBy: string; // User ID
  expirationDate?: Date;
  requiredInRFP?: boolean;
  notes?: string;
}

export interface RejectVendorRequest {
  healthSystemId: string;
  vendorId: string;
  certificationId?: string;
  rejectedBy: string; // User ID
  rejectionReason: string;
  notes?: string;
}

export interface VendorAcceptanceStatus {
  id: string;
  status: 'pending' | 'accepted' | 'rejected' | 'expired';
  vendor: {
    id: string;
    name: string;
    category: string | null;
  };
  certification?: {
    id: string;
    status: string;
    verifiedDate: Date | null;
  };
  acceptedDate?: Date;
  expirationDate?: Date;
  acceptedBy?: string;
  rejectionReason?: string;
  notes?: string;
}

export class VendorAcceptanceWorkflow {
  /**
   * Accept a vendor certification
   * Creates acceptance record and establishes vendor-health system relationship
   */
  async acceptVendor(request: AcceptVendorRequest): Promise<VendorAcceptanceStatus> {
    logger.info({
      healthSystemId: request.healthSystemId,
      vendorId: request.vendorId,
      certificationId: request.certificationId,
    }, "Processing vendor acceptance");

    // Check if certification exists and is approved
    if (request.certificationId) {
      const certification = await db
        .select()
        .from(complianceCertifications)
        .where(eq(complianceCertifications.id, request.certificationId))
        .limit(1);

      if (certification.length === 0) {
        throw new Error("Certification not found");
      }

      if (certification[0].status !== 'approved') {
        throw new Error("Cannot accept vendor with non-approved certification");
      }
    }

    // Check for existing acceptance
    const existing = await db
      .select()
      .from(vendorAcceptances)
      .where(
        and(
          eq(vendorAcceptances.healthSystemId, request.healthSystemId),
          eq(vendorAcceptances.vendorId, request.vendorId)
        )
      )
      .limit(1);

    let acceptanceId: string;

    if (existing.length > 0) {
      // Update existing acceptance
      const updated = await db
        .update(vendorAcceptances)
        .set({
          status: 'accepted',
          acceptedDate: new Date(),
          expirationDate: request.expirationDate,
          acceptedBy: request.acceptedBy,
          certificationId: request.certificationId,
          requiredInRFP: request.requiredInRFP || false,
          notes: request.notes,
          rejectionReason: null, // Clear any previous rejection
          updatedAt: new Date(),
        })
        .where(eq(vendorAcceptances.id, existing[0].id))
        .returning();

      acceptanceId = updated[0].id;
    } else {
      // Create new acceptance
      const created = await db
        .insert(vendorAcceptances)
        .values({
          healthSystemId: request.healthSystemId,
          vendorId: request.vendorId,
          certificationId: request.certificationId,
          status: 'accepted',
          acceptedDate: new Date(),
          expirationDate: request.expirationDate,
          acceptedBy: request.acceptedBy,
          requiredInRFP: request.requiredInRFP || false,
          notes: request.notes,
        })
        .returning();

      acceptanceId = created[0].id;
    }

    // Create or update vendor-health system relationship
    await this.upsertVendorRelationship(request.healthSystemId, request.vendorId);

    // Get full acceptance details
    const acceptance = await this.getAcceptanceStatus(acceptanceId);

    logger.info({
      acceptanceId,
      healthSystemId: request.healthSystemId,
      vendorId: request.vendorId,
    }, "Vendor acceptance completed");

    return acceptance;
  }

  /**
   * Reject a vendor certification
   */
  async rejectVendor(request: RejectVendorRequest): Promise<VendorAcceptanceStatus> {
    logger.info({
      healthSystemId: request.healthSystemId,
      vendorId: request.vendorId,
      reason: request.rejectionReason,
    }, "Processing vendor rejection");

    // Check for existing acceptance
    const existing = await db
      .select()
      .from(vendorAcceptances)
      .where(
        and(
          eq(vendorAcceptances.healthSystemId, request.healthSystemId),
          eq(vendorAcceptances.vendorId, request.vendorId)
        )
      )
      .limit(1);

    let acceptanceId: string;

    if (existing.length > 0) {
      // Update existing to rejected
      const updated = await db
        .update(vendorAcceptances)
        .set({
          status: 'rejected',
          rejectionReason: request.rejectionReason,
          notes: request.notes,
          acceptedDate: null, // Clear acceptance date
          acceptedBy: null,
          updatedAt: new Date(),
        })
        .where(eq(vendorAcceptances.id, existing[0].id))
        .returning();

      acceptanceId = updated[0].id;
    } else {
      // Create new rejection
      const created = await db
        .insert(vendorAcceptances)
        .values({
          healthSystemId: request.healthSystemId,
          vendorId: request.vendorId,
          certificationId: request.certificationId,
          status: 'rejected',
          rejectionReason: request.rejectionReason,
          notes: request.notes,
        })
        .returning();

      acceptanceId = created[0].id;
    }

    // Deactivate vendor-health system relationship when rejected
    await this.deactivateVendorRelationship(request.healthSystemId, request.vendorId);

    const acceptance = await this.getAcceptanceStatus(acceptanceId);

    logger.info({
      acceptanceId,
      healthSystemId: request.healthSystemId,
      vendorId: request.vendorId,
    }, "Vendor rejection completed");

    return acceptance;
  }

  /**
   * Get all vendor acceptances for a health system
   */
  async getHealthSystemAcceptances(healthSystemId: string): Promise<VendorAcceptanceStatus[]> {
    const acceptances = await db
      .select({
        acceptance: vendorAcceptances,
        vendor: vendors,
        certification: complianceCertifications,
      })
      .from(vendorAcceptances)
      .leftJoin(vendors, eq(vendorAcceptances.vendorId, vendors.id))
      .leftJoin(complianceCertifications, eq(vendorAcceptances.certificationId, complianceCertifications.id))
      .where(eq(vendorAcceptances.healthSystemId, healthSystemId))
      .orderBy(desc(vendorAcceptances.updatedAt));

    return acceptances.map(row => ({
      id: row.acceptance.id,
      status: row.acceptance.status as 'pending' | 'accepted' | 'rejected' | 'expired',
      vendor: {
        id: row.vendor?.id || '',
        name: row.vendor?.name || '',
        category: row.vendor?.category || null,
      },
      certification: row.certification ? {
        id: row.certification.id,
        status: row.certification.status,
        verifiedDate: row.certification.verifiedDate,
      } : undefined,
      acceptedDate: row.acceptance.acceptedDate || undefined,
      expirationDate: row.acceptance.expirationDate || undefined,
      acceptedBy: row.acceptance.acceptedBy || undefined,
      rejectionReason: row.acceptance.rejectionReason || undefined,
      notes: row.acceptance.notes || undefined,
    }));
  }

  /**
   * Get acceptance status by ID
   */
  private async getAcceptanceStatus(acceptanceId: string): Promise<VendorAcceptanceStatus> {
    const result = await db
      .select({
        acceptance: vendorAcceptances,
        vendor: vendors,
        certification: complianceCertifications,
      })
      .from(vendorAcceptances)
      .leftJoin(vendors, eq(vendorAcceptances.vendorId, vendors.id))
      .leftJoin(complianceCertifications, eq(vendorAcceptances.certificationId, complianceCertifications.id))
      .where(eq(vendorAcceptances.id, acceptanceId))
      .limit(1);

    if (result.length === 0) {
      throw new Error("Acceptance not found");
    }

    const row = result[0];
    return {
      id: row.acceptance.id,
      status: row.acceptance.status as 'pending' | 'accepted' | 'rejected' | 'expired',
      vendor: {
        id: row.vendor?.id || '',
        name: row.vendor?.name || '',
        category: row.vendor?.category || null,
      },
      certification: row.certification ? {
        id: row.certification.id,
        status: row.certification.status,
        verifiedDate: row.certification.verifiedDate,
      } : undefined,
      acceptedDate: row.acceptance.acceptedDate || undefined,
      expirationDate: row.acceptance.expirationDate || undefined,
      acceptedBy: row.acceptance.acceptedBy || undefined,
      rejectionReason: row.acceptance.rejectionReason || undefined,
      notes: row.acceptance.notes || undefined,
    };
  }

  /**
   * Create or update vendor-health system relationship
   */
  private async upsertVendorRelationship(
    healthSystemId: string,
    vendorId: string
  ): Promise<void> {
    const existing = await db
      .select()
      .from(healthSystemVendorRelationships)
      .where(
        and(
          eq(healthSystemVendorRelationships.healthSystemId, healthSystemId),
          eq(healthSystemVendorRelationships.vendorId, vendorId)
        )
      )
      .limit(1);

    if (existing.length > 0) {
      // Update existing relationship
      await db
        .update(healthSystemVendorRelationships)
        .set({
          spectralVerifiedRequired: true,
          endDate: null, // Clear any previous end date
          updatedAt: new Date(),
        })
        .where(eq(healthSystemVendorRelationships.id, existing[0].id));
    } else {
      // Create new relationship
      await db.insert(healthSystemVendorRelationships).values({
        healthSystemId,
        vendorId,
        relationshipType: 'certified_vendor',
        spectralVerifiedRequired: true,
        startDate: new Date(),
      });
    }
  }

  /**
   * Deactivate vendor-health system relationship when vendor is rejected
   */
  private async deactivateVendorRelationship(
    healthSystemId: string,
    vendorId: string
  ): Promise<void> {
    const existing = await db
      .select()
      .from(healthSystemVendorRelationships)
      .where(
        and(
          eq(healthSystemVendorRelationships.healthSystemId, healthSystemId),
          eq(healthSystemVendorRelationships.vendorId, vendorId)
        )
      )
      .limit(1);

    if (existing.length > 0) {
      // Set end date to mark relationship as inactive
      await db
        .update(healthSystemVendorRelationships)
        .set({
          endDate: new Date(),
          updatedAt: new Date(),
        })
        .where(eq(healthSystemVendorRelationships.id, existing[0].id));
    }
  }

  /**
   * Check if vendor is accepted by health system
   */
  async isVendorAccepted(healthSystemId: string, vendorId: string): Promise<boolean> {
    const result = await db
      .select()
      .from(vendorAcceptances)
      .where(
        and(
          eq(vendorAcceptances.healthSystemId, healthSystemId),
          eq(vendorAcceptances.vendorId, vendorId),
          eq(vendorAcceptances.status, 'accepted')
        )
      )
      .limit(1);

    return result.length > 0;
  }

  /**
   * Get network metrics - how many health systems have accepted this vendor
   */
  async getVendorNetworkMetrics(vendorId: string): Promise<{
    totalAcceptances: number;
    activeAcceptances: number;
    healthSystemsReached: number;
    acceptanceRate: number;
  }> {
    const allAcceptances = await db
      .select()
      .from(vendorAcceptances)
      .where(eq(vendorAcceptances.vendorId, vendorId));

    const activeAcceptances = allAcceptances.filter(a => a.status === 'accepted');
    const uniqueHealthSystems = new Set(allAcceptances.map(a => a.healthSystemId));

    // Calculate acceptance rate (accepted / total)
    const acceptanceRate = allAcceptances.length > 0
      ? activeAcceptances.length / allAcceptances.length
      : 0;

    return {
      totalAcceptances: allAcceptances.length,
      activeAcceptances: activeAcceptances.length,
      healthSystemsReached: uniqueHealthSystems.size,
      acceptanceRate,
    };
  }
}

export const vendorAcceptanceWorkflow = new VendorAcceptanceWorkflow();

server/services/spectral-standard-tracke.content:
/**
 * üèÜ SPECTRAL STANDARD ADOPTION TRACKER - Phase 2 Network Effects
 * 
 * Tracks health systems adopting the Spectral Standard for AI procurement
 * Creates competitive moat through industry standardization
 */

import { db } from "../db";
import { spectralStandardAdoptions, healthSystems, vendorAcceptances } from "../../shared/schema";
import { eq, and, lte, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface SpectralStandardAdoption {
  healthSystemId: string;
  adoptionType: 'mandatory' | 'preferred' | 'pilot' | 'observing';
  scope: string; // 'all_ai_systems', 'high_risk_only', 'new_procurements'
  categories?: string[]; // Which AI categories require Spectral certification
  announcedDate: Date;
  effectiveDate: Date;
  publiclyAnnounced?: boolean;
  pressReleaseUrl?: string;
  notes?: string;
}

export interface AdoptionStatus {
  id: string;
  healthSystem: {
    id: string;
    name: string;
    state?: string | null;
  };
  adoptionType: string;
  scope: string;
  categories?: string[];
  announcedDate: Date;
  effectiveDate: Date;
  publiclyAnnounced: boolean;
  pressReleaseUrl?: string;
  daysActive: number;
  vendorsCompliant: number;
}

export class SpectralStandardTracker {
  /**
   * Record a health system adopting the Spectral Standard
   */
  async recordAdoption(adoption: SpectralStandardAdoption): Promise<AdoptionStatus> {
    logger.info({
      healthSystemId: adoption.healthSystemId,
      adoptionType: adoption.adoptionType,
      scope: adoption.scope,
    }, "Recording Spectral Standard adoption");

    // Check if health system exists
    const healthSystem = await db
      .select()
      .from(healthSystems)
      .where(eq(healthSystems.id, adoption.healthSystemId))
      .limit(1);

    if (healthSystem.length === 0) {
      throw new Error("Health system not found");
    }

    // Create adoption record
    const created = await db
      .insert(spectralStandardAdoptions)
      .values({
        healthSystemId: adoption.healthSystemId,
        adoptionType: adoption.adoptionType,
        scope: adoption.scope,
        categories: adoption.categories ? JSON.parse(JSON.stringify(adoption.categories)) : null,
        announcedDate: adoption.announcedDate,
        effectiveDate: adoption.effectiveDate,
        publiclyAnnounced: adoption.publiclyAnnounced || false,
        pressReleaseUrl: adoption.pressReleaseUrl,
        notes: adoption.notes,
      })
      .returning();

    logger.info({
      adoptionId: created[0].id,
      healthSystemId: adoption.healthSystemId,
      publiclyAnnounced: adoption.publiclyAnnounced,
    }, "Spectral Standard adoption recorded");

    // Get full adoption status
    return this.getAdoptionStatus(created[0].id);
  }

  /**
   * Get adoption status by ID
   */
  async getAdoptionStatus(adoptionId: string): Promise<AdoptionStatus> {
    const result = await db
      .select({
        adoption: spectralStandardAdoptions,
        healthSystem: healthSystems,
      })
      .from(spectralStandardAdoptions)
      .leftJoin(healthSystems, eq(spectralStandardAdoptions.healthSystemId, healthSystems.id))
      .where(eq(spectralStandardAdoptions.id, adoptionId))
      .limit(1);

    if (result.length === 0) {
      throw new Error("Adoption not found");
    }

    const row = result[0];

    // Calculate days active
    const daysActive = Math.floor(
      (new Date().getTime() - new Date(row.adoption.effectiveDate).getTime()) / 
      (1000 * 60 * 60 * 24)
    );

    // Count compliant vendors (vendors accepted by this health system)
    const compliantVendors = await db
      .select()
      .from(vendorAcceptances)
      .where(
        and(
          eq(vendorAcceptances.healthSystemId, row.adoption.healthSystemId),
          eq(vendorAcceptances.status, 'accepted')
        )
      );

    return {
      id: row.adoption.id,
      healthSystem: {
        id: row.healthSystem?.id || '',
        name: row.healthSystem?.name || '',
        state: row.healthSystem?.state || null,
      },
      adoptionType: row.adoption.adoptionType,
      scope: row.adoption.scope || '',
      categories: row.adoption.categories ? JSON.parse(JSON.stringify(row.adoption.categories)) : undefined,
      announcedDate: row.adoption.announcedDate,
      effectiveDate: row.adoption.effectiveDate,
      publiclyAnnounced: row.adoption.publiclyAnnounced || false,
      pressReleaseUrl: row.adoption.pressReleaseUrl || undefined,
      daysActive,
      vendorsCompliant: compliantVendors.length,
    };
  }

  /**
   * Get all Spectral Standard adoptions
   */
  async getAllAdoptions(): Promise<AdoptionStatus[]> {
    const results = await db
      .select({
        adoption: spectralStandardAdoptions,
        healthSystem: healthSystems,
      })
      .from(spectralStandardAdoptions)
      .leftJoin(healthSystems, eq(spectralStandardAdoptions.healthSystemId, healthSystems.id))
      .orderBy(desc(spectralStandardAdoptions.announcedDate));

    const adoptionStatuses: AdoptionStatus[] = [];

    for (const row of results) {
      const daysActive = Math.floor(
        (new Date().getTime() - new Date(row.adoption.effectiveDate).getTime()) / 
        (1000 * 60 * 60 * 24)
      );

      const compliantVendors = await db
        .select()
        .from(vendorAcceptances)
        .where(
          and(
            eq(vendorAcceptances.healthSystemId, row.adoption.healthSystemId),
            eq(vendorAcceptances.status, 'accepted')
          )
        );

      adoptionStatuses.push({
        id: row.adoption.id,
        healthSystem: {
          id: row.healthSystem?.id || '',
          name: row.healthSystem?.name || '',
          state: row.healthSystem?.state || null,
        },
        adoptionType: row.adoption.adoptionType,
        scope: row.adoption.scope || '',
        categories: row.adoption.categories ? JSON.parse(JSON.stringify(row.adoption.categories)) : undefined,
        announcedDate: row.adoption.announcedDate,
        effectiveDate: row.adoption.effectiveDate,
        publiclyAnnounced: row.adoption.publiclyAnnounced || false,
        pressReleaseUrl: row.adoption.pressReleaseUrl || undefined,
        daysActive,
        vendorsCompliant: compliantVendors.length,
      });
    }

    return adoptionStatuses;
  }

  /**
   * Get public adoptions (for marketing/press)
   */
  async getPublicAdoptions(): Promise<AdoptionStatus[]> {
    const all = await this.getAllAdoptions();
    return all.filter(a => a.publiclyAnnounced);
  }

  /**
   * Get adoption metrics for network effects analysis
   */
  async getAdoptionMetrics(): Promise<{
    totalAdopters: number;
    mandatoryAdopters: number;
    preferredAdopters: number;
    pilotAdopters: number;
    publicAnnouncements: number;
    averageDaysToAdoption: number;
    adoptionsByMonth: Record<string, number>;
    topAdopters: Array<{
      healthSystemName: string;
      adoptionType: string;
      vendorsCompliant: number;
    }>;
  }> {
    const all = await this.getAllAdoptions();

    const totalAdopters = all.length;
    const mandatoryAdopters = all.filter(a => a.adoptionType === 'mandatory').length;
    const preferredAdopters = all.filter(a => a.adoptionType === 'preferred').length;
    const pilotAdopters = all.filter(a => a.adoptionType === 'pilot').length;
    const publicAnnouncements = all.filter(a => a.publiclyAnnounced).length;

    // Calculate average days from announced to effective
    const daysToAdoption = all.map(a => {
      const diff = new Date(a.effectiveDate).getTime() - new Date(a.announcedDate).getTime();
      return Math.floor(diff / (1000 * 60 * 60 * 24));
    });
    const averageDaysToAdoption = daysToAdoption.length > 0
      ? daysToAdoption.reduce((a, b) => a + b, 0) / daysToAdoption.length
      : 0;

    // Group adoptions by month
    const adoptionsByMonth: Record<string, number> = {};
    all.forEach(a => {
      const month = new Date(a.announcedDate).toISOString().slice(0, 7); // YYYY-MM
      adoptionsByMonth[month] = (adoptionsByMonth[month] || 0) + 1;
    });

    // Top adopters by vendor compliance
    const topAdopters = all
      .sort((a, b) => b.vendorsCompliant - a.vendorsCompliant)
      .slice(0, 10)
      .map(a => ({
        healthSystemName: a.healthSystem.name,
        adoptionType: a.adoptionType,
        vendorsCompliant: a.vendorsCompliant,
      }));

    return {
      totalAdopters,
      mandatoryAdopters,
      preferredAdopters,
      pilotAdopters,
      publicAnnouncements,
      averageDaysToAdoption,
      adoptionsByMonth,
      topAdopters,
    };
  }

  /**
   * Check if a health system has adopted the Spectral Standard
   */
  async hasAdoptedStandard(healthSystemId: string): Promise<boolean> {
    const result = await db
      .select()
      .from(spectralStandardAdoptions)
      .where(
        and(
          eq(spectralStandardAdoptions.healthSystemId, healthSystemId),
          lte(spectralStandardAdoptions.effectiveDate, new Date())
        )
      )
      .limit(1);

    return result.length > 0;
  }

  /**
   * Get adoption type for a health system
   */
  async getAdoptionType(healthSystemId: string): Promise<string | null> {
    const result = await db
      .select()
      .from(spectralStandardAdoptions)
      .where(eq(spectralStandardAdoptions.healthSystemId, healthSystemId))
      .orderBy(desc(spectralStandardAdoptions.effectiveDate))
      .limit(1);

    return result.length > 0 ? result[0].adoptionType : null;
  }
}

export const spectralStandardTracker = new SpectralStandardTracker();

