/**
 * üì¶ AUDIT EVIDENCE PACKAGER - Phase 3 Executive Reporting
 * 
 * Automatically collects and packages audit evidence across all compliance controls
 * Generates completeness reports and downloadable evidence packages for auditors
 */

import { db } from "../db";
import {
  auditEvidencePackages,
  alerts,
  complianceCertifications,
  aiSystems,
  auditLogs,
  healthSystems,
} from "../../shared/schema";
import { eq, and, gte, lte, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface AuditEvidenceRequest {
  healthSystemId: string;
  framework: 'HIPAA' | 'Joint_Commission' | 'SOC2' | 'NIST_AI_RMF' | 'FDA_SaMD';
  packageType: 'annual_audit' | 'spot_check' | 'certification' | 'incident_investigation';
  auditPeriod: string;
  startDate: Date;
  endDate: Date;
  generatedBy: string;
}

export interface EvidenceItem {
  controlId: string;
  controlName: string;
  evidenceType: 'certification' | 'alert_log' | 'audit_log' | 'system_config' | 'test_result';
  description: string;
  timestamp: Date;
  sourceId: string;
  metadata?: any;
}

export interface AuditPackage {
  id: string;
  framework: string;
  packageType: string;
  auditPeriod: string;
  startDate: Date;
  endDate: Date;
  evidenceItems: EvidenceItem[];
  controlsCovered: string[];
  completenessScore: number;
  status: string;
  createdAt: Date;
}

export class AuditEvidencePackager {
  /**
   * Generate comprehensive audit evidence package
   */
  async generatePackage(request: AuditEvidenceRequest): Promise<AuditPackage> {
    logger.info({
      healthSystemId: request.healthSystemId,
      framework: request.framework,
      packageType: request.packageType,
    }, "Generating audit evidence package");

    // 1. Collect evidence from all sources
    const evidenceItems: EvidenceItem[] = [];

    // Evidence from certifications
    const certifications = await this.collectCertificationEvidence(
      request.healthSystemId,
      request.framework,
      request.startDate,
      request.endDate
    );
    evidenceItems.push(...certifications);

    // Evidence from alerts and compliance violations
    const alertEvidence = await this.collectAlertEvidence(
      request.healthSystemId,
      request.framework,
      request.startDate,
      request.endDate
    );
    evidenceItems.push(...alertEvidence);

    // Evidence from audit logs
    const auditEvidence = await this.collectAuditLogEvidence(
      request.healthSystemId,
      request.framework,
      request.startDate,
      request.endDate
    );
    evidenceItems.push(...auditEvidence);

    // Evidence from system configurations
    const configEvidence = await this.collectSystemConfigEvidence(
      request.healthSystemId,
      request.framework
    );
    evidenceItems.push(...configEvidence);

    // 2. Map evidence to controls
    const controlsCovered = this.extractCoveredControls(evidenceItems);

    // 3. Calculate completeness score
    const completenessScore = this.calculateCompletenessScore(
      request.framework,
      controlsCovered
    );

    // 4. Store package in database
    const created = await db
      .insert(auditEvidencePackages)
      .values({
        healthSystemId: request.healthSystemId,
        framework: request.framework,
        packageType: request.packageType,
        auditPeriod: request.auditPeriod,
        startDate: request.startDate,
        endDate: request.endDate,
        evidenceItems: JSON.stringify(evidenceItems),
        controlsCovered: JSON.stringify(controlsCovered),
        completenessScore: completenessScore.toString(),
        generatedBy: request.generatedBy,
        status: 'complete',
      })
      .returning();

    logger.info({
      packageId: created[0].id,
      framework: request.framework,
      evidenceCount: evidenceItems.length,
      completenessScore,
    }, "Audit evidence package generated");

    return {
      id: created[0].id,
      framework: created[0].framework,
      packageType: created[0].packageType,
      auditPeriod: created[0].auditPeriod!,
      startDate: created[0].startDate,
      endDate: created[0].endDate,
      evidenceItems,
      controlsCovered,
      completenessScore,
      status: created[0].status,
      createdAt: created[0].createdAt,
    };
  }

  /**
   * Collect certification evidence
   */
  private async collectCertificationEvidence(
    healthSystemId: string,
    framework: string,
    startDate: Date,
    endDate: Date
  ): Promise<EvidenceItem[]> {
    // Get all AI systems for this health system
    const systems = await db
      .select()
      .from(aiSystems)
      .where(eq(aiSystems.healthSystemId, healthSystemId));

    if (systems.length === 0) {
      return [];
    }

    const systemIds = systems.map(s => s.id);

    // Get certifications in period
    const certifications = await db
      .select()
      .from(complianceCertifications)
      .where(
        and(
          eq(complianceCertifications.framework, framework),
          gte(complianceCertifications.createdAt, startDate),
          lte(complianceCertifications.createdAt, endDate)
        )
      );

    // Filter to only certifications for this health system's AI systems
    const relevantCerts = certifications.filter(c => systemIds.includes(c.aiSystemId));

    return relevantCerts.map(cert => ({
      controlId: this.mapFrameworkToControlId(framework, 'certification'),
      controlName: `${framework} Certification`,
      evidenceType: 'certification' as const,
      description: `Certification ${cert.status} for AI system ${cert.aiSystemId}`,
      timestamp: cert.createdAt,
      sourceId: cert.id,
      metadata: {
        certificationId: cert.id,
        aiSystemId: cert.aiSystemId,
        status: cert.status,
        validUntil: cert.validUntil,
      },
    }));
  }

  /**
   * Collect alert evidence (compliance violations)
   */
  private async collectAlertEvidence(
    healthSystemId: string,
    framework: string,
    startDate: Date,
    endDate: Date
  ): Promise<EvidenceItem[]> {
    const alerts = await db
      .select()
      .from(alerts)
      .where(
        and(
          eq(alerts.healthSystemId, healthSystemId),
          gte(alerts.createdAt, startDate),
          lte(alerts.createdAt, endDate)
        )
      );

    // Filter alerts relevant to this framework
    const relevantAlerts = alerts.filter(alert =>
      this.isAlertRelevantToFramework(alert.type, framework)
    );

    return relevantAlerts.map(alert => ({
      controlId: this.mapAlertTypeToControlId(alert.type, framework),
      controlName: alert.type,
      evidenceType: 'alert_log' as const,
      description: alert.description || `${alert.type} detected - ${alert.severity} severity`,
      timestamp: alert.createdAt,
      sourceId: alert.id,
      metadata: {
        alertId: alert.id,
        severity: alert.severity,
        status: alert.status,
        resolvedAt: alert.resolvedAt,
        aiSystemId: alert.aiSystemId,
      },
    }));
  }

  /**
   * Collect audit log evidence
   */
  private async collectAuditLogEvidence(
    healthSystemId: string,
    framework: string,
    startDate: Date,
    endDate: Date
  ): Promise<EvidenceItem[]> {
    const logs = await db
      .select()
      .from(auditLogs)
      .where(
        and(
          eq(auditLogs.healthSystemId, healthSystemId),
          gte(auditLogs.createdAt, startDate),
          lte(auditLogs.createdAt, endDate)
        )
      )
      .limit(1000); // Limit to prevent overwhelming the package

    // Filter to compliance-relevant actions
    const relevantLogs = logs.filter(log =>
      this.isAuditLogRelevantToFramework(log.action, framework)
    );

    return relevantLogs.map(log => ({
      controlId: this.mapAuditActionToControlId(log.action, framework),
      controlName: log.action,
      evidenceType: 'audit_log' as const,
      description: `${log.action} by ${log.userId}: ${log.details ? JSON.stringify(log.details) : 'No details'}`,
      timestamp: log.createdAt,
      sourceId: log.id.toString(),
      metadata: {
        action: log.action,
        userId: log.userId,
        ipAddress: log.ipAddress,
        details: log.details,
      },
    }));
  }

  /**
   * Collect system configuration evidence
   */
  private async collectSystemConfigEvidence(
    healthSystemId: string,
    framework: string
  ): Promise<EvidenceItem[]> {
    const systems = await db
      .select()
      .from(aiSystems)
      .where(eq(aiSystems.healthSystemId, healthSystemId));

    return systems.map(system => ({
      controlId: this.mapFrameworkToControlId(framework, 'system_config'),
      controlName: `${framework} System Configuration`,
      evidenceType: 'system_config' as const,
      description: `AI System: ${system.name} - Status: ${system.status}, Risk: ${system.riskScore || 'Not assessed'}`,
      timestamp: system.updatedAt,
      sourceId: system.id,
      metadata: {
        systemId: system.id,
        systemName: system.name,
        category: system.category,
        status: system.status,
        riskScore: system.riskScore,
        deployments: system.deploymentCount,
      },
    }));
  }

  /**
   * Extract unique controls covered by evidence
   */
  private extractCoveredControls(evidenceItems: EvidenceItem[]): string[] {
    const controls = new Set(evidenceItems.map(item => item.controlId));
    return Array.from(controls).sort();
  }

  /**
   * Calculate completeness score based on framework requirements
   */
  private calculateCompletenessScore(
    framework: string,
    controlsCovered: string[]
  ): number {
    // Framework-specific required controls
    const requiredControls: Record<string, string[]> = {
      HIPAA: [
        '164.308(a)(1)(i)', // Security Management Process
        '164.308(a)(3)', // Workforce Security
        '164.308(a)(4)', // Information Access Management
        '164.308(a)(5)', // Security Awareness and Training
        '164.310(a)(1)', // Facility Access Controls
        '164.310(d)', // Device and Media Controls
        '164.312(a)(1)', // Access Control
        '164.312(c)(1)', // Integrity
        '164.312(d)', // Person or Entity Authentication
        '164.312(e)(1)', // Transmission Security
      ],
      NIST_AI_RMF: [
        'GOVERN-1.1', 'GOVERN-1.2',
        'MAP-1.1', 'MAP-2.1',
        'MEASURE-1.1', 'MEASURE-2.1',
        'MANAGE-1.1', 'MANAGE-2.1',
      ],
      FDA_SaMD: [
        'CV-1', // Clinical Validation
        'AV-1', // Analytical Validation
        'QMS-1', // Quality Management System
        'CYBER-1', // Cybersecurity
        'PM-1', // Post-Market Surveillance
      ],
      SOC2: [
        'CC1.1', // Control Environment
        'CC2.1', // Communication
        'CC3.1', // Risk Assessment
        'CC6.1', // Logical Access
        'CC7.1', // System Operations
      ],
      Joint_Commission: [
        'IM.02.01.01', // Information Management
        'IM.02.02.01', // Privacy and Security
        'PI.01.01.01', // Performance Improvement
      ],
    };

    const required = requiredControls[framework] || [];
    if (required.length === 0) return 100; // If no requirements defined, 100%

    const coveredCount = required.filter(ctrl =>
      controlsCovered.some(covered => covered.includes(ctrl))
    ).length;

    return Math.round((coveredCount / required.length) * 100);
  }

  /**
   * Map framework to generic control ID
   */
  private mapFrameworkToControlId(framework: string, type: string): string {
    const mapping: Record<string, Record<string, string>> = {
      HIPAA: {
        certification: '164.312(a)(1)',
        system_config: '164.308(a)(1)(i)',
      },
      NIST_AI_RMF: {
        certification: 'GOVERN-1.1',
        system_config: 'GOVERN-1.2',
      },
      FDA_SaMD: {
        certification: 'QMS-1',
        system_config: 'CV-1',
      },
    };

    return mapping[framework]?.[type] || `${framework}-GENERAL`;
  }

  /**
   * Check if alert is relevant to framework
   */
  private isAlertRelevantToFramework(alertType: string, framework: string): boolean {
    const frameworkMapping: Record<string, string[]> = {
      HIPAA: ['phi_leakage', 'unauthorized_access', 'data_breach', 'security_incident'],
      NIST_AI_RMF: ['drift', 'bias', 'fairness', 'safety_violation', 'performance_degradation'],
      FDA_SaMD: ['clinical_accuracy', 'false_alerts', 'safety_violation', 'adverse_event'],
    };

    const relevantTypes = frameworkMapping[framework] || [];
    return relevantTypes.some(type => alertType.toLowerCase().includes(type));
  }

  /**
   * Map alert type to control ID
   */
  private mapAlertTypeToControlId(alertType: string, framework: string): string {
    const mapping: Record<string, Record<string, string>> = {
      HIPAA: {
        phi_leakage: '164.308(a)(4)',
        unauthorized_access: '164.312(a)(1)',
        data_breach: '164.308(a)(6)',
      },
      NIST_AI_RMF: {
        drift: 'MEASURE-2.1',
        bias: 'MEASURE-2.2',
        performance_degradation: 'MEASURE-1.1',
      },
      FDA_SaMD: {
        clinical_accuracy: 'CV-1',
        false_alerts: 'AV-1',
        safety_violation: 'PM-1',
      },
    };

    return mapping[framework]?.[alertType.toLowerCase()] || `${framework}-ALERT`;
  }

  /**
   * Check if audit log is relevant to framework
   */
  private isAuditLogRelevantToFramework(action: string, framework: string): boolean {
    const complianceActions = [
      'ai_system_created',
      'ai_system_updated',
      'ai_system_deleted',
      'certification_approved',
      'certification_rejected',
      'alert_created',
      'alert_resolved',
      'user_login',
      'user_logout',
      'access_granted',
      'access_revoked',
    ];

    return complianceActions.includes(action);
  }

  /**
   * Map audit action to control ID
   */
  private mapAuditActionToControlId(action: string, framework: string): string {
    const mapping: Record<string, Record<string, string>> = {
      HIPAA: {
        user_login: '164.312(d)',
        user_logout: '164.312(d)',
        access_granted: '164.308(a)(4)',
        access_revoked: '164.308(a)(4)',
      },
      NIST_AI_RMF: {
        ai_system_created: 'GOVERN-1.1',
        ai_system_updated: 'MANAGE-1.1',
        ai_system_deleted: 'MANAGE-2.1',
      },
    };

    return mapping[framework]?.[action] || `${framework}-AUDIT`;
  }

  /**
   * Get audit package by ID
   */
  async getPackage(packageId: string): Promise<AuditPackage | null> {
    const result = await db
      .select()
      .from(auditEvidencePackages)
      .where(eq(auditEvidencePackages.id, packageId))
      .limit(1);

    if (result.length === 0) {
      return null;
    }

    const pkg = result[0];
    return {
      id: pkg.id,
      framework: pkg.framework,
      packageType: pkg.packageType,
      auditPeriod: pkg.auditPeriod!,
      startDate: pkg.startDate,
      endDate: pkg.endDate,
      evidenceItems: JSON.parse(JSON.stringify(pkg.evidenceItems)),
      controlsCovered: JSON.parse(JSON.stringify(pkg.controlsCovered)),
      completenessScore: parseFloat(pkg.completenessScore || '0'),
      status: pkg.status,
      createdAt: pkg.createdAt,
    };
  }

  /**
   * Get all packages for a health system
   */
  async getAllPackages(healthSystemId: string): Promise<AuditPackage[]> {
    const results = await db
      .select()
      .from(auditEvidencePackages)
      .where(eq(auditEvidencePackages.healthSystemId, healthSystemId))
      .orderBy(desc(auditEvidencePackages.createdAt));

    return results.map(pkg => ({
      id: pkg.id,
      framework: pkg.framework,
      packageType: pkg.packageType,
      auditPeriod: pkg.auditPeriod!,
      startDate: pkg.startDate,
      endDate: pkg.endDate,
      evidenceItems: JSON.parse(JSON.stringify(pkg.evidenceItems)),
      controlsCovered: JSON.parse(JSON.stringify(pkg.controlsCovered)),
      completenessScore: parseFloat(pkg.completenessScore || '0'),
      status: pkg.status,
      createdAt: pkg.createdAt,
    }));
  }

  /**
   * Mark package as delivered to auditor
   */
  async deliverPackage(packageId: string, deliveredTo: string): Promise<void> {
    await db
      .update(auditEvidencePackages)
      .set({
        status: 'delivered',
        deliveredTo,
        deliveredAt: new Date(),
      })
      .where(eq(auditEvidencePackages.id, packageId));

    logger.info({ packageId, deliveredTo }, "Audit evidence package delivered");
  }
}

export const auditEvidencePackager = new AuditEvidencePackager();

server/services/policy-enforcement-engin.content:
/**
 * üîí POLICY ENFORCEMENT ENGINE - Phase 4 Business Model
 * 
 * Real-time governance policy evaluation and enforcement
 * Enables automated compliance policy management
 */

import { db } from "../db";
import {
  policyRules,
  policyEnforcementLogs,
  aiSystems,
  healthSystems,
  monitoringAlerts,
} from "../../shared/schema";
import { eq, and, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface PolicyRuleConfig {
  healthSystemId: string;
  policyName: string;
  policyType: 'approval_required' | 'prohibited' | 'restricted' | 'monitored';
  scope: 'all_ai' | 'department' | 'category' | 'vendor';
  scopeFilter?: {
    departments?: string[];
    categories?: string[];
    vendorIds?: string[];
    riskLevels?: string[];
  };
  conditions?: {
    minRiskScore?: number;
    maxRiskScore?: number;
    requiresCertification?: boolean;
    requiredFrameworks?: string[];
  };
  enforcementActions: {
    blockDeployment?: boolean;
    requireApproval?: boolean;
    sendAlert?: boolean;
    restrictAccess?: boolean;
    escalateToAdmin?: boolean;
  };
  approvalWorkflow?: {
    approvers: string[]; // User IDs
    requireAllApprovals?: boolean;
    escalationTimeout?: number; // Hours
  };
  createdBy: string;
}

export interface PolicyRule {
  id: string;
  healthSystemId: string;
  policyName: string;
  policyType: string;
  scope: string;
  scopeFilter?: any;
  conditions?: any;
  enforcementActions: any;
  approvalWorkflow?: any;
  active: boolean;
  createdBy: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface PolicyViolation {
  id: string;
  policyId: string;
  aiSystemId: string;
  violationType: string;
  actionTaken: string;
  details: any;
  resolvedBy?: string | null;
  resolvedAt?: Date | null;
  createdAt: Date;
}

export interface PolicyEvaluationResult {
  allowed: boolean;
  violations: Array<{
    policyId: string;
    policyName: string;
    violationType: string;
    severity: 'critical' | 'high' | 'medium' | 'low';
    actionRequired: string;
  }>;
  requiresApproval: boolean;
  approvers?: string[];
}

export class PolicyEnforcementEngine {
  /**
   * Create a new policy rule
   */
  async createPolicy(config: PolicyRuleConfig): Promise<PolicyRule> {
    logger.info({
      healthSystemId: config.healthSystemId,
      policyName: config.policyName,
      policyType: config.policyType,
    }, "Creating policy rule");

    const created = await db
      .insert(policyRules)
      .values({
        healthSystemId: config.healthSystemId,
        policyName: config.policyName,
        policyType: config.policyType,
        scope: config.scope,
        scopeFilter: config.scopeFilter ? JSON.stringify(config.scopeFilter) : null,
        conditions: config.conditions ? JSON.stringify(config.conditions) : null,
        enforcementActions: JSON.stringify(config.enforcementActions),
        approvalWorkflow: config.approvalWorkflow ? JSON.stringify(config.approvalWorkflow) : null,
        active: true,
        createdBy: config.createdBy,
      })
      .returning();

    logger.info({
      policyId: created[0].id,
      policyName: config.policyName,
    }, "Policy rule created");

    return this.formatPolicyRule(created[0]);
  }

  /**
   * Evaluate if an AI system complies with all active policies
   */
  async evaluateAISystem(
    aiSystemId: string,
    action: 'deployment' | 'modification' | 'access' | 'monitoring'
  ): Promise<PolicyEvaluationResult> {
    logger.info({
      aiSystemId,
      action,
    }, "Evaluating AI system against policies");

    // Get AI system details
    const aiSystem = await db
      .select()
      .from(aiSystems)
      .where(eq(aiSystems.id, aiSystemId))
      .limit(1);

    if (aiSystem.length === 0) {
      throw new Error("AI system not found");
    }

    const system = aiSystem[0];

    // Get all active policies for this health system
    const policies = await db
      .select()
      .from(policyRules)
      .where(
        and(
          eq(policyRules.healthSystemId, system.healthSystemId),
          eq(policyRules.active, true)
        )
      );

    const violations: PolicyEvaluationResult['violations'] = [];
    let requiresApproval = false;
    let approvers: string[] = [];

    // Evaluate each policy
    for (const policy of policies) {
      const applicable = this.isPolicyApplicable(system, policy);

      if (!applicable) {
        continue;
      }

      // Check conditions
      const conditionsRaw = policy.conditions;
      const conditions = typeof conditionsRaw === 'string'
        ? JSON.parse(conditionsRaw)
        : (conditionsRaw || {});

      const enforcementActionsRaw = policy.enforcementActions;
      const enforcementActions = typeof enforcementActionsRaw === 'string'
        ? JSON.parse(enforcementActionsRaw)
        : (enforcementActionsRaw || {});

      let violated = false;
      let violationType = '';

      // Check risk level (using predefined levels: low, medium, high, critical)
      const riskLevelValues: Record<string, number> = {
        'low': 1,
        'medium': 2,
        'high': 3,
        'critical': 4,
      };

      if (conditions.minRiskScore !== undefined) {
        const riskValue = riskLevelValues[system.riskLevel] || 0;
        if (riskValue < conditions.minRiskScore) {
          violated = true;
          violationType = 'risk_level_too_low';
        }
      }

      if (conditions.maxRiskScore !== undefined) {
        const riskValue = riskLevelValues[system.riskLevel] || 0;
        if (riskValue > conditions.maxRiskScore) {
          violated = true;
          violationType = 'risk_level_too_high';
        }
      }

      // Check certification requirement (vendor must have certifications)
      // Note: This is a simplified check - in production would query complianceCertifications table
      if (conditions.requiresCertification && !system.vendorId) {
        violated = true;
        violationType = 'certification_required';
      }

      // Check prohibited AI
      if (policy.policyType === 'prohibited') {
        violated = true;
        violationType = 'prohibited_ai_system';
      }

      // Handle violations
      if (violated || policy.policyType === 'approval_required') {
        const severity = this.calculateViolationSeverity(policy.policyType);

        if (policy.policyType === 'approval_required' || enforcementActions.requireApproval) {
          requiresApproval = true;

          // Get approvers from workflow
          const workflowRaw = policy.approvalWorkflow;
          const workflow = typeof workflowRaw === 'string'
            ? JSON.parse(workflowRaw)
            : (workflowRaw || {});

          if (workflow.approvers) {
            approvers = [...approvers, ...workflow.approvers];
          }
        }

        if (violated) {
          violations.push({
            policyId: policy.id,
            policyName: policy.policyName,
            violationType,
            severity,
            actionRequired: this.getRequiredAction(enforcementActions),
          });

          // Log violation
          await this.logViolation({
            policyId: policy.id,
            aiSystemId,
            violationType,
            actionTaken: this.getRequiredAction(enforcementActions),
            details: {
              policyName: policy.policyName,
              policyType: policy.policyType,
              systemName: system.name,
              action,
            },
          });

          // Execute enforcement actions
          await this.executeEnforcementActions(
            policy,
            system,
            enforcementActions,
            violationType
          );
        }
      }
    }

    const allowed = violations.length === 0 && !requiresApproval;

    logger.info({
      aiSystemId,
      allowed,
      violationsCount: violations.length,
      requiresApproval,
    }, "Policy evaluation complete");

    return {
      allowed,
      violations,
      requiresApproval,
      approvers: requiresApproval ? Array.from(new Set(approvers)) : undefined,
    };
  }

  /**
   * Check if a policy applies to a given AI system
   */
  private isPolicyApplicable(system: any, policy: any): boolean {
    // Check scope
    if (policy.scope === 'all_ai') {
      return true;
    }

    const scopeFilterRaw = policy.scopeFilter;
    const scopeFilter = typeof scopeFilterRaw === 'string'
      ? JSON.parse(scopeFilterRaw)
      : (scopeFilterRaw || {});

    // Department scope
    if (policy.scope === 'department' && scopeFilter.departments) {
      if (!scopeFilter.departments.includes(system.department)) {
        return false;
      }
    }

    // Category scope
    if (policy.scope === 'category' && scopeFilter.categories) {
      if (!scopeFilter.categories.includes(system.category)) {
        return false;
      }
    }

    // Vendor scope
    if (policy.scope === 'vendor' && scopeFilter.vendorIds) {
      if (!scopeFilter.vendorIds.includes(system.vendorId)) {
        return false;
      }
    }

    // Risk level scope
    if (scopeFilter.riskLevels) {
      if (!scopeFilter.riskLevels.includes(system.riskLevel)) {
        return false;
      }
    }

    return true;
  }

  /**
   * Calculate violation severity
   */
  private calculateViolationSeverity(policyType: string): 'critical' | 'high' | 'medium' | 'low' {
    switch (policyType) {
      case 'prohibited':
        return 'critical';
      case 'approval_required':
        return 'high';
      case 'restricted':
        return 'medium';
      case 'monitored':
        return 'low';
      default:
        return 'medium';
    }
  }

  /**
   * Get required action from enforcement actions
   */
  private getRequiredAction(enforcementActions: any): string {
    if (enforcementActions.blockDeployment) return 'deployment_blocked';
    if (enforcementActions.requireApproval) return 'approval_required';
    if (enforcementActions.restrictAccess) return 'access_restricted';
    if (enforcementActions.sendAlert) return 'alert_sent';
    if (enforcementActions.escalateToAdmin) return 'escalated_to_admin';
    return 'flagged_for_review';
  }

  /**
   * Execute enforcement actions
   */
  private async executeEnforcementActions(
    policy: any,
    system: any,
    enforcementActions: any,
    violationType: string
  ): Promise<void> {
    // Send alert if required
    if (enforcementActions.sendAlert || enforcementActions.escalateToAdmin) {
      await db.insert(monitoringAlerts).values({
        aiSystemId: system.id,
        type: 'policy_violation',
        severity: this.calculateViolationSeverity(policy.policyType),
        message: `Policy Violation: ${policy.policyName} - AI system "${system.name}" violated policy (${violationType})`,
        resolved: false,
      });

      logger.info({
        policyId: policy.id,
        aiSystemId: system.id,
        violationType,
      }, "Policy violation alert created");
    }

    // TODO: Implement other enforcement actions:
    // - blockDeployment: Update deployment status
    // - restrictAccess: Update access controls
    // - escalateToAdmin: Send notifications to admins
  }

  /**
   * Log policy violation
   */
  private async logViolation(violation: {
    policyId: string;
    aiSystemId: string;
    violationType: string;
    actionTaken: string;
    details: any;
  }): Promise<PolicyViolation> {
    const created = await db
      .insert(policyEnforcementLogs)
      .values({
        policyId: violation.policyId,
        aiSystemId: violation.aiSystemId,
        violationType: violation.violationType,
        actionTaken: violation.actionTaken,
        details: JSON.stringify(violation.details),
      })
      .returning();

    return {
      id: created[0].id,
      policyId: created[0].policyId,
      aiSystemId: created[0].aiSystemId,
      violationType: created[0].violationType,
      actionTaken: created[0].actionTaken,
      details: typeof created[0].details === 'string'
        ? JSON.parse(created[0].details)
        : created[0].details,
      resolvedBy: created[0].resolvedBy,
      resolvedAt: created[0].resolvedAt,
      createdAt: created[0].createdAt,
    };
  }

  /**
   * Get all policies for a health system
   */
  async getPolicies(healthSystemId: string): Promise<PolicyRule[]> {
    const results = await db
      .select()
      .from(policyRules)
      .where(eq(policyRules.healthSystemId, healthSystemId))
      .orderBy(desc(policyRules.createdAt));

    return results.map(p => this.formatPolicyRule(p));
  }

  /**
   * Get policy violations
   */
  async getViolations(
    healthSystemId: string,
    filters?: { resolved?: boolean; policyId?: string }
  ): Promise<PolicyViolation[]> {
    // Get AI systems for this health system
    const systems = await db
      .select()
      .from(aiSystems)
      .where(eq(aiSystems.healthSystemId, healthSystemId));

    const systemIds = systems.map(s => s.id);

    if (systemIds.length === 0) {
      return [];
    }

    // Get violations for these systems
    let query = db
      .select()
      .from(policyEnforcementLogs);

    // Apply filters
    const violations = await query.orderBy(desc(policyEnforcementLogs.createdAt));

    // Filter to this health system's AI systems only
    const filtered = violations.filter(v => systemIds.includes(v.aiSystemId));

    // Apply additional filters
    let result = filtered;

    if (filters?.resolved !== undefined) {
      result = result.filter(v => 
        filters.resolved ? v.resolvedAt !== null : v.resolvedAt === null
      );
    }

    if (filters?.policyId) {
      result = result.filter(v => v.policyId === filters.policyId);
    }

    return result.map(v => ({
      id: v.id,
      policyId: v.policyId,
      aiSystemId: v.aiSystemId,
      violationType: v.violationType,
      actionTaken: v.actionTaken,
      details: typeof v.details === 'string' ? JSON.parse(v.details) : v.details,
      resolvedBy: v.resolvedBy,
      resolvedAt: v.resolvedAt,
      createdAt: v.createdAt,
    }));
  }

  /**
   * Resolve a policy violation
   */
  async resolveViolation(violationId: string, resolvedBy: string): Promise<void> {
    await db
      .update(policyEnforcementLogs)
      .set({
        resolvedBy,
        resolvedAt: new Date(),
      })
      .where(eq(policyEnforcementLogs.id, violationId));

    logger.info({
      violationId,
      resolvedBy,
    }, "Policy violation resolved");
  }

  /**
   * Update policy (activate/deactivate)
   */
  async updatePolicy(
    policyId: string,
    updates: { active?: boolean; enforcementActions?: any }
  ): Promise<void> {
    const updateData: any = {
      updatedAt: new Date(),
    };

    if (updates.active !== undefined) {
      updateData.active = updates.active;
    }

    if (updates.enforcementActions) {
      updateData.enforcementActions = JSON.stringify(updates.enforcementActions);
    }

    await db
      .update(policyRules)
      .set(updateData)
      .where(eq(policyRules.id, policyId));

    logger.info({ policyId, updates }, "Policy updated");
  }

  /**
   * Delete policy
   */
  async deletePolicy(policyId: string): Promise<void> {
    await db
      .delete(policyRules)
      .where(eq(policyRules.id, policyId));

    logger.info({ policyId }, "Policy deleted");
  }

  /**
   * Format policy rule for output
   */
  private formatPolicyRule(policy: any): PolicyRule {
    return {
      id: policy.id,
      healthSystemId: policy.healthSystemId,
      policyName: policy.policyName,
      policyType: policy.policyType,
      scope: policy.scope,
      scopeFilter: typeof policy.scopeFilter === 'string'
        ? JSON.parse(policy.scopeFilter)
        : policy.scopeFilter,
      conditions: typeof policy.conditions === 'string'
        ? JSON.parse(policy.conditions)
        : policy.conditions,
      enforcementActions: typeof policy.enforcementActions === 'string'
        ? JSON.parse(policy.enforcementActions)
        : policy.enforcementActions,
      approvalWorkflow: typeof policy.approvalWorkflow === 'string'
        ? JSON.parse(policy.approvalWorkflow)
        : policy.approvalWorkflow,
      active: policy.active,
      createdBy: policy.createdBy,
      createdAt: policy.createdAt,
      updatedAt: policy.updatedAt,
    };
  }

  /**
   * Get policy enforcement statistics
   */
  async getPolicyStats(healthSystemId: string): Promise<{
    totalPolicies: number;
    activePolicies: number;
    totalViolations: number;
    unresolvedViolations: number;
    violationsByType: Record<string, number>;
  }> {
    const policies = await this.getPolicies(healthSystemId);
    const violations = await this.getViolations(healthSystemId);

    const violationsByType: Record<string, number> = {};
    violations.forEach(v => {
      violationsByType[v.violationType] = (violationsByType[v.violationType] || 0) + 1;
    });

    return {
      totalPolicies: policies.length,
      activePolicies: policies.filter(p => p.active).length,
      totalViolations: violations.length,
      unresolvedViolations: violations.filter(v => !v.resolvedAt).length,
      violationsByType,
    };
  }
}

export const policyEnforcementEngine = new PolicyEnforcementEngine();

