/**
 * üîç AI DISCOVERY CRAWLER - Phase 4 Business Model
 * 
 * Automated discovery of AI systems across healthcare infrastructure
 * Helps health systems build complete AI inventory
 */

import { db } from "../db";
import {
  aiDiscoveryJobs,
  aiSystems,
  healthSystems,
} from "../../shared/schema";
import { eq, desc } from "drizzle-orm";
import { logger } from "../logger";

export interface DiscoveryJobRequest {
  healthSystemId: string;
  discoveryType: 'ehr_scan' | 'vendor_survey' | 'api_crawler' | 'manual_import';
  dataSource?: string;
  createdBy: string;
}

export interface DiscoveryJob {
  id: string;
  healthSystemId: string;
  discoveryType: string;
  dataSource?: string | null;
  status: string;
  aiSystemsFound: number;
  aiSystemsNew: number;
  aiSystemsUpdated: number;
  results?: any;
  startedAt?: Date | null;
  completedAt?: Date | null;
  createdBy: string;
  createdAt: Date;
}

export interface DiscoveredAISystem {
  name: string;
  department: string;
  vendor?: string;
  category?: string;
  description?: string;
  discoverySource: string;
  confidence: number; // 0-1 score of how confident we are this is an AI system
}

export class AIDiscoveryCrawler {
  /**
   * Start a new discovery job
   */
  async startDiscovery(request: DiscoveryJobRequest): Promise<DiscoveryJob> {
    logger.info({
      healthSystemId: request.healthSystemId,
      discoveryType: request.discoveryType,
    }, "Starting AI discovery job");

    const created = await db
      .insert(aiDiscoveryJobs)
      .values({
        healthSystemId: request.healthSystemId,
        discoveryType: request.discoveryType,
        dataSource: request.dataSource,
        status: 'pending',
        aiSystemsFound: 0,
        aiSystemsNew: 0,
        aiSystemsUpdated: 0,
        createdBy: request.createdBy,
      })
      .returning();

    const job = this.formatJob(created[0]);

    // Execute discovery async (in production, would be background job)
    this.executeDiscovery(job.id).catch(error => {
      logger.error({
        jobId: job.id,
        error: error instanceof Error ? error.message : 'Unknown error',
      }, "Discovery job failed");
    });

    return job;
  }

  /**
   * Execute discovery job
   */
  private async executeDiscovery(jobId: string): Promise<void> {
    // Update status to running
    await db
      .update(aiDiscoveryJobs)
      .set({
        status: 'running',
        startedAt: new Date(),
      })
      .where(eq(aiDiscoveryJobs.id, jobId));

    try {
      const job = await db
        .select()
        .from(aiDiscoveryJobs)
        .where(eq(aiDiscoveryJobs.id, jobId))
        .limit(1);

      if (job.length === 0) {
        throw new Error("Job not found");
      }

      const discoveryJob = job[0];
      let discovered: DiscoveredAISystem[] = [];

      // Execute discovery based on type
      switch (discoveryJob.discoveryType) {
        case 'ehr_scan':
          discovered = await this.scanEHRIntegrations(discoveryJob.healthSystemId);
          break;
        case 'vendor_survey':
          discovered = await this.scanVendorSurveys(discoveryJob.healthSystemId);
          break;
        case 'api_crawler':
          discovered = await this.crawlAPIEndpoints(discoveryJob.dataSource || '');
          break;
        case 'manual_import':
          discovered = await this.processManualImport(discoveryJob.dataSource || '');
          break;
        default:
          logger.warn({ type: discoveryJob.discoveryType }, "Unknown discovery type");
      }

      // Filter high-confidence discoveries
      const highConfidence = discovered.filter(d => d.confidence >= 0.7);

      // Match against existing AI systems
      const existing = await db
        .select()
        .from(aiSystems)
        .where(eq(aiSystems.healthSystemId, discoveryJob.healthSystemId));

      const existingNames = new Set(existing.map(s => s.name.toLowerCase()));

      let newCount = 0;
      let updatedCount = 0;

      // Create or update AI systems
      for (const system of highConfidence) {
        if (existingNames.has(system.name.toLowerCase())) {
          // System already exists - could update metadata
          updatedCount++;
        } else {
          // New system - create it
          await db.insert(aiSystems).values({
            healthSystemId: discoveryJob.healthSystemId,
            name: system.name,
            department: system.department,
            riskLevel: 'medium', // Default risk level
            status: 'pending_review', // Requires human review
            integrationConfig: JSON.stringify({
              discoverySource: system.discoverySource,
              confidence: system.confidence,
              vendor: system.vendor,
              category: system.category,
            }),
          });
          newCount++;
        }
      }

      // Update job with results
      await db
        .update(aiDiscoveryJobs)
        .set({
          status: 'completed',
          aiSystemsFound: discovered.length,
          aiSystemsNew: newCount,
          aiSystemsUpdated: updatedCount,
          results: JSON.stringify({
            discovered: highConfidence.map(d => ({
              name: d.name,
              department: d.department,
              vendor: d.vendor,
              confidence: d.confidence,
            })),
          }),
          completedAt: new Date(),
        })
        .where(eq(aiDiscoveryJobs.id, jobId));

      logger.info({
        jobId,
        found: discovered.length,
        new: newCount,
        updated: updatedCount,
      }, "Discovery job completed successfully");
    } catch (error) {
      // Mark job as failed
      await db
        .update(aiDiscoveryJobs)
        .set({
          status: 'failed',
          completedAt: new Date(),
        })
        .where(eq(aiDiscoveryJobs.id, jobId));

      throw error;
    }
  }

  /**
   * Scan EHR integrations for AI systems
   * In production, would connect to Epic/Cerner/Athenahealth APIs
   */
  private async scanEHRIntegrations(healthSystemId: string): Promise<DiscoveredAISystem[]> {
    logger.info({ healthSystemId }, "Scanning EHR integrations");

    // Mock discovery - in production, would query EHR APIs
    const mockDiscovered: DiscoveredAISystem[] = [
      {
        name: "Epic Sepsis Prediction Model",
        department: "Emergency Department",
        vendor: "Epic Systems",
        category: "Clinical Decision Support",
        description: "Predictive model for early sepsis detection",
        discoverySource: "Epic FHIR API",
        confidence: 0.95,
      },
      {
        name: "Radiology AI Assistant",
        department: "Radiology",
        vendor: "Aidoc",
        category: "Medical Imaging",
        description: "AI-powered radiology triage and detection",
        discoverySource: "Epic App Orchard Registry",
        confidence: 0.9,
      },
      {
        name: "Clinical Documentation AI",
        department: "Hospitalist",
        vendor: "Nuance",
        category: "Administrative",
        description: "AI-powered clinical documentation improvement",
        discoverySource: "Epic Integration Catalog",
        confidence: 0.85,
      },
    ];

    return mockDiscovered;
  }

  /**
   * Scan vendor survey responses
   */
  private async scanVendorSurveys(healthSystemId: string): Promise<DiscoveredAISystem[]> {
    logger.info({ healthSystemId }, "Scanning vendor surveys");

    // Mock discovery - in production, would parse survey responses
    const mockDiscovered: DiscoveredAISystem[] = [
      {
        name: "Patient Flow Optimizer",
        department: "Operations",
        vendor: "LeanTaaS",
        category: "Operations Optimization",
        description: "AI-driven patient flow and capacity planning",
        discoverySource: "Vendor Survey Response",
        confidence: 0.8,
      },
      {
        name: "Revenue Cycle AI",
        department: "Finance",
        vendor: "Change Healthcare",
        category: "Revenue Cycle",
        description: "AI for claims processing and denial management",
        discoverySource: "Vendor Survey Response",
        confidence: 0.75,
      },
    ];

    return mockDiscovered;
  }

  /**
   * Crawl API endpoints for AI services
   */
  private async crawlAPIEndpoints(baseUrl: string): Promise<DiscoveredAISystem[]> {
    logger.info({ baseUrl }, "Crawling API endpoints");

    // Mock discovery - in production, would crawl API documentation
    const mockDiscovered: DiscoveredAISystem[] = [
      {
        name: "API AI Service (Detected)",
        department: "IT",
        category: "API Service",
        description: "AI service detected via API crawl",
        discoverySource: `API Crawler: ${baseUrl}`,
        confidence: 0.65,
      },
    ];

    return mockDiscovered;
  }

  /**
   * Process manual import (CSV/Excel)
   */
  private async processManualImport(importData: string): Promise<DiscoveredAISystem[]> {
    logger.info("Processing manual import");

    // Mock - in production, would parse CSV/Excel
    // For now, return empty array
    return [];
  }

  /**
   * Get all discovery jobs for a health system
   */
  async getJobs(healthSystemId: string): Promise<DiscoveryJob[]> {
    const results = await db
      .select()
      .from(aiDiscoveryJobs)
      .where(eq(aiDiscoveryJobs.healthSystemId, healthSystemId))
      .orderBy(desc(aiDiscoveryJobs.createdAt));

    return results.map(j => this.formatJob(j));
  }

  /**
   * Get job by ID
   */
  async getJob(jobId: string): Promise<DiscoveryJob | null> {
    const results = await db
      .select()
      .from(aiDiscoveryJobs)
      .where(eq(aiDiscoveryJobs.id, jobId))
      .limit(1);

    if (results.length === 0) {
      return null;
    }

    return this.formatJob(results[0]);
  }

  /**
   * Get discovery statistics
   */
  async getDiscoveryStats(healthSystemId: string): Promise<{
    totalJobs: number;
    completedJobs: number;
    totalSystemsFound: number;
    totalSystemsAdded: number;
    averageConfidence: number;
    bySource: Record<string, number>;
  }> {
    const jobs = await this.getJobs(healthSystemId);

    const completed = jobs.filter(j => j.status === 'completed');
    const totalSystemsFound = completed.reduce((sum, j) => sum + j.aiSystemsFound, 0);
    const totalSystemsAdded = completed.reduce((sum, j) => sum + j.aiSystemsNew, 0);

    const bySource: Record<string, number> = {};
    jobs.forEach(j => {
      bySource[j.discoveryType] = (bySource[j.discoveryType] || 0) + 1;
    });

    return {
      totalJobs: jobs.length,
      completedJobs: completed.length,
      totalSystemsFound,
      totalSystemsAdded,
      averageConfidence: 0.82, // Mock - would calculate from results
      bySource,
    };
  }

  /**
   * Format job for output
   */
  private formatJob(job: any): DiscoveryJob {
    return {
      id: job.id,
      healthSystemId: job.healthSystemId,
      discoveryType: job.discoveryType,
      dataSource: job.dataSource,
      status: job.status,
      aiSystemsFound: job.aiSystemsFound,
      aiSystemsNew: job.aiSystemsNew,
      aiSystemsUpdated: job.aiSystemsUpdated,
      results: typeof job.results === 'string' ? JSON.parse(job.results) : job.results,
      startedAt: job.startedAt,
      completedAt: job.completedAt,
      createdBy: job.createdBy,
      createdAt: job.createdAt,
    };
  }

  /**
   * Classify AI system category from description
   * ML-based classification would be used in production
   */
  private classifyAICategory(description: string): string {
    const desc = description.toLowerCase();

    if (desc.includes('clinical') || desc.includes('diagnosis')) return 'Clinical Decision Support';
    if (desc.includes('imaging') || desc.includes('radiology')) return 'Medical Imaging';
    if (desc.includes('sepsis') || desc.includes('risk score')) return 'Risk Prediction';
    if (desc.includes('revenue') || desc.includes('billing')) return 'Revenue Cycle';
    if (desc.includes('flow') || desc.includes('capacity')) return 'Operations Optimization';
    if (desc.includes('documentation')) return 'Administrative';

    return 'Other';
  }

  /**
   * Calculate confidence score for discovered system
   * In production, would use ML model
   */
  private calculateConfidence(source: string, metadata: any): number {
    // Higher confidence for official sources
    if (source.includes('Epic') || source.includes('Cerner')) return 0.9;
    if (source.includes('Vendor Survey')) return 0.8;
    if (source.includes('API')) return 0.7;

    return 0.6;
  }
}

export const aiDiscoveryCrawler = new AIDiscoveryCrawler();

server/services/compliance-control-versi.content:
import { db } from '../db';
import { complianceControlVersions, complianceControls } from '../../shared/schema';
import { eq, desc, and, isNull } from 'drizzle-orm';
import { logger } from '../logger';

/**
 * Compliance Control Versioning Service
 * 
 * Manages semantic versioning for compliance controls to track regulatory updates
 * and maintain audit trail of control changes over time.
 * 
 * Features:
 * - Semantic versioning (MAJOR.MINOR.PATCH)
 * - Change tracking with JSONB diff
 * - Effective/deprecated date management
 * - Version history and rollback support
 * - Automatic version bumping
 */

export interface ControlVersion {
  id: string;
  controlId: string;
  version: string;
  changes: any;
  effectiveDate: Date;
  deprecatedDate: Date | null;
  createdAt: Date;
}

export interface VersionChanges {
  added?: string[];
  removed?: string[];
  modified?: {
    field: string;
    oldValue?: any;
    newValue?: any;
  }[];
  reason?: string;
}

export class ComplianceControlVersioningService {
  /**
   * Create a new version of a compliance control
   */
  async createVersion(
    controlId: string,
    versionType: 'major' | 'minor' | 'patch',
    changes: VersionChanges,
    effectiveDate: Date = new Date()
  ): Promise<ControlVersion> {
    logger.info({ controlId, versionType }, 'Creating new control version');

    // Get current version
    const currentVersion = await this.getCurrentVersion(controlId);

    // For first initialization, start at 1.0.0 without bumping
    const newVersion = currentVersion 
      ? this.bumpVersion(currentVersion, versionType)
      : '1.0.0';

    // Deprecate previous version if exists
    if (currentVersion) {
      await this.deprecateVersion(controlId, currentVersion, effectiveDate);
    }

    // Create new version
    const [version] = await db
      .insert(complianceControlVersions)
      .values({
        controlId,
        version: newVersion,
        changes,
        effectiveDate,
      })
      .returning();

    logger.info({ controlId, version: newVersion }, 'Control version created');
    return version as ControlVersion;
  }

  /**
   * Get current (active) version of a control
   */
  async getCurrentVersion(controlId: string): Promise<string | null> {
    const [version] = await db
      .select({ version: complianceControlVersions.version })
      .from(complianceControlVersions)
      .where(
        and(
          eq(complianceControlVersions.controlId, controlId),
          isNull(complianceControlVersions.deprecatedDate)
        )
      )
      .orderBy(desc(complianceControlVersions.effectiveDate))
      .limit(1);

    return version?.version || null;
  }

  /**
   * Get full version history for a control
   */
  async getVersionHistory(controlId: string): Promise<ControlVersion[]> {
    const versions = await db
      .select()
      .from(complianceControlVersions)
      .where(eq(complianceControlVersions.controlId, controlId))
      .orderBy(desc(complianceControlVersions.effectiveDate));

    return versions as ControlVersion[];
  }

  /**
   * Get specific version details
   */
  async getVersion(controlId: string, version: string): Promise<ControlVersion | null> {
    const [versionRecord] = await db
      .select()
      .from(complianceControlVersions)
      .where(
        and(
          eq(complianceControlVersions.controlId, controlId),
          eq(complianceControlVersions.version, version)
        )
      )
      .limit(1);

    return (versionRecord as ControlVersion) || null;
  }

  /**
   * Deprecate a specific version
   */
  async deprecateVersion(
    controlId: string,
    version: string,
    deprecatedDate: Date = new Date()
  ): Promise<void> {
    await db
      .update(complianceControlVersions)
      .set({ deprecatedDate })
      .where(
        and(
          eq(complianceControlVersions.controlId, controlId),
          eq(complianceControlVersions.version, version)
        )
      );

    logger.info({ controlId, version }, 'Control version deprecated');
  }

  /**
   * Get all controls that need version updates (no current version)
   */
  async getControlsNeedingVersions(): Promise<string[]> {
    const controls = await db.select({ controlId: complianceControls.controlId }).from(complianceControls);

    const needsVersion: string[] = [];
    for (const control of controls) {
      const currentVersion = await this.getCurrentVersion(control.controlId);
      if (!currentVersion) {
        needsVersion.push(control.controlId);
      }
    }

    return needsVersion;
  }

  /**
   * Initialize versioning for all existing controls
   */
  async initializeAllControlVersions(): Promise<void> {
    logger.info('Initializing versions for all compliance controls');

    const controls = await db.select({ controlId: complianceControls.controlId }).from(complianceControls);

    let initialized = 0;
    for (const control of controls) {
      const currentVersion = await this.getCurrentVersion(control.controlId);
      if (!currentVersion) {
        await this.createVersion(
          control.controlId,
          'major',
          {
            added: ['Initial version'],
            reason: 'Control catalog initialization',
          },
          new Date()
        );
        initialized++;
      }
    }

    logger.info({ initialized, total: controls.length }, 'Control versions initialized');
  }

  /**
   * Bump version number based on type
   */
  private bumpVersion(currentVersion: string, versionType: 'major' | 'minor' | 'patch'): string {
    const [major, minor, patch] = currentVersion.split('.').map(Number);

    switch (versionType) {
      case 'major':
        return `${major + 1}.0.0`;
      case 'minor':
        return `${major}.${minor + 1}.0`;
      case 'patch':
        return `${major}.${minor}.${patch + 1}`;
      default:
        throw new Error(`Invalid version type: ${versionType}`);
    }
  }

  /**
   * Get version statistics
   */
  async getVersionStats(): Promise<{
    totalControls: number;
    versionedControls: number;
    unversionedControls: number;
    totalVersions: number;
    averageVersionsPerControl: number;
  }> {
    const allControls = await db.select().from(complianceControls);
    const allVersions = await db.select().from(complianceControlVersions);

    const versionedControlIds = new Set(allVersions.map((v) => v.controlId));

    return {
      totalControls: allControls.length,
      versionedControls: versionedControlIds.size,
      unversionedControls: allControls.length - versionedControlIds.size,
      totalVersions: allVersions.length,
      averageVersionsPerControl:
        versionedControlIds.size > 0 ? allVersions.length / versionedControlIds.size : 0,
    };
  }
}

export const controlVersioningService = new ComplianceControlVersioningService();

server/services/billing/automated-invoic.content:
import { db } from '../../db';
import {
  billingAccounts,
  subscriptions,
  invoices,
  usageMeters,
  usageEvents,
  type Invoice,
} from '../../../shared/schema';
import { eq, and, gte, lte, sql, desc } from 'drizzle-orm';
import { logger } from '../logger';

export interface UsageAggregation {
  meterId: string;
  meterType: string;
  totalUnits: number;
  unitPrice: number;
  totalAmount: number;
}

export interface InvoiceLineItem {
  description: string;
  quantity: number;
  unitPrice: number;
  amount: number;
  meterId?: string;
}

export interface GeneratedInvoice {
  invoiceId: string;
  billingAccountId: string;
  subscriptionId: string;
  periodStart: Date;
  periodEnd: Date;
  lineItems: InvoiceLineItem[];
  subtotal: number;
  tax: number;
  total: number;
  dueDate: Date;
}

export class AutomatedInvoicingService {
  private readonly TAX_RATE = 0.0;

  async generateMonthlyInvoices(billingMonth: Date): Promise<GeneratedInvoice[]> {
    const startOfMonth = new Date(billingMonth.getFullYear(), billingMonth.getMonth(), 1);
    const endOfMonth = new Date(billingMonth.getFullYear(), billingMonth.getMonth() + 1, 0, 23, 59, 59, 999);

    logger.info('Starting monthly invoice generation', {
      billingMonth: billingMonth.toISOString(),
      periodStart: startOfMonth.toISOString(),
      periodEnd: endOfMonth.toISOString(),
    });

    const activeSubscriptions = await db
      .select({
        subscription: subscriptions,
        account: billingAccounts,
      })
      .from(subscriptions)
      .innerJoin(billingAccounts, eq(subscriptions.billingAccountId, billingAccounts.id))
      .where(
        and(
          eq(subscriptions.status, 'active'),
          lte(subscriptions.currentPeriodStart, endOfMonth),
          gte(subscriptions.currentPeriodEnd, startOfMonth)
        )
      );

    logger.info(`Found ${activeSubscriptions.length} active subscription(s) for billing`);

    const generatedInvoices: GeneratedInvoice[] = [];

    for (const { subscription, account } of activeSubscriptions) {
      try {
        const invoice = await this.generateInvoiceForSubscription(
          subscription,
          account.id,
          startOfMonth,
          endOfMonth
        );
        generatedInvoices.push(invoice);
      } catch (error) {
        logger.error('Failed to generate invoice for subscription', {
          subscriptionId: subscription.id,
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    }

    logger.info(`Generated ${generatedInvoices.length} invoice(s)`);
    return generatedInvoices;
  }

  private async generateInvoiceForSubscription(
    subscription: typeof subscriptions.$inferSelect,
    billingAccountId: string,
    periodStart: Date,
    periodEnd: Date
  ): Promise<GeneratedInvoice> {
    const lineItems: InvoiceLineItem[] = [];

    lineItems.push({
      description: `${subscription.planTier} Plan - Monthly Subscription`,
      quantity: 1,
      unitPrice: parseFloat(subscription.planPrice),
      amount: parseFloat(subscription.planPrice),
    });

    const usageLineItems = await this.aggregateUsageForPeriod(
      subscription.id,
      periodStart,
      periodEnd
    );
    lineItems.push(...usageLineItems);

    const subtotal = lineItems.reduce((sum, item) => sum + item.amount, 0);
    const tax = subtotal * this.TAX_RATE;
    const total = subtotal + tax;

    const dueDate = new Date(periodEnd);
    dueDate.setDate(dueDate.getDate() + 14);

    const invoiceNumber = this.generateInvoiceNumber(periodStart);

    const [createdInvoice] = await db
      .insert(invoices)
      .values({
        billingAccountId,
        subscriptionId: subscription.id,
        invoiceNumber,
        status: 'draft',
        periodStart,
        periodEnd,
        subtotal: subtotal.toString(),
        tax: tax.toString(),
        total: total.toString(),
        currency: subscription.currency,
        dueDate,
        lineItems: lineItems as any,
      })
      .returning();

    logger.info('Invoice created', {
      invoiceId: createdInvoice.id,
      invoiceNumber,
      total,
      lineItemCount: lineItems.length,
    });

    return {
      invoiceId: createdInvoice.id,
      billingAccountId,
      subscriptionId: subscription.id,
      periodStart,
      periodEnd,
      lineItems,
      subtotal,
      tax,
      total,
      dueDate,
    };
  }

  private async aggregateUsageForPeriod(
    subscriptionId: string,
    periodStart: Date,
    periodEnd: Date
  ): Promise<InvoiceLineItem[]> {
    const meters = await db
      .select()
      .from(usageMeters)
      .where(eq(usageMeters.subscriptionId, subscriptionId));

    const lineItems: InvoiceLineItem[] = [];

    for (const meter of meters) {
      const aggregation = await this.aggregateUsageForMeter(meter.id, periodStart, periodEnd);

      if (aggregation.totalUnits > 0) {
        lineItems.push({
          description: this.getUsageDescription(meter.meterType, aggregation.totalUnits),
          quantity: aggregation.totalUnits,
          unitPrice: aggregation.unitPrice,
          amount: aggregation.totalAmount,
          meterId: meter.id,
        });
      }
    }

    return lineItems;
  }

  private async aggregateUsageForMeter(
    meterId: string,
    periodStart: Date,
    periodEnd: Date
  ): Promise<UsageAggregation> {
    const meter = await db.query.usageMeters.findFirst({
      where: eq(usageMeters.id, meterId),
    });

    if (!meter) {
      throw new Error(`Usage meter not found: ${meterId}`);
    }

    const events = await db
      .select()
      .from(usageEvents)
      .where(
        and(
          eq(usageEvents.meterId, meterId),
          gte(usageEvents.timestamp, periodStart),
          lte(usageEvents.timestamp, periodEnd)
        )
      );

    const totalUnits = events.reduce((sum, event) => sum + event.quantity, 0);
    const unitPrice = parseFloat(meter.unitPrice);
    const totalAmount = totalUnits * unitPrice;

    logger.info('Aggregated usage for meter', {
      meterId,
      meterType: meter.meterType,
      eventCount: events.length,
      totalUnits,
      totalAmount,
    });

    return {
      meterId,
      meterType: meter.meterType,
      totalUnits,
      unitPrice,
      totalAmount,
    };
  }

  private getUsageDescription(meterType: string, quantity: number): string {
    const descriptions: Record<string, string> = {
      ai_systems_monitored: `${quantity} AI System(s) Monitored`,
      compliance_checks_run: `${quantity} Compliance Check(s)`,
      alerts_generated: `${quantity} Alert(s) Generated`,
      reports_generated: `${quantity} Report(s) Generated`,
      api_calls: `${quantity} API Call(s)`,
      vendor_certifications: `${quantity} Vendor Certification(s)`,
    };

    return descriptions[meterType] || `${meterType}: ${quantity} unit(s)`;
  }

  private generateInvoiceNumber(periodStart: Date): string {
    const year = periodStart.getFullYear();
    const month = (periodStart.getMonth() + 1).toString().padStart(2, '0');
    const random = Math.random().toString(36).substring(2, 6).toUpperCase();
    return `INV-${year}${month}-${random}`;
  }

  async finalizeInvoice(invoiceId: string): Promise<Invoice> {
    const [invoice] = await db
      .update(invoices)
      .set({
        status: 'open',
        finalizedAt: new Date(),
      })
      .where(eq(invoices.id, invoiceId))
      .returning();

    if (!invoice) {
      throw new Error(`Invoice not found: ${invoiceId}`);
    }

    logger.info('Invoice finalized', {
      invoiceId,
      invoiceNumber: invoice.invoiceNumber,
      total: invoice.total,
    });

    return invoice;
  }

  async markInvoicePaid(invoiceId: string, paymentIntentId?: string): Promise<Invoice> {
    const [invoice] = await db
      .update(invoices)
      .set({
        status: 'paid',
        paidAt: new Date(),
        stripePaymentIntentId: paymentIntentId || null,
      })
      .where(eq(invoices.id, invoiceId))
      .returning();

    if (!invoice) {
      throw new Error(`Invoice not found: ${invoiceId}`);
    }

    logger.info('Invoice marked as paid', {
      invoiceId,
      invoiceNumber: invoice.invoiceNumber,
      paymentIntentId,
    });

    return invoice;
  }

  async voidInvoice(invoiceId: string, reason?: string): Promise<Invoice> {
    const [invoice] = await db
      .update(invoices)
      .set({
        status: 'void',
        voidedAt: new Date(),
      })
      .where(eq(invoices.id, invoiceId))
      .returning();

    if (!invoice) {
      throw new Error(`Invoice not found: ${invoiceId}`);
    }

    logger.info('Invoice voided', {
      invoiceId,
      invoiceNumber: invoice.invoiceNumber,
      reason,
    });

    return invoice;
  }

  async getUpcomingInvoicePreview(subscriptionId: string): Promise<{
    lineItems: InvoiceLineItem[];
    subtotal: number;
    tax: number;
    total: number;
  }> {
    const subscription = await db.query.subscriptions.findFirst({
      where: eq(subscriptions.id, subscriptionId),
    });

    if (!subscription) {
      throw new Error(`Subscription not found: ${subscriptionId}`);
    }

    const now = new Date();
    const periodStart = subscription.currentPeriodStart;
    const periodEnd = subscription.currentPeriodEnd;

    const lineItems: InvoiceLineItem[] = [];

    lineItems.push({
      description: `${subscription.planTier} Plan - Monthly Subscription`,
      quantity: 1,
      unitPrice: parseFloat(subscription.planPrice),
      amount: parseFloat(subscription.planPrice),
    });

    const usageLineItems = await this.aggregateUsageForPeriod(
      subscription.id,
      periodStart,
      now
    );
    lineItems.push(...usageLineItems);

    const subtotal = lineItems.reduce((sum, item) => sum + item.amount, 0);
    const tax = subtotal * this.TAX_RATE;
    const total = subtotal + tax;

    return {
      lineItems,
      subtotal,
      tax,
      total,
    };
  }

  async getInvoicesByBillingAccount(
    billingAccountId: string,
    limit = 50
  ): Promise<Invoice[]> {
    return db
      .select()
      .from(invoices)
      .where(eq(invoices.billingAccountId, billingAccountId))
      .orderBy(desc(invoices.createdAt))
      .limit(limit);
  }

  async getOverdueInvoices(): Promise<Invoice[]> {
    const now = new Date();
    return db
      .select()
      .from(invoices)
      .where(
        and(
          eq(invoices.status, 'open'),
          lte(invoices.dueDate, now)
        )
      )
      .orderBy(invoices.dueDate);
  }
}

export const automatedInvoicingService = new AutomatedInvoicingService();

