client/src/hooks/use-websocket.ts.content:
import { useEffect, useState, useCallback, useRef } from 'react';
import { useAuth } from '@/lib/auth';
import { useQueryClient } from '@tanstack/react-query';

export interface WebSocketEvent {
  type: 'alert_created' | 'alert_resolved' | 'compliance_updated' | 'vendor_status_changed' | 'system_status_changed' | 'connection_established' | 'pong';
  payload: any;
  timestamp: Date;
}

export interface UseWebSocketOptions {
  onMessage?: (event: WebSocketEvent) => void;
  onConnect?: () => void;
  onDisconnect?: () => void;
  autoReconnect?: boolean;
}

export function useWebSocket(options: UseWebSocketOptions = {}) {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [isConnected, setIsConnected] = useState(false);
  const [lastEvent, setLastEvent] = useState<WebSocketEvent | null>(null);
  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout>();
  const pingIntervalRef = useRef<NodeJS.Timeout>();

  const connect = useCallback(() => {
    if (!user || wsRef.current?.readyState === WebSocket.OPEN) {
      return;
    }

    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const host = window.location.host;
    const wsUrl = `${protocol}//${host}/ws`;

    try {
      const ws = new WebSocket(wsUrl);

      ws.onopen = () => {
        console.log('WebSocket connected');
        setIsConnected(true);
        options.onConnect?.();

        pingIntervalRef.current = setInterval(() => {
          if (ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({ type: 'ping' }));
          }
        }, 25000);
      };

      ws.onmessage = (event) => {
        try {
          const data: WebSocketEvent = JSON.parse(event.data);
          setLastEvent(data);
          options.onMessage?.(data);

          switch (data.type) {
            case 'alert_created':
              queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
              queryClient.invalidateQueries({ queryKey: ['/api/predictive-alerts'] });
              break;

            case 'alert_resolved':
              queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
              queryClient.invalidateQueries({ queryKey: ['/api/predictive-alerts'] });
              break;

            case 'compliance_updated':
              queryClient.invalidateQueries({ queryKey: ['/api/compliance'] });
              queryClient.invalidateQueries({ queryKey: ['/api/compliance-mappings'] });
              break;

            case 'system_status_changed':
              queryClient.invalidateQueries({ queryKey: ['/api/ai-systems'] });
              break;

            case 'vendor_status_changed':
              queryClient.invalidateQueries({ queryKey: ['/api/vendors'] });
              break;

            case 'connection_established':
              console.log('WebSocket connection established:', data.payload);
              break;

            case 'pong':
              break;

            default:
              console.log('Unknown WebSocket event type:', data.type);
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
      };

      ws.onclose = () => {
        console.log('WebSocket disconnected');
        setIsConnected(false);
        options.onDisconnect?.();

        if (pingIntervalRef.current) {
          clearInterval(pingIntervalRef.current);
        }

        if (options.autoReconnect !== false) {
          reconnectTimeoutRef.current = setTimeout(() => {
            console.log('Attempting to reconnect...');
            connect();
          }, 5000);
        }
      };

      wsRef.current = ws;
    } catch (error) {
      console.error('Error creating WebSocket:', error);
    }
  }, [user, options, queryClient]);

  const disconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
    }
    if (pingIntervalRef.current) {
      clearInterval(pingIntervalRef.current);
    }
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }
    setIsConnected(false);
  }, []);

  useEffect(() => {
    if (user) {
      connect();
    }

    return () => {
      disconnect();
    };
  }, [user, connect, disconnect]);

  return {
    isConnected,
    lastEvent,
    connect,
    disconnect,
  };
}

server/inngest/functions/certification-w.content:
import { inngest } from "../client";
import { logger } from "../../logger";
import { storage } from "../../storage";
import { runAllVendorTests } from "../../services/vendor-testing/test-runner";

/**
 * Durable certification workflow with automatic retries
 * Runs PHI exposure, clinical accuracy, bias detection, and security scans
 */
export const certificationWorkflow = inngest.createFunction(
  {
    id: "certification-workflow",
    retries: 3,
  },
  { event: "certification/application.submitted" },
  async ({ event, step }) => {
    const { applicationId, vendorId, testSuiteId } = event.data;

    // Step 1: Load application data
    const application = await step.run("load-application", async () => {
      const app = await storage.getCertificationApplication(applicationId);
      if (!app) {
        throw new Error(`Application ${applicationId} not found`);
      }
      logger.info({ applicationId, vendorId }, "Starting certification workflow");
      return app;
    });

    // Step 2: Run all vendor tests (PHI, clinical accuracy, bias, security)
    const testResults = await step.run("run-vendor-tests", async () => {
      const results = await runAllVendorTests({
        applicationId,
        vendorId,
        apiEndpoint: application.apiEndpoint || "",
        apiKey: application.apiKey || undefined,
      });

      logger.info({ applicationId, results: results.summary }, "Test execution complete");
      return results;
    });

    // Step 3: Calculate overall score
    const overallScore = await step.run("calculate-score", async () => {
      const { phiTest, clinicalTest, biasTest, securityTest } = testResults.summary;
      const score =
        (phiTest.score + clinicalTest.score + biasTest.score + securityTest.score) / 4;
      return Math.round(score);
    });

    // Step 4: Determine pass/fail
    const passed = overallScore >= 80;

    // Step 5: Update application status
    await step.run("update-application-status", async () => {
      await storage.updateCertificationApplication(applicationId, {
        status: passed ? "approved" : "rejected",
        testResults: testResults.summary,
        overallScore,
        completedAt: new Date(),
      });

      logger.info(
        { applicationId, passed, overallScore },
        "Certification workflow complete"
      );
    });

    // Step 6: Send notification email (if enabled)
    if (process.env.SENDGRID_API_KEY) {
      await step.run("send-notification", async () => {
        const vendor = await storage.getVendor(vendorId);
        if (vendor?.email) {
          const { sendCertificationResultEmail } = await import(
            "../../services/email-notification"
          );
          await sendCertificationResultEmail(
            vendor.email,
            vendor.name,
            passed,
            overallScore
          );
        }
      });
    }

    return {
      applicationId,
      passed,
      overallScore,
      testResults: testResults.summary,
    };
  }
);

server/inngest/functions/predictive-aler.content:
import { inngest } from "../client";
import { logger } from "../../logger";
import { storage } from "../../storage";
import { generatePredictiveAlerts } from "../../services/predictive-alerts";

/**
 * Scheduled function to generate predictive alerts for health systems
 * Runs hourly via cron
 */
export const predictiveAlertsJob = inngest.createFunction(
  {
    id: "predictive-alerts-hourly",
    retries: 2,
  },
  { cron: "0 * * * *" }, // Every hour at minute 0
  async ({ step }) => {
    // Step 1: Get all active health systems
    const healthSystems = await step.run("get-health-systems", async () => {
      // For now, generate for demo health system
      // In production, loop through all active health systems
      return [{ id: "demo-health-system-001", name: "Demo Health System" }];
    });

    // Step 2: Generate alerts for each health system (parallel)
    const results = await Promise.all(
      healthSystems.map((hs) =>
        step.run(`generate-alerts-${hs.id}`, async () => {
          try {
            const alerts = await generatePredictiveAlerts(hs.id);
            logger.info(
              { healthSystemId: hs.id, alertCount: alerts.length },
              "Generated predictive alerts"
            );
            return { healthSystemId: hs.id, alertCount: alerts.length };
          } catch (error) {
            logger.error(
              { err: error, healthSystemId: hs.id },
              "Failed to generate predictive alerts"
            );
            return { healthSystemId: hs.id, alertCount: 0, error: String(error) };
          }
        })
      )
    );

    return {
      processedHealthSystems: results.length,
      totalAlerts: results.reduce((sum, r) => sum + r.alertCount, 0),
      results,
    };
  }
);

/**
 * On-demand predictive alert generation
 */
export const predictiveAlertsOnDemand = inngest.createFunction(
  {
    id: "predictive-alerts-on-demand",
    retries: 2,
  },
  { event: "alerts/predictive.generate" },
  async ({ event, step }) => {
    const { healthSystemId } = event.data;

    const alerts = await step.run("generate-alerts", async () => {
      const alerts = await generatePredictiveAlerts(healthSystemId);
      logger.info(
        { healthSystemId, alertCount: alerts.length },
        "Generated predictive alerts on-demand"
      );
      return alerts;
    });

    return {
      healthSystemId,
      alertCount: alerts.length,
    };
  }
);

server/inngest/functions/action-executor.content:
import { inngest } from "../client";
import { logger } from "../../logger";
import { storage } from "../../storage";
import { executeAutomatedAction } from "../../services/action-executor";

/**
 * Scheduled automated action executor
 * Runs every 5 minutes to execute pending automated actions
 */
export const automatedActionExecutor = inngest.createFunction(
  {
    id: "automated-action-executor",
    retries: 2,
  },
  { cron: "*/5 * * * *" }, // Every 5 minutes
  async ({ step }) => {
    // Step 1: Get pending automated actions
    const pendingActions = await step.run("get-pending-actions", async () => {
      const actions = await storage.getPendingAutomatedActions();
      logger.info({ count: actions.length }, "Found pending automated actions");
      return actions;
    });

    if (pendingActions.length === 0) {
      return { executedActions: 0, results: [] };
    }

    // Step 2: Execute actions in parallel (with concurrency limit)
    const batchSize = 5; // Execute 5 actions at a time
    const results = [];

    for (let i = 0; i < pendingActions.length; i += batchSize) {
      const batch = pendingActions.slice(i, i + batchSize);

      const batchResults = await Promise.all(
        batch.map((action) =>
          step.run(`execute-action-${action.id}`, async () => {
            try {
              await executeAutomatedAction(action.id);
              logger.info({ actionId: action.id }, "Executed automated action");
              return { actionId: action.id, success: true };
            } catch (error) {
              logger.error(
                { err: error, actionId: action.id },
                "Failed to execute automated action"
              );
              return { actionId: action.id, success: false, error: String(error) };
            }
          })
        )
      );

      results.push(...batchResults);

      // Optional: Add delay between batches to avoid rate limits
      if (i + batchSize < pendingActions.length) {
        await step.sleep("batch-delay", "2s");
      }
    }

    return {
      executedActions: results.length,
      successCount: results.filter((r) => r.success).length,
      failureCount: results.filter((r) => !r.success).length,
      results,
    };
  }
);

server/services/workos.ts.content:
import { WorkOS } from '@workos-inc/node';
import { logger } from '../logger';

// WorkOS client singleton
let workosClient: WorkOS | null = null;

/**
 * Get WorkOS client instance
 * Returns null if credentials not configured
 */
export function getWorkOSClient(): WorkOS | null {
  if (workosClient) {
    return workosClient;
  }

  const apiKey = process.env.WORKOS_API_KEY;

  if (!apiKey) {
    logger.warn('WorkOS not configured - SSO features disabled. Set WORKOS_API_KEY to enable.');
    return null;
  }

  try {
    workosClient = new WorkOS(apiKey);
    logger.info('WorkOS client initialized successfully');
    return workosClient;
  } catch (error) {
    logger.error({ err: error }, 'Failed to initialize WorkOS client');
    return null;
  }
}

/**
 * Get WorkOS configuration
 */
export function getWorkOSConfig() {
  return {
    clientId: process.env.WORKOS_CLIENT_ID || '',
    cookiePassword: process.env.WORKOS_COOKIE_PASSWORD || '',
    redirectUri: process.env.WORKOS_REDIRECT_URI || '',
  };
}

/**
 * Check if WorkOS is properly configured
 */
export function isWorkOSConfigured(): boolean {
  return !!(
    process.env.WORKOS_API_KEY &&
    process.env.WORKOS_CLIENT_ID &&
    process.env.WORKOS_COOKIE_PASSWORD
  );
}

/**
 * WorkOS session data structure
 */
export interface WorkOSSession {
  userId: string;
  email: string;
  firstName?: string;
  lastName?: string;
  ssoProvider: string;
  ssoExternalId: string;
  ssoOrganizationId?: string;
}

