server/services/scoring/clinical-safety-.content:
/**
 * Clinical Safety Scoring Service
 * 
 * CRITICAL COMPONENT (Gap Severity: 90/100)
 * Healthcare-specific risk types: Clinical accuracy, bias, hallucination detection
 * 
 * This service calculates patient safety scores by combining:
 * - Clinical accuracy (correct diagnoses, predictions)
 * - Bias detection (demographic fairness)
 * - Hallucination detection (AI generating false clinical info)
 * - Patient safety events
 * 
 * Weight in overall score: 25% (clinical safety component)
 */

import { storage } from "../../storage";
import { logger } from "../../logger";
import { validateTelemetry, getDataQuality, logScoringAudit } from "./telemetry-validator";

export interface ClinicalSafetyScore {
  score: number; // 0-100 (100 = perfect clinical safety)
  grade: "A" | "B" | "C" | "D" | "F";
  trend: "improving" | "stable" | "declining";
  components: {
    clinicalAccuracy: { score: number; grade: string };
    biasDetection: { score: number; grade: string };
    hallucinationControl: { score: number; grade: string };
    patientSafety: { score: number; grade: string };
  };
  riskFactors: {
    inaccurateDiagnoses: number;
    biasViolations: number;
    hallucinationEvents: number;
    patientSafetyIncidents: number;
  };
  recommendations: string[];
}

// Clinical accuracy event types
const CLINICAL_ACCURACY_EVENTS = [
  'clinical_accuracy_test',
  'diagnosis_accuracy',
  'prediction_accuracy',
  'clinical_validation_failed',
  'false_positive',
  'false_negative',
];

// Bias-related event types
const BIAS_EVENTS = [
  'bias_detected',
  'demographic_disparity',
  'fairness_violation',
  'disparate_impact',
  'bias_test_failed',
];

// Hallucination event types
const HALLUCINATION_EVENTS = [
  'hallucination_detected',
  'false_clinical_info',
  'ai_confabulation',
  'unsupported_claim',
  'model_hallucination',
];

// Patient safety event types
const PATIENT_SAFETY_EVENTS = [
  'patient_safety_incident',
  'adverse_event',
  'near_miss',
  'safety_alert',
  'clinical_error',
];

/**
 * Calculate clinical accuracy score
 */
async function calculateClinicalAccuracyScore(aiSystemId: string): Promise<number> {
  try {
    const events = await storage.getAITelemetryEvents(aiSystemId);

    // Get recent clinical accuracy events (last 7 days for clinical metrics)
    const recentEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const weekAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);
      return eventTime > weekAgo && CLINICAL_ACCURACY_EVENTS.includes(e.eventType);
    });

    // No events means no data (default to moderate score)
    if (recentEvents.length === 0) return 75;

    // Calculate risk from clinical accuracy events
    const riskPoints = recentEvents.reduce((sum: number, e: any) => {
      if (e.severity === 'critical') return sum + 50; // Critical clinical error
      if (e.severity === 'high') return sum + 25;
      if (e.severity === 'medium') return sum + 10;
      return sum + 5;
    }, 0);

    // Get AI system to find vendor ID
    const aiSystem = await storage.getAISystem(aiSystemId);
    if (aiSystem && aiSystem.vendorId) {
      // Look for clinical validation test results in vendor test results
      const testResults = await storage.getVendorTestResultsByVendor(aiSystem.vendorId);
      const clinicalTests = testResults.filter((t: any) => t.testType === 'clinical_accuracy');

      if (clinicalTests.length > 0) {
        // Use latest test score if available
        const latestTest = clinicalTests[clinicalTests.length - 1];
        if (latestTest.score !== null) {
          return latestTest.score;
        }
      }
    }

    // Convert risk points to score (inverted)
    const normalizedRisk = Math.min(riskPoints, 100);
    return Math.max(0, 100 - normalizedRisk);
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate clinical accuracy score");
    return 70; // Default moderate score on error
  }
}

/**
 * Calculate bias detection score
 */
async function calculateBiasScore(aiSystemId: string): Promise<number> {
  try {
    const events = await storage.getAITelemetryEvents(aiSystemId);

    // Get recent bias events (last 30 days)
    const recentEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const monthAgo = Date.now() - (30 * 24 * 60 * 60 * 1000);
      return eventTime > monthAgo && BIAS_EVENTS.includes(e.eventType);
    });

    // Calculate risk from bias events
    const riskPoints = recentEvents.reduce((sum: number, e: any) => {
      if (e.severity === 'critical') return sum + 40;
      if (e.severity === 'high') return sum + 20;
      if (e.severity === 'medium') return sum + 10;
      return sum + 5;
    }, 0);

    // Get AI system to find vendor ID
    const aiSystem = await storage.getAISystem(aiSystemId);
    if (aiSystem && aiSystem.vendorId) {
      // Check for bias test results
      const testResults = await storage.getVendorTestResultsByVendor(aiSystem.vendorId);
      const biasTests = testResults.filter((t: any) => t.testType === 'bias_detection');

      if (biasTests.length > 0) {
        const latestTest = biasTests[biasTests.length - 1];
        if (latestTest.score !== null) {
          return latestTest.score;
        }
      }
    }

    // Convert risk points to score
    const normalizedRisk = Math.min(riskPoints, 100);
    return Math.max(0, 100 - normalizedRisk);
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate bias score");
    return 75;
  }
}

/**
 * Calculate hallucination control score
 */
async function calculateHallucinationScore(aiSystemId: string): Promise<number> {
  try {
    const events = await storage.getAITelemetryEvents(aiSystemId);

    // Get recent hallucination events (last 7 days)
    const recentEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const weekAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);
      return eventTime > weekAgo && HALLUCINATION_EVENTS.includes(e.eventType);
    });

    // Hallucination events are critical in healthcare
    const riskPoints = recentEvents.reduce((sum: number, e: any) => {
      if (e.severity === 'critical') return sum + 60; // Very serious
      if (e.severity === 'high') return sum + 30;
      if (e.severity === 'medium') return sum + 15;
      return sum + 7;
    }, 0);

    const normalizedRisk = Math.min(riskPoints, 100);
    return Math.max(0, 100 - normalizedRisk);
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate hallucination score");
    return 80;
  }
}

/**
 * Calculate patient safety score
 */
async function calculatePatientSafetyScore(aiSystemId: string): Promise<number> {
  try {
    const events = await storage.getAITelemetryEvents(aiSystemId);

    // Get patient safety events (last 30 days)
    const recentEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const monthAgo = Date.now() - (30 * 24 * 60 * 60 * 1000);
      return eventTime > monthAgo && PATIENT_SAFETY_EVENTS.includes(e.eventType);
    });

    // Patient safety incidents are the most critical
    const riskPoints = recentEvents.reduce((sum: number, e: any) => {
      if (e.severity === 'critical') return sum + 80; // Extremely serious
      if (e.severity === 'high') return sum + 40;
      if (e.severity === 'medium') return sum + 20;
      return sum + 10;
    }, 0);

    const normalizedRisk = Math.min(riskPoints, 100);
    return Math.max(0, 100 - normalizedRisk);
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate patient safety score");
    return 85;
  }
}

/**
 * Calculate comprehensive clinical safety score
 */
export async function calculateClinicalSafetyScore(aiSystemId: string): Promise<ClinicalSafetyScore> {
  try {
    // Validate telemetry data freshness and completeness FIRST
    const allEvents = await storage.getAITelemetryEvents(aiSystemId);
    const validation = validateTelemetry(allEvents, 'clinical-safety');
    const dataQuality = getDataQuality(validation);

    // Log warnings
    if (validation.warnings.length > 0) {
      logger.warn({ 
        aiSystemId, 
        warnings: validation.warnings,
        telemetryAge: `${Math.round(validation.age)}h`,
        eventCount: validation.eventCount
      }, 'Clinical safety scoring: data quality warnings');
    }

    // SHORT-CIRCUIT: Return degraded score when validation fails
    if (validation.errors.length > 0) {
      logger.error({ 
        aiSystemId, 
        errors: validation.errors,
        telemetryAge: `${Math.round(validation.age)}h`,
        eventCount: validation.eventCount
      }, 'Clinical safety scoring: data quality errors - returning degraded score');

      logScoringAudit({
        timestamp: new Date(),
        aiSystemId,
        scoringType: 'clinical-safety',
        telemetryAge: validation.age,
        eventCount: validation.eventCount,
        score: 0,
        dataQuality: 'missing',
        warnings: validation.errors,
      });

      // Return minimal score structure
      return {
        score: 0,
        grade: "F",
        trend: "stable",
        components: {
          clinicalAccuracy: { score: 0, grade: "F" },
          biasDetection: { score: 0, grade: "F" },
          hallucinationControl: { score: 0, grade: "F" },
          patientSafety: { score: 0, grade: "F" },
        },
        riskFactors: {
          inaccurateDiagnoses: 0,
          biasViolations: 0,
          hallucinationEvents: 0,
          patientSafetyIncidents: 0,
        },
        recommendations: ['Missing or stale telemetry data - unable to assess clinical safety'],
      };
    }

    // Calculate component scores in parallel
    const [clinicalAccuracy, biasDetection, hallucinationControl, patientSafety] = await Promise.all([
      calculateClinicalAccuracyScore(aiSystemId),
      calculateBiasScore(aiSystemId),
      calculateHallucinationScore(aiSystemId),
      calculatePatientSafetyScore(aiSystemId),
    ]);

    // Component weights
    const WEIGHTS = {
      clinicalAccuracy: 0.35,   // 35% - Most critical
      biasDetection: 0.25,      // 25% - Fairness is key
      hallucinationControl: 0.20, // 20% - False info is dangerous
      patientSafety: 0.20,      // 20% - Direct harm prevention
    };

    // Calculate overall weighted score
    const score = Math.round(
      clinicalAccuracy * WEIGHTS.clinicalAccuracy +
      biasDetection * WEIGHTS.biasDetection +
      hallucinationControl * WEIGHTS.hallucinationControl +
      patientSafety * WEIGHTS.patientSafety
    );

    // Generate grade
    let grade: "A" | "B" | "C" | "D" | "F";
    if (score >= 90) grade = "A";
    else if (score >= 80) grade = "B";
    else if (score >= 70) grade = "C";
    else if (score >= 60) grade = "D";
    else grade = "F";

    // Helper to generate component grade
    const gradeForScore = (s: number): string => {
      if (s >= 90) return "A";
      if (s >= 80) return "B";
      if (s >= 70) return "C";
      if (s >= 60) return "D";
      return "F";
    };

    // Get event counts for risk factors
    const events = await storage.getAITelemetryEvents(aiSystemId);
    const monthAgo = Date.now() - (30 * 24 * 60 * 60 * 1000);
    const recentEvents = events.filter((e: any) => new Date(e.timestamp).getTime() > monthAgo);

    const riskFactors = {
      inaccurateDiagnoses: recentEvents.filter((e: any) => 
        CLINICAL_ACCURACY_EVENTS.includes(e.eventType)
      ).length,
      biasViolations: recentEvents.filter((e: any) => 
        BIAS_EVENTS.includes(e.eventType)
      ).length,
      hallucinationEvents: recentEvents.filter((e: any) => 
        HALLUCINATION_EVENTS.includes(e.eventType)
      ).length,
      patientSafetyIncidents: recentEvents.filter((e: any) => 
        PATIENT_SAFETY_EVENTS.includes(e.eventType)
      ).length,
    };

    // Calculate trend (compare last 30 days vs previous 30 days)
    const previousEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const twoMonthsAgo = Date.now() - (60 * 24 * 60 * 60 * 1000);
      return eventTime > twoMonthsAgo && eventTime <= monthAgo;
    });

    const currentRiskEvents = recentEvents.filter((e: any) => 
      [...CLINICAL_ACCURACY_EVENTS, ...BIAS_EVENTS, ...HALLUCINATION_EVENTS, ...PATIENT_SAFETY_EVENTS].includes(e.eventType)
    ).length;

    const previousRiskEvents = previousEvents.filter((e: any) => 
      [...CLINICAL_ACCURACY_EVENTS, ...BIAS_EVENTS, ...HALLUCINATION_EVENTS, ...PATIENT_SAFETY_EVENTS].includes(e.eventType)
    ).length;

    let trend: "improving" | "stable" | "declining";
    if (currentRiskEvents < previousRiskEvents * 0.8) trend = "improving";
    else if (currentRiskEvents > previousRiskEvents * 1.2) trend = "declining";
    else trend = "stable";

    // Generate recommendations
    const recommendations: string[] = [];
    if (clinicalAccuracy < 70) {
      recommendations.push("URGENT: Clinical accuracy below acceptable threshold - review model validation");
    }
    if (biasDetection < 75) {
      recommendations.push("Bias detected across demographics - implement fairness constraints");
    }
    if (hallucinationControl < 80) {
      recommendations.push("AI generating unsupported claims - enable fact-checking and citations");
    }
    if (patientSafety < 85) {
      recommendations.push("CRITICAL: Patient safety incidents detected - immediate review required");
    }
    if (riskFactors.patientSafetyIncidents > 0) {
      recommendations.push(`${riskFactors.patientSafetyIncidents} patient safety incident(s) in last 30 days - escalate to clinical team`);
    }

    // Log audit trail for acquisition due diligence
    logScoringAudit({
      timestamp: new Date(),
      aiSystemId,
      scoringType: 'clinical-safety',
      telemetryAge: validation.age,
      eventCount: validation.eventCount,
      score,
      dataQuality,
      warnings: validation.warnings,
    });

    return {
      score,
      grade,
      trend,
      components: {
        clinicalAccuracy: { score: Math.round(clinicalAccuracy), grade: gradeForScore(clinicalAccuracy) },
        biasDetection: { score: Math.round(biasDetection), grade: gradeForScore(biasDetection) },
        hallucinationControl: { score: Math.round(hallucinationControl), grade: gradeForScore(hallucinationControl) },
        patientSafety: { score: Math.round(patientSafety), grade: gradeForScore(patientSafety) },
      },
      riskFactors,
      recommendations,
    };
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate clinical safety score");
    throw error;
  }
}

/**
 * Calculate portfolio-wide clinical safety score
 */
export async function calculatePortfolioClinicalSafety(healthSystemId: string): Promise<{
  score: number;
  grade: "A" | "B" | "C" | "D" | "F";
  systemScores: Array<{ systemId: string; systemName: string; score: number }>;
  criticalSafetyIssues: number;
  totalRiskEvents: number;
}> {
  try {
    const systems = await storage.getAISystems(healthSystemId);

    if (systems.length === 0) {
      return {
        score: 100,
        grade: "A",
        systemScores: [],
        criticalSafetyIssues: 0,
        totalRiskEvents: 0,
      };
    }

    // Calculate score for each system
    const systemAssessments = await Promise.all(
      systems.map(async (sys: any) => {
        const assessment = await calculateClinicalSafetyScore(sys.id);
        return {
          systemId: sys.id,
          systemName: sys.name,
          score: assessment.score,
          assessment,
        };
      })
    );

    const systemScores = systemAssessments.map((s: any) => ({
      systemId: s.systemId,
      systemName: s.systemName,
      score: s.score,
    }));

    // Portfolio score is average
    const avgScore = systemScores.reduce((sum: number, s: any) => sum + s.score, 0) / systemScores.length;

    // Count critical safety issues (patient safety incidents + critical hallucinations)
    const criticalSafetyIssues = systemAssessments.reduce(
      (sum: number, s: any) => sum + s.assessment.riskFactors.patientSafetyIncidents, 
      0
    );

    // Total risk events across all categories
    const totalRiskEvents = systemAssessments.reduce((sum: number, s: any) => {
      const rf = s.assessment.riskFactors;
      return sum + rf.inaccurateDiagnoses + rf.biasViolations + rf.hallucinationEvents + rf.patientSafetyIncidents;
    }, 0);

    // Generate grade
    let grade: "A" | "B" | "C" | "D" | "F";
    if (avgScore >= 90) grade = "A";
    else if (avgScore >= 80) grade = "B";
    else if (avgScore >= 70) grade = "C";
    else if (avgScore >= 60) grade = "D";
    else grade = "F";

    return {
      score: Math.round(avgScore),
      grade,
      systemScores,
      criticalSafetyIssues,
      totalRiskEvents,
    };
  } catch (error) {
    logger.error({ error, healthSystemId }, "Failed to calculate portfolio clinical safety");
    throw error;
  }
}

export const clinicalSafetyScoringService = {
  calculateClinicalSafetyScore,
  calculatePortfolioClinicalSafety,
};

server/services/scoring/phi-risk-scoring.content:
/**
 * PHI Risk Scoring Service
 * 
 * CRITICAL COMPONENT (Gap Severity: 95/100)
 * PHI leakage is Spectral's #1 value proposition.
 * 
 * This service calculates PHI protection scores by:
 * - Identifying PHI-specific telemetry events
 * - Weighting PHI events 10x higher than generic events
 * - Tracking PHI exposure risk across AI systems
 * - Mapping PHI violations to HIPAA controls
 * 
 * Score: 0-100 (higher = better PHI protection)
 * Weight in overall score: 35% (highest priority)
 */

import { storage } from "../../storage";
import { logger } from "../../logger";
import { 
  validateTelemetry, 
  logScoringAudit, 
  getDataQuality, 
  getConfidenceModifier 
} from "./telemetry-validator";

export interface PHIRiskScore {
  score: number; // 0-100 (100 = perfect PHI protection)
  grade: "A" | "B" | "C" | "D" | "F";
  trend: "improving" | "stable" | "declining";
  phiEvents: {
    critical: number;
    high: number;
    medium: number;
    low: number;
    total: number;
  };
  riskFactors: {
    phiLeakageEvents: number; // Actual PHI detected in outputs
    phiExposureRisk: number; // Systems with high PHI exposure risk
    phiAccessViolations: number; // Unauthorized PHI access attempts
    encryptionFailures: number; // Failed PHI encryption events
  };
  hipaaImpact: {
    violatedControls: string[]; // HIPAA controls violated by PHI events
    severity: "none" | "low" | "medium" | "high" | "critical";
  };
  recommendations: string[];
}

// PHI event types that trigger scoring (mapped from aiTelemetryEvents)
const PHI_EVENT_TYPES = [
  'phi_exposure',
  'phi_leak',
  'phi_leakage_detected',
  'phi_access_violation',
  'phi_encryption_failure',
  'unauthorized_phi_access',
  'phi_in_logs',
  'phi_in_model_output',
];

// PHI metric types from predictive alerts
const PHI_METRICS = [
  'phi_leak_count',
  'phi_exposure_score',
  'phi_risk_level',
];

// HIPAA controls related to PHI protection
const PHI_HIPAA_CONTROLS = {
  '164.312(a)(1)': 'Access Control - PHI Access Authorization',
  '164.312(b)': 'Audit Controls - PHI Access Logging',
  '164.312(c)(1)': 'Integrity Controls - PHI Data Integrity',
  '164.312(d)': 'Person or Entity Authentication',
  '164.312(e)(1)': 'Transmission Security - PHI Encryption',
  '164.308(a)(1)': 'Security Management Process',
  '164.308(a)(3)': 'Workforce Security - PHI Access',
  '164.530(c)': 'Safeguards - PHI Protection',
};

/**
 * Calculate PHI risk score for a single AI system
 */
export async function calculatePHIRiskScore(aiSystemId: string): Promise<number> {
  try {
    // Get ALL telemetry events (not just recent) for validation
    const allEvents = await storage.getAITelemetryEvents(aiSystemId);

    // Validate telemetry data freshness and completeness
    const validation = validateTelemetry(allEvents, 'phi-risk');
    const dataQuality = getDataQuality(validation);

    // Log warnings and errors
    if (validation.warnings.length > 0) {
      logger.warn({ 
        aiSystemId, 
        warnings: validation.warnings,
        telemetryAge: `${Math.round(validation.age)}h`,
        eventCount: validation.eventCount
      }, 'PHI risk scoring: data quality warnings');
    }

    if (validation.errors.length > 0) {
      logger.error({ 
        aiSystemId, 
        errors: validation.errors,
        telemetryAge: `${Math.round(validation.age)}h`,
        eventCount: validation.eventCount
      }, 'PHI risk scoring: data quality errors - returning degraded score');

      // SHORT-CIRCUIT: Return degraded score immediately when validation fails
      // This prevents stale/missing telemetry from inflating grades
      logScoringAudit({
        timestamp: new Date(),
        aiSystemId,
        scoringType: 'phi-risk',
        telemetryAge: validation.age,
        eventCount: validation.eventCount,
        score: 0,
        dataQuality: 'missing',
        warnings: validation.errors,
      });

      return 0;
    }

    // Filter for recent events (last 24 hours) for scoring
    const recentEvents = allEvents.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const dayAgo = Date.now() - (24 * 60 * 60 * 1000);
      return eventTime > dayAgo;
    });

    // Filter for PHI-specific events
    const phiEvents = recentEvents.filter((e: any) => 
      PHI_EVENT_TYPES.includes(e.eventType) || 
      (e.metric && PHI_METRICS.includes(e.metric))
    );

    // Weight PHI events heavily (10x multiplier vs generic events)
    const phiRiskPoints = phiEvents.reduce((sum: number, e: any) => {
      if (e.severity === 'critical') return sum + 100; // Critical PHI leak = 100 points
      if (e.severity === 'high') return sum + 50;      // High PHI risk = 50 points
      if (e.severity === 'medium') return sum + 20;    // Medium PHI risk = 20 points
      return sum + 10;                                  // Low PHI risk = 10 points
    }, 0);

    // Get predictive alerts for PHI exposure
    const predictiveAlerts = await storage.getPredictiveAlerts(aiSystemId);
    const phiPredictiveAlerts = predictiveAlerts.filter((a: any) => 
      a.predictionType === 'phi_exposure' && !a.dismissed
    );

    // Add predictive risk points (20 points per high-confidence PHI prediction)
    const predictiveRiskPoints = phiPredictiveAlerts.reduce((sum: number, alert: any) => {
      const confidence = alert.confidenceScore || 0;
      if (confidence > 80) return sum + 20;
      if (confidence > 60) return sum + 10;
      return sum + 5;
    }, 0);

    // Total risk points
    const totalRiskPoints = phiRiskPoints + predictiveRiskPoints;

    // Convert to 0-100 score (inverted: higher score = better protection)
    // Cap at 200 points max for normalization
    const normalizedRisk = Math.min(totalRiskPoints, 200);
    let score = Math.max(0, 100 - (normalizedRisk / 2));

    // Apply confidence modifier based on data quality
    // Stale data gets penalized, missing data gets heavily penalized
    const confidenceModifier = getConfidenceModifier(dataQuality);
    score = score * confidenceModifier;

    // Audit log for due diligence
    logScoringAudit({
      timestamp: new Date(),
      aiSystemId,
      scoringType: 'phi-risk',
      telemetryAge: validation.age,
      eventCount: validation.eventCount,
      score: Math.round(score),
      dataQuality,
      warnings: validation.warnings,
    });

    return Math.round(score);
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate PHI risk score");

    // Audit log the failure
    logScoringAudit({
      timestamp: new Date(),
      aiSystemId,
      scoringType: 'phi-risk',
      telemetryAge: Infinity,
      eventCount: 0,
      score: 0,
      dataQuality: 'missing',
      warnings: ['Scoring calculation failed - returning degraded score'],
    });

    // Return degraded score (0) instead of optimistic 50
    return 0;
  }
}

/**
 * Calculate comprehensive PHI risk assessment for an AI system
 */
export async function calculateComprehensivePHIRisk(aiSystemId: string): Promise<PHIRiskScore> {
  try {
    const score = await calculatePHIRiskScore(aiSystemId);

    // Get recent PHI events for breakdown
    const events = await storage.getAITelemetryEvents(aiSystemId);
    const recentEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const dayAgo = Date.now() - (24 * 60 * 60 * 1000);
      return eventTime > dayAgo;
    });

    const phiEvents = recentEvents.filter((e: any) => 
      PHI_EVENT_TYPES.includes(e.eventType) || 
      (e.metric && PHI_METRICS.includes(e.metric))
    );

    // Categorize PHI events by severity
    const phiEventBreakdown = {
      critical: phiEvents.filter((e: any) => e.severity === 'critical').length,
      high: phiEvents.filter((e: any) => e.severity === 'high').length,
      medium: phiEvents.filter((e: any) => e.severity === 'medium').length,
      low: phiEvents.filter((e: any) => e.severity === 'low').length,
      total: phiEvents.length,
    };

    // Categorize by risk factor type
    const riskFactors = {
      phiLeakageEvents: phiEvents.filter((e: any) => 
        e.eventType.includes('leak') || e.eventType.includes('exposure')
      ).length,
      phiExposureRisk: phiEvents.filter((e: any) => 
        e.eventType.includes('exposure')
      ).length,
      phiAccessViolations: phiEvents.filter((e: any) => 
        e.eventType.includes('access') || e.eventType.includes('unauthorized')
      ).length,
      encryptionFailures: phiEvents.filter((e: any) => 
        e.eventType.includes('encryption')
      ).length,
    };

    // Determine violated HIPAA controls based on event types
    const violatedControls: string[] = [];
    if (riskFactors.phiLeakageEvents > 0) {
      violatedControls.push('164.530(c)'); // Safeguards - PHI Protection
      violatedControls.push('164.312(c)(1)'); // Integrity Controls
    }
    if (riskFactors.phiAccessViolations > 0) {
      violatedControls.push('164.312(a)(1)'); // Access Control
      violatedControls.push('164.312(d)'); // Authentication
    }
    if (riskFactors.encryptionFailures > 0) {
      violatedControls.push('164.312(e)(1)'); // Transmission Security
    }
    if (phiEventBreakdown.total > 0) {
      violatedControls.push('164.312(b)'); // Audit Controls (should have caught this)
    }

    // Determine HIPAA impact severity
    let hipaaImpactSeverity: "none" | "low" | "medium" | "high" | "critical" = "none";
    if (phiEventBreakdown.critical > 0) hipaaImpactSeverity = "critical";
    else if (phiEventBreakdown.high > 0) hipaaImpactSeverity = "high";
    else if (phiEventBreakdown.medium > 0) hipaaImpactSeverity = "medium";
    else if (phiEventBreakdown.low > 0) hipaaImpactSeverity = "low";

    // Generate grade
    let grade: "A" | "B" | "C" | "D" | "F";
    if (score >= 90) grade = "A";
    else if (score >= 80) grade = "B";
    else if (score >= 70) grade = "C";
    else if (score >= 60) grade = "D";
    else grade = "F";

    // Calculate trend (compare last 24h vs previous 24h)
    const previousEvents = events.filter((e: any) => {
      const eventTime = new Date(e.timestamp).getTime();
      const twoDaysAgo = Date.now() - (48 * 60 * 60 * 1000);
      const oneDayAgo = Date.now() - (24 * 60 * 60 * 1000);
      return eventTime > twoDaysAgo && eventTime <= oneDayAgo;
    });
    const previousPHIEvents = previousEvents.filter((e: any) => 
      PHI_EVENT_TYPES.includes(e.eventType)
    ).length;

    let trend: "improving" | "stable" | "declining";
    if (phiEvents.length < previousPHIEvents * 0.8) trend = "improving";
    else if (phiEvents.length > previousPHIEvents * 1.2) trend = "declining";
    else trend = "stable";

    // Generate recommendations
    const recommendations: string[] = [];
    if (phiEventBreakdown.critical > 0) {
      recommendations.push("URGENT: Critical PHI leakage detected. Immediate rollback recommended.");
      recommendations.push("Review access controls per HIPAA 164.312(a)(1)");
    }
    if (riskFactors.encryptionFailures > 0) {
      recommendations.push("Enable PHI encryption in transit per HIPAA 164.312(e)(1)");
    }
    if (riskFactors.phiAccessViolations > 0) {
      recommendations.push("Audit user permissions and implement least-privilege access");
    }
    if (score < 70) {
      recommendations.push("Consider pausing system deployment until PHI protection improves");
    }
    if (violatedControls.length > 0) {
      recommendations.push(`Address ${violatedControls.length} HIPAA control violations`);
    }

    return {
      score,
      grade,
      trend,
      phiEvents: phiEventBreakdown,
      riskFactors,
      hipaaImpact: {
        violatedControls,
        severity: hipaaImpactSeverity,
      },
      recommendations,
    };
  } catch (error) {
    logger.error({ error, aiSystemId }, "Failed to calculate comprehensive PHI risk");
    throw error;
  }
}

/**
 * Calculate portfolio-wide PHI protection score
 */
export async function calculatePortfolioPHIScore(healthSystemId: string): Promise<{
  score: number;
  grade: "A" | "B" | "C" | "D" | "F";
  systemScores: Array<{ systemId: string; systemName: string; score: number }>;
  criticalPHIIssues: number;
  totalViolatedControls: string[];
}> {
  try {
    const systems = await storage.getAISystems(healthSystemId);

    if (systems.length === 0) {
      return {
        score: 100,
        grade: "A",
        systemScores: [],
        criticalPHIIssues: 0,
        totalViolatedControls: [],
      };
    }

    // Calculate score for each system
    const systemScores = await Promise.all(
      systems.map(async (sys: any) => ({
        systemId: sys.id,
        systemName: sys.name,
        score: await calculatePHIRiskScore(sys.id),
      }))
    );

    // Portfolio score is average of system scores
    const avgScore = systemScores.reduce((sum: number, s: any) => sum + s.score, 0) / systemScores.length;

    // Get comprehensive assessments to count critical issues
    const assessments = await Promise.all(
      systems.map((sys: any) => calculateComprehensivePHIRisk(sys.id))
    );

    const criticalPHIIssues = assessments.reduce(
      (sum: number, a: any) => sum + a.phiEvents.critical, 
      0
    );

    // Collect all unique violated controls
    const allViolatedControls = new Set<string>();
    assessments.forEach((a: any) => {
      a.hipaaImpact.violatedControls.forEach((c: string) => allViolatedControls.add(c));
    });

    // Generate grade
    let grade: "A" | "B" | "C" | "D" | "F";
    if (avgScore >= 90) grade = "A";
    else if (avgScore >= 80) grade = "B";
    else if (avgScore >= 70) grade = "C";
    else if (avgScore >= 60) grade = "D";
    else grade = "F";

    return {
      score: Math.round(avgScore),
      grade,
      systemScores,
      criticalPHIIssues,
      totalViolatedControls: Array.from(allViolatedControls),
    };
  } catch (error) {
    logger.error({ error, healthSystemId }, "Failed to calculate portfolio PHI score");
    throw error;
  }
}

export const phiRiskScoringService = {
  calculatePHIRiskScore,
  calculateComprehensivePHIRisk,
  calculatePortfolioPHIScore,
};

server/services/analytics-healthcare-sco.content:
/**
 * Healthcare-Specific Analytics & Scoring Service
 * 
 * TRANSFORMS SPECTRAL FROM C+ (73%) ‚Üí A- (92%)
 * 
 * New Scoring Weights (Healthcare AI Differentiation):
 * - PHI Protection: 35% (highest priority - defensive healthcare positioning)
 * - Clinical Safety: 25% (accuracy + bias + hallucinations)
 * - Regulatory Compliance: 25% (HIPAA + NIST + FDA + State Laws)
 * - Operational Health: 15% (alerts, response times, verification)
 * 
 * This replaces the generic portfolio health score with healthcare-specific metrics
 * that match the company's value proposition for strategic acquisition.
 */

import { storage } from "../storage";
import { calculatePortfolioPHIScore } from "./scoring/phi-risk-scoring";
import { calculatePortfolioClinicalSafety } from "./scoring/clinical-safety-scoring";
import { calculatePortfolioCompliance } from "./scoring/framework-compliance-scoring";
import { logger } from "../logger";

export interface HealthcarePortfolioScore {
  overall: number; // 0-100 (weighted composite score)
  grade: "A" | "B" | "C" | "D" | "F";
  breakdown: {
    phiProtection: { score: number; grade: string; weight: number }; // 35%
    clinicalSafety: { score: number; grade: string; weight: number }; // 25%
    regulatoryCompliance: { score: number; grade: string; weight: number }; // 25%
    operationalHealth: { score: number; grade: string; weight: number }; // 15%
  };
  criticalIssues: {
    phiViolations: number;
    patientSafetyIncidents: number;
    complianceViolations: number;
    unresolvedCriticalAlerts: number;
  };
  boardMetrics: {
    totalSystems: number;
    auditReady: boolean;
    averageResponseTime: number | null; // in seconds
    beaconTiers: {
      verified: number;
      certified: number;
      trusted: number;
    };
  };
  trend: "improving" | "stable" | "declining";
  recommendations: string[];
}

// Healthcare-specific scoring weights
const SCORING_WEIGHTS = {
  PHI_PROTECTION: 0.35,      // 35% - Defensive healthcare positioning
  CLINICAL_SAFETY: 0.25,     // 25% - Patient safety focus
  REGULATORY_COMPLIANCE: 0.25, // 25% - Audit readiness
  OPERATIONAL_HEALTH: 0.15,  // 15% - Operational excellence
};

/**
 * Calculate operational health score (alerts, response times, verification)
 */
async function calculateOperationalHealth(healthSystemId: string): Promise<{
  score: number;
  unresolvedCriticalAlerts: number;
  averageResponseTime: number | null;
}> {
  try {
    const systems = await storage.getAISystems(healthSystemId);

    if (systems.length === 0) {
      return { score: 100, unresolvedCriticalAlerts: 0, averageResponseTime: null };
    }

    // 1. Alert Response (50% of operational health)
    const allAlerts = await storage.getUnresolvedAlerts(healthSystemId);
    const unresolvedCriticalAlerts = allAlerts.filter((a: any) => a.severity === 'critical').length;
    const unresolvedHighAlerts = allAlerts.filter((a: any) => a.severity === 'high').length;

    // Penalty for unresolved alerts (critical = -15 points, high = -7 points, medium = -3 points)
    const alertPenalty = (unresolvedCriticalAlerts * 15) + 
                        (unresolvedHighAlerts * 7) + 
                        (allAlerts.filter((a: any) => a.severity === 'medium').length * 3);
    const alertScore = Math.max(0, 100 - alertPenalty);

    // 2. System Verification (30% of operational health)
    const beaconVerified = systems.filter((s: any) => s.verificationTier === 'Verified').length;
    const beaconCertified = systems.filter((s: any) => s.verificationTier === 'Certified').length;
    const beaconTrusted = systems.filter((s: any) => s.verificationTier === 'Trusted').length;
    const totalBeaconSystems = beaconVerified + beaconCertified + beaconTrusted;
    const verificationScore = totalBeaconSystems > 0 
      ? ((beaconVerified * 70 + beaconCertified * 90 + beaconTrusted * 100) / totalBeaconSystems)
      : 50; // Default to 50 if no systems verified

    // 3. Response Time (20% of operational health)
    // Get all resolved alerts from all systems to calculate average response time
    const allAlertsPromises = systems.map((s: any) => storage.getAlerts(s.id));
    const alertArrays = await Promise.all(allAlertsPromises);
    const allAlertsForSystems = alertArrays.flat();

    const resolvedAlertsWithTime = allAlertsForSystems.filter(
      (a: any) => a.resolved && a.responseTimeSeconds !== null
    );

    let averageResponseTime: number | null = null;
    let responseScore = 50; // Default

    if (resolvedAlertsWithTime.length > 0) {
      const totalSeconds = resolvedAlertsWithTime.reduce(
        (sum: number, a: any) => sum + (a.responseTimeSeconds || 0), 
        0
      );
      averageResponseTime = Math.round(totalSeconds / resolvedAlertsWithTime.length);

      // Score based on response time (excellent < 2 min, good < 5 min, acceptable < 10 min)
      if (averageResponseTime < 120) responseScore = 100; // < 2 minutes = perfect
      else if (averageResponseTime < 300) responseScore = 85; // < 5 minutes = good
      else if (averageResponseTime < 600) responseScore = 70; // < 10 minutes = acceptable
      else responseScore = 50; // > 10 minutes = needs improvement
    }

    // Weighted operational score
    const operationalScore = Math.round(
      alertScore * 0.50 +
      verificationScore * 0.30 +
      responseScore * 0.20
    );

    return {
      score: operationalScore,
      unresolvedCriticalAlerts,
      averageResponseTime,
    };
  } catch (error) {
    logger.error({ error, healthSystemId }, "Failed to calculate operational health");
    return { score: 50, unresolvedCriticalAlerts: 0, averageResponseTime: null };
  }
}

/**
 * Calculate comprehensive healthcare portfolio score
 */
export async function calculateHealthcarePortfolioScore(
  healthSystemId: string
): Promise<HealthcarePortfolioScore> {
  try {
    const systems = await storage.getAISystems(healthSystemId);

    // Calculate all four components in parallel
    const [phiRisk, clinicalSafety, complianceResult, operationalHealth] = await Promise.all([
      calculatePortfolioPHIScore(healthSystemId),
      calculatePortfolioClinicalSafety(healthSystemId),
      calculatePortfolioCompliance(healthSystemId),
      calculateOperationalHealth(healthSystemId),
    ]);

    // Calculate weighted overall score
    const overall = Math.round(
      phiRisk.score * SCORING_WEIGHTS.PHI_PROTECTION +
      clinicalSafety.score * SCORING_WEIGHTS.CLINICAL_SAFETY +
      complianceResult.overall * SCORING_WEIGHTS.REGULATORY_COMPLIANCE +
      operationalHealth.score * SCORING_WEIGHTS.OPERATIONAL_HEALTH
    );

    // Generate overall grade
    let grade: "A" | "B" | "C" | "D" | "F";
    if (overall >= 90) grade = "A";
    else if (overall >= 80) grade = "B";
    else if (overall >= 70) grade = "C";
    else if (overall >= 60) grade = "D";
    else grade = "F";

    // Helper for component grades
    const gradeForScore = (score: number): string => {
      if (score >= 90) return "A";
      if (score >= 80) return "B";
      if (score >= 70) return "C";
      if (score >= 60) return "D";
      return "F";
    };

    // Critical issues summary
    const criticalIssues = {
      phiViolations: phiRisk.criticalPHIIssues,
      patientSafetyIncidents: clinicalSafety.criticalSafetyIssues,
      complianceViolations: complianceResult.criticalViolations,
      unresolvedCriticalAlerts: operationalHealth.unresolvedCriticalAlerts,
    };

    // Beacon tier distribution
    const beaconTiers = {
      verified: systems.filter((s: any) => s.verificationTier === 'Verified').length,
      certified: systems.filter((s: any) => s.verificationTier === 'Certified').length,
      trusted: systems.filter((s: any) => s.verificationTier === 'Trusted').length,
    };

    // Board-level metrics
    const boardMetrics = {
      totalSystems: systems.length,
      auditReady: complianceResult.auditReady,
      averageResponseTime: operationalHealth.averageResponseTime,
      beaconTiers,
    };

    // Calculate trend (simplified - compare to target scores)
    // In production, this would compare to historical data
    const targetScore = 90; // A- grade target
    let trend: "improving" | "stable" | "declining";
    if (overall >= targetScore) trend = "improving";
    else if (overall >= targetScore - 5) trend = "stable";
    else trend = "declining";

    // Generate recommendations
    const recommendations: string[] = [];
    if (phiRisk.score < 80) {
      recommendations.push(`PHI Protection needs improvement (${phiRisk.score}/100) - ${phiRisk.criticalPHIIssues} critical violations`);
    }
    if (clinicalSafety.score < 80) {
      recommendations.push(`Clinical Safety below target (${clinicalSafety.score}/100) - review bias and accuracy metrics`);
    }
    if (complianceResult.overall < 85) {
      recommendations.push(`Regulatory Compliance needs attention (${complianceResult.overall}/100) - ${complianceResult.criticalViolations} violations`);
    }
    if (operationalHealth.score < 80) {
      recommendations.push(`Operational Health needs improvement (${operationalHealth.score}/100) - focus on alert response`);
    }
    if (criticalIssues.unresolvedCriticalAlerts > 0) {
      recommendations.push(`URGENT: ${criticalIssues.unresolvedCriticalAlerts} unresolved critical alerts require immediate attention`);
    }
    if (criticalIssues.patientSafetyIncidents > 0) {
      recommendations.push(`CRITICAL: ${criticalIssues.patientSafetyIncidents} patient safety incidents detected - escalate to clinical leadership`);
    }
    if (!boardMetrics.auditReady) {
      recommendations.push("Portfolio not audit-ready - address compliance violations before regulatory review");
    }

    return {
      overall,
      grade,
      breakdown: {
        phiProtection: {
          score: phiRisk.score,
          grade: gradeForScore(phiRisk.score),
          weight: SCORING_WEIGHTS.PHI_PROTECTION,
        },
        clinicalSafety: {
          score: clinicalSafety.score,
          grade: gradeForScore(clinicalSafety.score),
          weight: SCORING_WEIGHTS.CLINICAL_SAFETY,
        },
        regulatoryCompliance: {
          score: complianceResult.overall,
          grade: gradeForScore(complianceResult.overall),
          weight: SCORING_WEIGHTS.REGULATORY_COMPLIANCE,
        },
        operationalHealth: {
          score: operationalHealth.score,
          grade: gradeForScore(operationalHealth.score),
          weight: SCORING_WEIGHTS.OPERATIONAL_HEALTH,
        },
      },
      criticalIssues,
      boardMetrics,
      trend,
      recommendations,
    };
  } catch (error) {
    logger.error({ error, healthSystemId }, "Failed to calculate healthcare portfolio score");
    throw error;
  }
}

/**
 * Get response time metrics (for "2-minute rollback" claims)
 */
export async function getResponseTimeMetrics(healthSystemId: string): Promise<{
  averageSeconds: number | null;
  medianSeconds: number | null;
  p95Seconds: number | null;
  totalResolved: number;
  under2Minutes: number;
  under5Minutes: number;
  over10Minutes: number;
}> {
  try {
    const systems = await storage.getAISystems(healthSystemId);

    // Get all resolved alerts with response times
    const allAlertsPromises = systems.map((s: any) => storage.getAlerts(s.id));
    const alertArrays = await Promise.all(allAlertsPromises);
    const allAlerts = alertArrays.flat();

    const resolvedAlertsWithTime = allAlerts.filter(
      (a: any) => a.resolved && a.responseTimeSeconds !== null
    );

    if (resolvedAlertsWithTime.length === 0) {
      return {
        averageSeconds: null,
        medianSeconds: null,
        p95Seconds: null,
        totalResolved: 0,
        under2Minutes: 0,
        under5Minutes: 0,
        over10Minutes: 0,
      };
    }

    // Sort response times
    const responseTimes = resolvedAlertsWithTime
      .map((a: any) => a.responseTimeSeconds!)
      .sort((a: number, b: number) => a - b);

    // Calculate metrics
    const average = Math.round(
      responseTimes.reduce((sum: number, t: number) => sum + t, 0) / responseTimes.length
    );

    const median = responseTimes[Math.floor(responseTimes.length / 2)];
    const p95Index = Math.floor(responseTimes.length * 0.95);
    const p95 = responseTimes[p95Index];

    // Count by time buckets
    const under2Minutes = responseTimes.filter((t: number) => t < 120).length;
    const under5Minutes = responseTimes.filter((t: number) => t < 300).length;
    const over10Minutes = responseTimes.filter((t: number) => t >= 600).length;

    return {
      averageSeconds: average,
      medianSeconds: median,
      p95Seconds: p95,
      totalResolved: resolvedAlertsWithTime.length,
      under2Minutes,
      under5Minutes,
      over10Minutes,
    };
  } catch (error) {
    logger.error({ error, healthSystemId }, "Failed to calculate response time metrics");
    return {
      averageSeconds: null,
      medianSeconds: null,
      p95Seconds: null,
      totalResolved: 0,
      under2Minutes: 0,
      under5Minutes: 0,
      over10Minutes: 0,
    };
  }
}

export const healthcareAnalyticsService = {
  calculateHealthcarePortfolioScore,
  getResponseTimeMetrics,
};

server/services/scoring/telemetry-valida.content:
/**
 * Telemetry Validation Service
 * 
 * CRITICAL FOR ACQUISITION DUE DILIGENCE
 * Ensures scoring data is fresh and complete to prevent artificially inflated grades.
 * 
 * Validates:
 * - Telemetry data exists and is recent (< 7 days stale)
 * - Minimum event thresholds met for statistical validity
 * - No duplicate severity weighting
 * - Audit logging for all score calculations
 */

import { logger } from "../../logger";

export interface TelemetryValidationResult {
  isValid: boolean;
  isFresh: boolean;
  age: number; // Age of most recent telemetry in hours
  eventCount: number;
  warnings: string[];
  errors: string[];
}

export interface ScoringAuditLog {
  timestamp: Date;
  healthSystemId?: string;
  aiSystemId?: string;
  scoringType: string; // 'phi-risk' | 'clinical-safety' | 'compliance' | 'operational'
  telemetryAge: number; // hours
  eventCount: number;
  score: number;
  dataQuality: 'fresh' | 'stale' | 'missing';
  warnings: string[];
}

// Data freshness thresholds
const TELEMETRY_FRESHNESS = {
  FRESH: 24 * 60 * 60 * 1000,        // 24 hours
  ACCEPTABLE: 7 * 24 * 60 * 60 * 1000, // 7 days
  STALE: 30 * 24 * 60 * 60 * 1000,   // 30 days
};

// Minimum event thresholds for statistical validity
const MIN_EVENTS = {
  PHI_RISK: 5,         // Need at least 5 events for PHI scoring
  CLINICAL_SAFETY: 10, // Need at least 10 events for clinical safety
  COMPLIANCE: 3,       // Need at least 3 events for compliance
};

/**
 * Validate telemetry data freshness and completeness
 */
export function validateTelemetry(
  events: any[],
  scoringType: 'phi-risk' | 'clinical-safety' | 'compliance' | 'operational'
): TelemetryValidationResult {
  const warnings: string[] = [];
  const errors: string[] = [];
  let isValid = true;
  let isFresh = true;

  // Check if we have any events
  if (events.length === 0) {
    errors.push('No telemetry events found');
    return {
      isValid: false,
      isFresh: false,
      age: Infinity,
      eventCount: 0,
      warnings: [],
      errors,
    };
  }

  // Find most recent event
  const mostRecentEvent = events.reduce((latest: any, event: any) => {
    const eventTime = new Date(event.timestamp).getTime();
    const latestTime = latest ? new Date(latest.timestamp).getTime() : 0;
    return eventTime > latestTime ? event : latest;
  }, null);

  const mostRecentTime = mostRecentEvent ? new Date(mostRecentEvent.timestamp).getTime() : 0;
  const age = (Date.now() - mostRecentTime) / (60 * 60 * 1000); // in hours
  const ageInDays = Math.round(age / 24);

  // Check data freshness
  const ageInMs = Date.now() - mostRecentTime;
  if (ageInMs > TELEMETRY_FRESHNESS.STALE) {
    errors.push(`Telemetry is stale (${ageInDays} days old) - scores may be inaccurate`);
    isValid = false;
    isFresh = false;
  } else if (ageInMs > TELEMETRY_FRESHNESS.ACCEPTABLE) {
    warnings.push(`Telemetry is aging (${ageInDays} days old) - recommend data refresh`);
    isFresh = false;
  } else if (ageInMs > TELEMETRY_FRESHNESS.FRESH) {
    warnings.push(`Telemetry is ${Math.round(age)} hours old`);
    isFresh = false;
  }

  // Check minimum event thresholds
  const minThreshold = scoringType === 'phi-risk' ? MIN_EVENTS.PHI_RISK :
                      scoringType === 'clinical-safety' ? MIN_EVENTS.CLINICAL_SAFETY :
                      scoringType === 'compliance' ? MIN_EVENTS.COMPLIANCE : 1;

  if (events.length < minThreshold) {
    warnings.push(
      `Insufficient events (${events.length} < ${minThreshold}) - ` +
      `statistical validity may be compromised`
    );
  }

  // Check for duplicate event IDs (prevents double-counting severity)
  const eventIds = events.map((e: any) => e.id).filter(Boolean);
  const uniqueEventIds = new Set(eventIds);
  if (eventIds.length !== uniqueEventIds.size) {
    const duplicateCount = eventIds.length - uniqueEventIds.size;
    errors.push(`Found ${duplicateCount} duplicate events - severity may be inflated`);
    isValid = false;
  }

  return {
    isValid,
    isFresh,
    age,
    eventCount: events.length,
    warnings,
    errors,
  };
}

/**
 * Log scoring audit trail with telemetry quality metadata
 */
export function logScoringAudit(audit: ScoringAuditLog): void {
  logger.info({
    scoringAudit: {
      timestamp: audit.timestamp.toISOString(),
      healthSystemId: audit.healthSystemId,
      aiSystemId: audit.aiSystemId,
      scoringType: audit.scoringType,
      score: audit.score,
      telemetryAge: `${Math.round(audit.telemetryAge)}h`,
      eventCount: audit.eventCount,
      dataQuality: audit.dataQuality,
      warnings: audit.warnings,
    }
  }, `Scoring calculation: ${audit.scoringType} = ${audit.score}`);
}

/**
 * Determine data quality level based on validation result
 */
export function getDataQuality(validation: TelemetryValidationResult): 'fresh' | 'stale' | 'missing' {
  if (!validation.isValid || validation.eventCount === 0) return 'missing';
  if (!validation.isFresh) return 'stale';
  return 'fresh';
}

/**
 * Calculate confidence modifier based on data quality
 * Returns a multiplier (0.0 - 1.0) to apply to the score
 */
export function getConfidenceModifier(dataQuality: 'fresh' | 'stale' | 'missing'): number {
  switch (dataQuality) {
    case 'fresh': return 1.0;   // 100% confidence
    case 'stale': return 0.85;  // 85% confidence - penalize stale data
    case 'missing': return 0.5; // 50% confidence - significant penalty
  }
}

