];

/**
 * Run migration to seed database with encrypted policies
 */
export async function migratePolicies(seedUserId: string): Promise<void> {
  logger.info({ policyCount: STATIC_POLICIES.length }, 'Starting policy migration');

  const seededCount = await policyVersioningService.seedPoliciesFromStaticMapping(
    STATIC_POLICIES,
    seedUserId
  );

  logger.info({ seededCount, total: STATIC_POLICIES.length }, '‚úÖ Policy migration complete');
}

/**
 * Validate that all critical event types have policies
 */
export async function validatePolicyCompleteness(): Promise<boolean> {
  const criticalEventTypes = [
    'phi_exposure',
    'bias_detected',
    'model_drift',
    'unauthorized_data_access',
    'clinical_accuracy_failure'
  ];

  const frameworks = ['HIPAA', 'NIST_AI_RMF', 'FDA_SaMD'];
  let missingPolicies = 0;

  for (const eventType of criticalEventTypes) {
    for (const framework of frameworks) {
      const policy = await policyVersioningService.getActivePolicy(eventType, framework);
      if (!policy) {
        logger.warn({ eventType, framework }, 'Missing policy');
        missingPolicies++;
      }
    }
  }

  if (missingPolicies > 0) {
    logger.warn({ missingPolicies }, 'Policy completeness check failed');
    return false;
  }

  logger.info('‚úÖ All critical policies present');
  return true;
}

server/routes/policy-admin.ts.content:
/**
 * üîí POLICY ADMINISTRATION API
 * 
 * Admin endpoints for managing encrypted compliance policies.
 * Restricted to admin users only.
 */

import type { Express } from 'express';
import { migratePolicies, validatePolicyCompleteness } from '../services/translation-engine/policy-migration';
import { policyLoader } from '../services/translation-engine/policy-loader';
import { storage } from '../storage';
import { logger } from '../logger';

export function registerPolicyAdminRoutes(app: Express) {
  /**
   * POST /api/admin/policies/migrate
   * 
   * Migrate static policies to encrypted database storage (one-time setup)
   */
  app.post('/api/admin/policies/migrate', async (req, res) => {
    if (!req.session.userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user || user.permissions !== 'admin') {
      return res.status(403).json({ error: 'Admin access required' });
    }

    try {
      logger.info({ userId: user.id }, 'Starting policy migration');

      await migratePolicies(user.id);

      // Clear cache to force reload of new policies
      policyLoader.clearCache();

      logger.info('Policy migration completed successfully');

      res.json({
        success: true,
        message: 'Policies migrated successfully - IP moat activated'
      });
    } catch (error: any) {
      logger.error({ error }, 'Policy migration failed');
      res.status(500).json({ 
        error: 'Failed to migrate policies',
        details: error.message
      });
    }
  });

  /**
   * GET /api/admin/policies/validate
   * 
   * Validate that all critical policies are present
   */
  app.get('/api/admin/policies/validate', async (req, res) => {
    if (!req.session.userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user || user.permissions !== 'admin') {
      return res.status(403).json({ error: 'Admin access required' });
    }

    try {
      const isComplete = await validatePolicyCompleteness();

      res.json({
        success: true,
        complete: isComplete,
        message: isComplete 
          ? 'All critical policies are present'
          : 'Some critical policies are missing - run migration'
      });
    } catch (error: any) {
      logger.error({ error }, 'Policy validation failed');
      res.status(500).json({ 
        error: 'Failed to validate policies',
        details: error.message
      });
    }
  });

  /**
   * POST /api/admin/policies/warm-cache
   * 
   * Preload common policies into cache for performance
   */
  app.post('/api/admin/policies/warm-cache', async (req, res) => {
    if (!req.session.userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user || user.permissions !== 'admin') {
      return res.status(403).json({ error: 'Admin access required' });
    }

    try {
      const eventTypes = [
        'phi_exposure',
        'bias_detected',
        'model_drift',
        'unauthorized_data_access',
        'clinical_accuracy_failure'
      ];

      await policyLoader.warmCache(eventTypes);

      res.json({
        success: true,
        message: 'Policy cache warmed successfully'
      });
    } catch (error: any) {
      logger.error({ error }, 'Cache warming failed');
      res.status(500).json({ 
        error: 'Failed to warm cache',
        details: error.message
      });
    }
  });

  /**
   * DELETE /api/admin/policies/clear-cache
   * 
   * Clear policy cache (forces reload from database)
   */
  app.delete('/api/admin/policies/clear-cache', async (req, res) => {
    if (!req.session.userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user || user.permissions !== 'admin') {
      return res.status(403).json({ error: 'Admin access required' });
    }

    try {
      policyLoader.clearCache();

      res.json({
        success: true,
        message: 'Policy cache cleared'
      });
    } catch (error: any) {
      logger.error({ error }, 'Cache clearing failed');
      res.status(500).json({ 
        error: 'Failed to clear cache',
        details: error.message
      });
    }
  });
}

server/services/translation-engine/polic.content:
/**
 * üîí POLICY LOADER - Translation Engine IP Moat Integration
 * 
 * Loads encrypted compliance policies from database with caching.
 * Bridges PolicyVersioningService ‚Üî ComplianceMapping runtime.
 * 
 * CRITICAL FOR M&A: Makes static TypeScript rules into encrypted, versioned database policies.
 */

import { policyVersioningService, type PolicyRuleLogic } from '../policy-versioning-service';
import { logger } from '../../logger';

interface CachedPolicy {
  policy: PolicyRuleLogic;
  lastLoaded: number;
}

interface PolicyCache {
  [eventType: string]: {
    [framework: string]: CachedPolicy;
  };
}

export class PolicyLoader {
  private cache: PolicyCache = {};
  private readonly CACHE_TTL = 5 * 60 * 1000; // 5 minutes

  /**
   * Get policy for specific event type and framework (with caching)
   */
  async getPolicy(eventType: string, framework: string): Promise<PolicyRuleLogic | null> {
    // Check cache first
    const cached = this.cache[eventType]?.[framework];
    if (cached && Date.now() - cached.lastLoaded < this.CACHE_TTL) {
      return cached.policy;
    }

    try {
      // Load from database
      const policy = await policyVersioningService.getActivePolicy(eventType, framework);

      if (!policy) {
        logger.warn({ eventType, framework }, 'No active policy found - falling back to static rules');
        return null;
      }

      // Cache the result
      if (!this.cache[eventType]) {
        this.cache[eventType] = {};
      }
      this.cache[eventType][framework] = {
        policy,
        lastLoaded: Date.now()
      };

      logger.debug({ eventType, framework }, 'Policy loaded from database');
      return policy;
    } catch (error) {
      logger.error({ error, eventType, framework }, 'Failed to load policy - falling back to static rules');
      return null;
    }
  }

  /**
   * Get all framework policies for an event type
   */
  async getPoliciesForEvent(eventType: string): Promise<Map<string, PolicyRuleLogic>> {
    const frameworks = ['HIPAA', 'NIST_AI_RMF', 'FDA_SaMD', 'ISO_42001'];
    const policies = new Map<string, PolicyRuleLogic>();

    for (const framework of frameworks) {
      const policy = await this.getPolicy(eventType, framework);
      if (policy) {
        policies.set(framework, policy);
      }
    }

    return policies;
  }

  /**
   * Clear cache (for testing or after policy updates)
   */
  clearCache() {
    this.cache = {};
    logger.info('Policy cache cleared');
  }

  /**
   * Warm cache by preloading common policies
   */
  async warmCache(eventTypes: string[]) {
    logger.info({ eventTypes }, 'Warming policy cache');

    const frameworks = ['HIPAA', 'NIST_AI_RMF', 'FDA_SaMD'];
    const promises = [];

    for (const eventType of eventTypes) {
      for (const framework of frameworks) {
        promises.push(this.getPolicy(eventType, framework));
      }
    }

    await Promise.all(promises);
    logger.info(`Policy cache warmed: ${promises.length} policies loaded`);
  }
}

export const policyLoader = new PolicyLoader();

server/routes/billing.ts.content:
/**
 * üí∞ BILLING API ROUTES
 * 
 * Comprehensive Stripe integration for revenue collection:
 * - Subscription creation (health systems + vendors)
 * - Usage limit enforcement
 * - Webhook processing
 * - Customer portal access
 */

import { Router } from 'express';
import { z } from 'zod';
import Stripe from 'stripe';
import { stripeBillingService, HEALTH_SYSTEM_PRICING, VENDOR_PRICING } from '../services/stripe-billing';
import { storage } from '../storage';
import { logger } from '../logger';
import { requireAuth } from '../middleware/auth';

const router = Router();

if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error('STRIPE_SECRET_KEY is required for billing routes');
}

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY);

// Validation schemas
const createHealthSystemSubscriptionSchema = z.object({
  tier: z.enum(['starter', 'professional', 'enterprise']),
});

const createVendorSubscriptionSchema = z.object({
  certificationTier: z.enum(['verified', 'certified', 'trusted']),
});

/**
 * GET /api/billing/pricing
 * Get pricing information for all tiers
 */
router.get('/pricing', (req, res) => {
  res.json({
    healthSystems: HEALTH_SYSTEM_PRICING,
    vendors: VENDOR_PRICING,
  });
});

/**
 * POST /api/billing/subscriptions/health-system
 * Create subscription for health system
 */
router.post('/subscriptions/health-system', requireAuth, async (req, res) => {
  try {
    const user = req.user!;

    if (!user.healthSystemId) {
      return res.status(403).json({ error: 'Only health system users can create health system subscriptions' });
    }

    const { tier } = createHealthSystemSubscriptionSchema.parse(req.body);

    const result = await stripeBillingService.createHealthSystemSubscription(
      user.healthSystemId,
      tier
    );

    logger.info({ healthSystemId: user.healthSystemId, tier }, 'Health system subscription created');

    res.json(result);
  } catch (error) {
    logger.error({ error }, 'Failed to create health system subscription');
    res.status(500).json({ error: 'Failed to create subscription' });
  }
});

/**
 * POST /api/billing/subscriptions/vendor
 * Create subscription for vendor
 */
router.post('/subscriptions/vendor', requireAuth, async (req, res) => {
  try {
    const user = req.user!;

    if (!user.vendorId) {
      return res.status(403).json({ error: 'Only vendor users can create vendor subscriptions' });
    }

    const { certificationTier } = createVendorSubscriptionSchema.parse(req.body);

    const result = await stripeBillingService.createVendorSubscription(
      user.vendorId,
      certificationTier
    );

    logger.info({ vendorId: user.vendorId, certificationTier }, 'Vendor subscription created');

    res.json(result);
  } catch (error) {
    logger.error({ error }, 'Failed to create vendor subscription');
    res.status(500).json({ error: 'Failed to create subscription' });
  }
});

/**
 * GET /api/billing/subscription/status
 * Get current subscription status
 */
router.get('/subscription/status', requireAuth, async (req, res) => {
  try {
    const user = req.user!;

    if (user.healthSystemId) {
      const status = await stripeBillingService.getHealthSystemSubscriptionStatus(user.healthSystemId);
      return res.json({ type: 'health_system', ...status });
    }

    if (user.vendorId) {
      const status = await stripeBillingService.getVendorSubscriptionStatus(user.vendorId);
      return res.json({ type: 'vendor', ...status });
    }

    res.json({ type: 'none', status: 'no_subscription' });
  } catch (error) {
    logger.error({ error }, 'Failed to get subscription status');
    res.status(500).json({ error: 'Failed to get subscription status' });
  }
});

/**
 * GET /api/billing/usage/ai-systems
 * Check usage limits for AI systems
 */
router.get('/usage/ai-systems', requireAuth, async (req, res) => {
  try {
    const user = req.user!;

    if (!user.healthSystemId) {
      return res.status(403).json({ error: 'Only health system users can check AI system usage' });
    }

    const usage = await stripeBillingService.canAddAISystem(user.healthSystemId);

    res.json(usage);
  } catch (error) {
    logger.error({ error }, 'Failed to check AI system usage');
    res.status(500).json({ error: 'Failed to check usage' });
  }
});

/**
 * POST /api/billing/cancel-subscription
 * Cancel active subscription
 */
router.post('/cancel-subscription', requireAuth, async (req, res) => {
  try {
    const user = req.user!;

    let subscriptionId: string | null = null;

    if (user.healthSystemId) {
      const healthSystem = await storage.getHealthSystem(user.healthSystemId);
      subscriptionId = healthSystem?.stripeSubscriptionId || null;
    } else if (user.vendorId) {
      const vendor = await storage.getVendor(user.vendorId);
      subscriptionId = vendor?.stripeSubscriptionId || null;
    }

    if (!subscriptionId) {
      return res.status(404).json({ error: 'No active subscription found' });
    }

    await stripeBillingService.cancelSubscription(subscriptionId);

    logger.info({ subscriptionId, userId: user.id }, 'Subscription canceled');

    res.json({ success: true, message: 'Subscription canceled successfully' });
  } catch (error) {
    logger.error({ error }, 'Failed to cancel subscription');
    res.status(500).json({ error: 'Failed to cancel subscription' });
  }
});

/**
 * POST /api/billing/webhooks/stripe
 * Stripe webhook endpoint for subscription events
 */
router.post('/webhooks/stripe', async (req, res) => {
  const sig = req.headers['stripe-signature'];

  if (!sig) {
    logger.error('Missing Stripe signature');
    return res.status(400).json({ error: 'Missing signature' });
  }

  let event: Stripe.Event;

  try {
    // Verify webhook signature (requires raw body)
    event = stripe.webhooks.constructEvent(
      req.body,
      sig,
      process.env.STRIPE_WEBHOOK_SECRET || ''
    );
  } catch (error) {
    logger.error({ error }, 'Stripe webhook signature verification failed');
    return res.status(400).json({ error: 'Invalid signature' });
  }

  try {
    // Handle different event types
    switch (event.type) {
      case 'customer.subscription.created':
      case 'customer.subscription.updated':
        const subscription = event.data.object as Stripe.Subscription;
        await stripeBillingService.handleSubscriptionUpdated(subscription);
        logger.info({ subscriptionId: subscription.id, eventType: event.type }, 'Subscription event processed');
        break;

      case 'customer.subscription.deleted':
        const deletedSubscription = event.data.object as Stripe.Subscription;
        await stripeBillingService.handleSubscriptionUpdated(deletedSubscription);
        logger.info({ subscriptionId: deletedSubscription.id }, 'Subscription deleted');
        break;

      case 'invoice.payment_succeeded':
        const invoice = event.data.object as Stripe.Invoice;
        logger.info({ invoiceId: invoice.id, customerId: invoice.customer }, 'Payment succeeded');
        break;

      case 'invoice.payment_failed':
        const failedInvoice = event.data.object as Stripe.Invoice;
        logger.error({ invoiceId: failedInvoice.id, customerId: failedInvoice.customer }, 'Payment failed');
        break;

      default:
        logger.info({ eventType: event.type }, 'Unhandled Stripe webhook event');
    }

    res.json({ received: true });
  } catch (error) {
    logger.error({ error, eventType: event.type }, 'Failed to process Stripe webhook');
    res.status(500).json({ error: 'Webhook processing failed' });
  }
});

export default router;

