  },
];

/**
 * Combined Dataset
 */
export const clinicalValidationDataset: ClinicalTestCase[] = [
  ...cardiologyDataset,
  ...endocrinologyDataset,
  ...infectiousDiseaseDataset,
  ...neurologyDataset,
  ...emergencyMedicineDataset,
  ...pediatricsDataset,
];

/**
 * Get dataset by specialty
 */
export function getDatasetBySpecialty(specialty: string): ClinicalTestCase[] {
  return clinicalValidationDataset.filter(tc => tc.specialty === specialty);
}

/**
 * Get dataset by difficulty
 */
export function getDatasetByDifficulty(difficulty: 'basic' | 'intermediate' | 'advanced'): ClinicalTestCase[] {
  return clinicalValidationDataset.filter(tc => tc.difficulty === difficulty);
}

/**
 * Get dataset by urgency
 */
export function getDatasetByUrgency(urgency: 'routine' | 'urgent' | 'emergency'): ClinicalTestCase[] {
  return clinicalValidationDataset.filter(tc => tc.groundTruth.urgency === urgency);
}

/**
 * Get random sample for testing
 */
export function getRandomSample(count: number): ClinicalTestCase[] {
  const shuffled = [...clinicalValidationDataset].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, Math.min(count, shuffled.length));
}

server/services/langsmith-client.ts.content:
/**
 * LangSmith API Client
 * 
 * Provides direct API access to LangSmith for polling telemetry data
 * Complements webhook-based ingestion with on-demand retrieval
 * 
 * Documentation: https://docs.smith.langchain.com/api-reference
 */

import { logger } from '../logger';

export interface LangSmithConfig {
  apiKey: string;
  baseUrl?: string;
}

export interface LangSmithRun {
  id: string;
  name: string;
  run_type: 'llm' | 'chain' | 'tool' | 'retriever' | 'prompt';
  start_time: string;
  end_time?: string;
  error?: string;
  inputs: Record<string, any>;
  outputs?: Record<string, any>;
  extra?: Record<string, any>;
  trace_id: string;
  dotted_order: string;
  parent_run_id?: string;
  session_id?: string;
  feedback_stats?: Record<string, any>;
  tags?: string[];
  metadata?: Record<string, any>;
}

export interface LangSmithProject {
  id: string;
  name: string;
  description?: string;
  created_at: string;
  run_count?: number;
  metadata?: Record<string, any>;
}

export interface LangSmithFeedback {
  id: string;
  run_id: string;
  key: string;
  score?: number;
  value?: any;
  comment?: string;
  correction?: Record<string, any>;
  created_at: string;
}

export interface ListRunsOptions {
  projectName?: string;
  projectId?: string;
  runType?: 'llm' | 'chain' | 'tool' | 'retriever' | 'prompt';
  startTime?: Date;
  endTime?: Date;
  limit?: number;
  offset?: number;
  hasError?: boolean;
  tags?: string[];
}

export interface PollMetricsResult {
  runs: LangSmithRun[];
  feedback: LangSmithFeedback[];
  totalRuns: number;
  errorRate: number;
  averageLatency: number;
  polledAt: Date;
}

export class LangSmithClient {
  private apiKey: string;
  private baseUrl: string;

  constructor(config: LangSmithConfig) {
    this.apiKey = config.apiKey;
    this.baseUrl = config.baseUrl || 'https://api.smith.langchain.com';
  }

  /**
   * List projects (datasets/sessions)
   */
  async listProjects(limit: number = 100): Promise<LangSmithProject[]> {
    try {
      const response = await this.makeRequest(`/projects?limit=${limit}`);
      return response;
    } catch (error) {
      logger.error({ err: error }, 'Failed to list LangSmith projects');
      throw error;
    }
  }

  /**
   * Get runs (traces) for a project
   */
  async listRuns(options: ListRunsOptions = {}): Promise<LangSmithRun[]> {
    try {
      const params = new URLSearchParams();

      if (options.projectName) params.append('session', options.projectName);
      if (options.projectId) params.append('session_id', options.projectId);
      if (options.runType) params.append('run_type', options.runType);
      if (options.startTime) params.append('start_time', options.startTime.toISOString());
      if (options.endTime) params.append('end_time', options.endTime.toISOString());
      if (options.limit) params.append('limit', options.limit.toString());
      if (options.offset) params.append('offset', options.offset.toString());
      if (options.hasError !== undefined) params.append('has_error', options.hasError.toString());
      if (options.tags && options.tags.length > 0) {
        options.tags.forEach(tag => params.append('tag', tag));
      }

      const response = await this.makeRequest(`/runs?${params.toString()}`);
      return response;
    } catch (error) {
      logger.error({ err: error, options }, 'Failed to list LangSmith runs');
      throw error;
    }
  }

  /**
   * Get a specific run by ID
   */
  async getRun(runId: string): Promise<LangSmithRun> {
    try {
      const response = await this.makeRequest(`/runs/${runId}`);
      return response;
    } catch (error) {
      logger.error({ err: error, runId }, 'Failed to get LangSmith run');
      throw error;
    }
  }

  /**
   * Get feedback for a run
   */
  async getFeedback(runId: string): Promise<LangSmithFeedback[]> {
    try {
      const response = await this.makeRequest(`/runs/${runId}/feedback`);
      return response;
    } catch (error) {
      logger.error({ err: error, runId }, 'Failed to get LangSmith feedback');
      throw error;
    }
  }

  /**
   * Poll for recent telemetry data and compute metrics
   * 
   * @param projectName The LangSmith project/session to poll
   * @param lookbackMinutes How many minutes of data to retrieve (default: 15)
   * @returns Aggregated metrics and raw data
   */
  async pollMetrics(
    projectName: string,
    lookbackMinutes: number = 15
  ): Promise<PollMetricsResult> {
    const startTime = new Date(Date.now() - lookbackMinutes * 60 * 1000);
    const endTime = new Date();

    logger.info({ projectName, lookbackMinutes, startTime, endTime }, 'Polling LangSmith metrics');

    try {
      // Fetch runs from the lookback window
      const runs = await this.listRuns({
        projectName,
        startTime,
        endTime,
        limit: 1000, // Adjust based on expected volume
      });

      // Fetch feedback for runs
      const feedbackPromises = runs
        .slice(0, 100) // Limit to 100 most recent runs to avoid rate limits
        .map(run => this.getFeedback(run.id).catch(() => []));

      const feedbackArrays = await Promise.all(feedbackPromises);
      const feedback = feedbackArrays.flat();

      // Calculate metrics
      const totalRuns = runs.length;
      const errorRuns = runs.filter(r => r.error !== null && r.error !== undefined).length;
      const errorRate = totalRuns > 0 ? (errorRuns / totalRuns) * 100 : 0;

      // Calculate average latency (only for completed runs)
      const completedRuns = runs.filter(r => r.end_time && r.start_time);
      const latencies = completedRuns.map(r => {
        const start = new Date(r.start_time).getTime();
        const end = new Date(r.end_time!).getTime();
        return end - start;
      });
      const averageLatency = latencies.length > 0
        ? latencies.reduce((a, b) => a + b, 0) / latencies.length
        : 0;

      logger.info({
        projectName,
        totalRuns,
        errorRate,
        averageLatency: `${(averageLatency / 1000).toFixed(2)}s`,
      }, 'LangSmith metrics polling complete');

      return {
        runs,
        feedback,
        totalRuns,
        errorRate,
        averageLatency,
        polledAt: new Date(),
      };
    } catch (error) {
      logger.error({ err: error, projectName }, 'LangSmith metrics polling failed');
      throw error;
    }
  }

  /**
   * Make authenticated request to LangSmith API
   */
  private async makeRequest(endpoint: string, options: RequestInit = {}): Promise<any> {
    const url = `${this.baseUrl}${endpoint}`;

    const headers = {
      'Content-Type': 'application/json',
      'x-api-key': this.apiKey,
      ...options.headers,
    };

    try {
      const response = await fetch(url, {
        ...options,
        headers,
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`LangSmith API error: ${response.status} ${response.statusText} - ${errorText}`);
      }

      return await response.json();
    } catch (error) {
      logger.error({ err: error, url }, 'LangSmith API request failed');
      throw error;
    }
  }
}

/**
 * Create LangSmith client from environment variable
 */
export function createLangSmithClient(): LangSmithClient | null {
  const apiKey = process.env.LANGSMITH_API_KEY;

  if (!apiKey) {
    logger.warn('LANGSMITH_API_KEY not configured - polling disabled');
    return null;
  }

  return new LangSmithClient({ apiKey });
}

server/services/telemetry-poller.ts.content:
/**
 * Telemetry Polling Service
 * 
 * Complements webhook-based telemetry with active polling
 * Useful for:
 * - Backfilling data
 * - Verifying webhook delivery
 * - Fallback when webhooks fail
 * - On-demand metrics retrieval
 * 
 * PRODUCTION-READY: Database-backed persistence with deduplication
 */

import { logger } from '../logger';
import { storage } from '../storage';
import { LangSmithClient, createLangSmithClient } from './langsmith-client';
import type { TelemetryPollingConfig, InsertTelemetryPollingConfig } from '@shared/schema';

export interface PollingResult {
  aiSystemId: string;
  eventsIngested: number;
  errorsDetected: number;
  latencyMs: number;
  polledAt: Date;
  success: boolean;
  error?: string;
}

export class TelemetryPoller {
  private langSmithClient: LangSmithClient | null;

  constructor() {
    this.langSmithClient = createLangSmithClient();
  }

  /**
   * Register an AI system for polling (database-backed)
   */
  async registerAISystem(config: InsertTelemetryPollingConfig): Promise<TelemetryPollingConfig> {
    const existing = await storage.getPollingConfig(config.aiSystemId);

    if (existing) {
      // Update existing config
      await storage.updatePollingConfig(config.aiSystemId, config);
      logger.info({ config }, 'Updated AI system polling configuration');
      return storage.getPollingConfig(config.aiSystemId) as Promise<TelemetryPollingConfig>;
    } else {
      // Create new config
      const created = await storage.createPollingConfig(config);
      logger.info({ config }, 'Registered AI system for telemetry polling');
      return created;
    }
  }

  /**
   * Unregister an AI system from polling (database-backed)
   */
  async unregisterAISystem(aiSystemId: string): Promise<void> {
    await storage.deletePollingConfig(aiSystemId);
    logger.info({ aiSystemId }, 'Unregistered AI system from polling');
  }

  /**
   * Poll telemetry for a specific AI system
   */
  async pollSystem(aiSystemId: string): Promise<PollingResult> {
    const config = await storage.getPollingConfig(aiSystemId);

    if (!config) {
      throw new Error(`No polling config found for AI system ${aiSystemId}`);
    }

    if (!config.enabled) {
      logger.debug({ aiSystemId }, 'Polling disabled for AI system');
      return {
        aiSystemId,
        eventsIngested: 0,
        errorsDetected: 0,
        latencyMs: 0,
        polledAt: new Date(),
        success: true,
      };
    }

    if (!this.langSmithClient) {
      logger.warn({ aiSystemId }, 'LangSmith client not configured - skipping poll');

      await storage.updatePollingStatus(aiSystemId, {
        lastPolledAt: new Date(),
        lastPollStatus: 'failed',
        lastPollEventsIngested: 0,
        lastPollError: 'LangSmith client not configured',
      });

      return {
        aiSystemId,
        eventsIngested: 0,
        errorsDetected: 0,
        latencyMs: 0,
        polledAt: new Date(),
        success: false,
        error: 'LangSmith client not configured',
      };
    }

    const startTime = Date.now();

    try {
      logger.info({ aiSystemId, projectName: config.projectName }, 'Polling LangSmith telemetry');

      // Fetch metrics from LangSmith
      const metrics = await this.langSmithClient.pollMetrics(
        config.projectName,
        config.lookbackMinutes
      );

      // Convert LangSmith runs to our telemetry events
      const events = metrics.runs.map(run => {
        const payload = {
          run_id: run.id,
          trace_id: run.trace_id,
          name: run.name,
          run_type: run.run_type,
          inputs: run.inputs,
          outputs: run.outputs,
          error: run.error,
          start_time: run.start_time,
          end_time: run.end_time,
          tags: run.tags,
          metadata: run.metadata,
          feedback_stats: run.feedback_stats,
          polled: true,
          polling_timestamp: new Date().toISOString(),
          project_name: config.projectName,
        };

        return {
          aiSystemId,
          eventType: run.run_type === 'llm' ? 'run' : 'other',
          source: 'langsmith',
          runId: run.id, // Deduplicated by unique index on (aiSystemId, source, runId)
          ruleId: null,
          severity: run.error ? 'high' : null,
          metric: run.error ? 'error_count' : 'run_count',
          metricValue: '1',
          threshold: null,
          encryptedPayload: JSON.stringify(payload), // Will be encrypted by storage layer
          phiRedacted: false,
          phiEntitiesDetected: 0,
          processedAt: new Date(),
        };
      });

      // Store events in database with deduplication
      let eventsIngested = 0;
      for (const event of events) {
        try {
          await storage.createAITelemetryEvent(event);
          eventsIngested++;
        } catch (error) {
          // Log but continue (likely duplicate due to unique index on runId)
          logger.debug({ err: error, runId: event.runId }, 'Failed to store telemetry event (likely duplicate)');
        }
      }

      const latencyMs = Date.now() - startTime;

      logger.info({
        aiSystemId,
        eventsIngested,
        totalRuns: metrics.totalRuns,
        errorRate: `${metrics.errorRate.toFixed(2)}%`,
        latencyMs,
      }, 'Telemetry polling complete');

      // Update polling status in database
      await storage.updatePollingStatus(aiSystemId, {
        lastPolledAt: new Date(),
        lastPollStatus: 'success',
        lastPollEventsIngested: eventsIngested,
        lastPollError: null,
      });

      return {
        aiSystemId,
        eventsIngested,
        errorsDetected: Math.round((metrics.errorRate / 100) * metrics.totalRuns),
        latencyMs,
        polledAt: new Date(),
        success: true,
      };
    } catch (error) {
      const latencyMs = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';

      logger.error({ err: error, aiSystemId }, 'Telemetry polling failed');

      // Update polling status in database
      await storage.updatePollingStatus(aiSystemId, {
        lastPolledAt: new Date(),
        lastPollStatus: 'failed',
        lastPollEventsIngested: 0,
        lastPollError: errorMessage,
      });

      return {
        aiSystemId,
        eventsIngested: 0,
        errorsDetected: 0,
        latencyMs,
        polledAt: new Date(),
        success: false,
        error: errorMessage,
      };
    }
  }

  /**
   * Poll all enabled AI systems (database-backed)
   */
  async pollAll(): Promise<PollingResult[]> {
    const results: PollingResult[] = [];

    // Fetch all enabled polling configs from database
    const configs = await storage.getAllPollingConfigs(true);

    logger.info({ count: configs.length }, 'Polling all enabled AI systems');

    for (const config of configs) {
      try {
        const result = await this.pollSystem(config.aiSystemId);
        results.push(result);
      } catch (error) {
        logger.error({ err: error, aiSystemId: config.aiSystemId }, 'Failed to poll AI system');
        results.push({
          aiSystemId: config.aiSystemId,
          eventsIngested: 0,
          errorsDetected: 0,
          latencyMs: 0,
          polledAt: new Date(),
          success: false,
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    }

    return results;
  }

  /**
   * Get polling status for an AI system (database-backed)
   */
  async getPollingConfig(aiSystemId: string): Promise<TelemetryPollingConfig | undefined> {
    return storage.getPollingConfig(aiSystemId);
  }

  /**
   * Get all polling configurations (database-backed)
   */
  async getAllConfigs(): Promise<TelemetryPollingConfig[]> {
    return storage.getAllPollingConfigs(false);
  }
}

// Singleton instance
export const telemetryPoller = new TelemetryPoller();

